INSERT INTO `p8_admin_log` VALUES ('1779','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=964&model=article\',\n  \'cid\' => \'964\',\n  \'title\' => \'宫俊芳\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/6122e61c118df2b2.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，眼科副主任医师，硕士研究生。江苏省医师协会眼科医师分会防盲、青光眼学组委员，盐城市医学会眼科学分会委员，眼科美容主诊医师。中国第14期援南美洲圭亚那医疗队队员、支委。曾赴上海复旦大学附属眼耳鼻喉医院进修。多次获优秀带教老师、医院先进个人称号。获盐城市科学技术进步奖。国内核心期刊发表文章数篇。致力于眼科临床工作，擅长白内障、青光眼、视网膜玻璃体疾病（视网膜脱离、玻璃体积血、糖尿病性...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>女，眼科副主任医师，硕士研究生。江苏省医师协会眼科医师分会防盲、青光眼学组委员，盐城市医学会眼科学分会委员，眼科美容主诊医师。中国第14期援南美洲圭亚那医疗队队员、支委。曾赴上海复旦大学附属眼耳鼻喉医院进修。多次获优秀带教老师、医院先进个人称号。获盐城市科学技术进步奖。国内核心期刊发表文章数篇。致力于眼科临床工作，擅长白内障、青光眼、视网膜玻璃体疾病（视网膜脱离、玻璃体积血、糖尿病性视网膜病变）及斜视、弱视等常见及疑难病症的诊治，擅长眼科美容整形，精于白内障超声乳化术、玻璃体切割术。</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'白内障、青光眼、视网膜玻璃体疾病（视网膜脱离、玻璃体积血、糖尿病性视网膜病变）及斜视、弱视等常见及疑难病症的诊治。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\