INSERT INTO `p8_admin_log` VALUES ('1780','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=964&model=article\',\n  \'cid\' => \'964\',\n  \'title\' => \' 胡海涛\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'副主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/47fb92a3b23897c5.JPG\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，眼科副主任医师，医学博士，盐城市眼科学会委员兼秘书，从事眼科临床工作近二十年，积累了丰富的临床经验，主要研究方向为白内障和眼底病。目前已经熟练掌握了眼科常见病和多发病的诊断与治疗，并且对玻璃体视网膜疾病的诊治积累了丰富的经验，尤其擅长白内障、青光眼、糖尿病视网膜病变、年龄相关性黄斑变性、视网膜脱离等疾病的治疗，对一些眼科少见病如视网膜色素变性等也有一定的研究。主持并参与盐城市级...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'男，眼科副主任医师，医学博士，盐城市眼科学会委员兼秘书，从事眼科临床工作近二十年，积累了丰富的临床经验，主要研究方向为白内障和眼底病。目前已经熟练掌握了眼科常见病和多发病的诊断与治疗，并且对玻璃体视网膜疾病的诊治积累了丰富的经验，尤其擅长白内障、青光眼、糖尿病视网膜病变、年龄相关性黄斑变性、视网膜脱离等疾病的治疗，对一些眼科少见病如视网膜色素变性等也有一定的研究。主持并参与盐城市级科研项目多项，获得盐城市科技进步一等奖两项，并在国内外核心期刊发表论文10余篇，SCI论文5篇。\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'糖尿病视网膜病变、年龄相关性黄斑变性、视网膜脱离等疾病的治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\