INSERT INTO `p8_admin_log` VALUES ('1793','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?cid=944&model=article\',\n  \'cid\' => \'944\',\n  \'title\' => \'闻国华\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://192.168.162.72/attachment/cms/item/2021_07/14_16/2a742e3d2954ef46.JPG.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'男，市一院副院长、党委委员，五官科医院院长，耳鼻喉科主任，主任医师，本科学历，硕士研究生导师。任江苏省医学会耳鼻喉科分会常务委员、江苏省残联听力与康复学会副主任委员、盐城市医学会耳鼻喉科分会主任委员、南通大学副教授、学科带头人。从事耳鼻咽喉科临床、教学、科研工作20余年，率先在盐城市开展低温等离子手术，二氧化碳激光手术等耳鼻咽喉部微创手术，积累了丰富的耳鼻咽喉疾病手术经验。在国内核心...\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p>男，市一院副院长、党委委员，五官科医院院长，耳鼻喉科主任，主任医师，本科学历，硕士研究生导师。任江苏省医学会耳鼻喉科分会常务委员、江苏省残联听力与康复学会副主任委员、盐城市医学会耳鼻喉科分会主任委员、南通大学副教授、学科带头人。从事耳鼻咽喉科临床、教学、科研工作20余年，率先在盐城市开展低温等离子手术，二氧化碳激光手术等耳鼻咽喉部微创手术，积累了丰富的耳鼻咽喉疾病手术经验。在国内核心期刊上发表论文10余篇，多次获得盐城市科技进步奖、新技术引进奖，被评为 &ldquo;盐城市名医生&rdquo;。</p>\r\n\r\n<p>&nbsp;</p>\r\n\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'\',\n  \'views\' => \'\',\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'耳鼻咽喉头颈外科疑难杂症的诊治、化脓性中耳炎的听力重建手术、头颈部肿瘤切除及功能性鼻内窥镜手术。\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-add\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5f73cc0c2a61eab9\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626251686');
