INSERT INTO `p8_admin_log` VALUES ('1927','2','admin5','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=984\',\n  \'name\' => \'烧伤整形科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院于1982年组建烧伤病区，1991年成立烧伤整形科，1995年成立烧伤整形研究室，2003年被评为市级重点专科，2010年被评为市级整形（美容）重点专科，2014年被评为国家级药物临床试验基地，住院医师规范化培训基地，南通大学医学院、徐州医学院本科班临床课教学培养基地。年门急诊就诊人次为2000余人次，住院人次超过800余人，长年开展各类严重烧伤、创伤的抢救治疗，各类疤痕治疗，烧伤后严重畸形的整复治疗，烧伤康复治疗，难治创面、体表肿瘤及组织缺损治疗，部分组织器官再造，毛发...</p>\',\n    \'linkurl\' => \'/html/984/2021-07-14/content-2274.html\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobile\'