INSERT INTO `p8_admin_log` VALUES ('1937','2','admin5','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=991\',\n  \'name\' => \'疼痛科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院疼痛科成立于2016年。科室引进多名博士与硕士研究生，设立规范化的疼痛科门诊与病房，先后引进激光治疗系统、臭氧治疗仪、射频治疗仪、冲击波治疗仪、椎间孔镜等先进诊疗设备。</p>\r\n<p>科室开展各项疼痛微创治疗技术。在治疗上，科室按照安全、有效、微创的原则，现综合应用各种微创治疗方法：如神经阻滞、小针刀、银质针技术、射频、臭氧、等离子以及椎间孔镜等微创治疗技术，并配合药物、心理、物理治疗等方法可以有效的治疗各种急慢性疼痛。在疾病诊疗范围上，主要治疗颈肩腰腿痛...</p>\r\n\',\n    \'linkurl\' => \'/html/991/2021-07-14/content-2281.html\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobile\' => \'mobile/list\',\n  \'frame\' => \'/attachment/cms/category/2021_07/15_10/86ece38ba1d11e6a.jpg.thumb.jpg\',\n  \'need_password\' => \'0\',\n  \'category_password\' => \'\',\n  \'domain\' => \'\',\n  \'htmlize\' => \'0\',\n  \'path\' => \'tengtongke\',\n  \'html_list_url_rule\' => \'{$core_url}/html/{$id}/#list-{$page}.html#\',\n  \'html_view_url_rule\' => \'{$core_url}/html/{$cid}/{$Y}-{$m}-{$d}/content-{$id}#-{$page}#.html\',\n  \'html_list_url_rule_mobile\' => \'{$core_m_url}/{$id}/#list-{$page}.html#\',\n  \'html_view_url_rule_mobile\' => \'{$core_m_url}/{$cid}/{$Y}-{$m}-{$d}/content-{$id}#-{$page}#.html\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'label_postfix\' => \'category_991\',\n  \'id\' => \'991\',\n)','1626316786');
INSERT INTO `p8_admin_log` VALUES ('1938','2','admin5','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=993\',\n  \'name\' => \'儿科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院儿科成立于1958年8月，是盐城市临床重点专科、盐城市医学重点学科。科室承担全市儿童的疾病预防、健康保健任务，每年收治各种疑难、危重症患儿数千例，门急诊量万余人次。</p>\r\n<p>目前科室设有儿科病区3个、新生儿病区2个，开放床位共214张。现有主任医师5名，副主任医师5名，主治医师8名，住院医师17名。科室拥有美国鸟牌、熊牌、英国SLE呼吸机、德国STEPHN CPAP、婴儿氧仓、全自动电子温控婴儿洗浴中心等大型先进设备，另有婴儿保温箱，蓝光治疗箱，蓝光治疗毯、监护仪、输液泵...</p>\',\n    \'linkurl\' => \'/html/993/2021-07-13/content-2283.html\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobil