INSERT INTO `p8_admin_log` VALUES ('1939','2','admin5','修改分类','/admin.php/cms/category-update','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/category-update?model=&id=994\',\n  \'name\' => \'急诊科\',\n  \'type\' => \'2\',\n  \'parent\' => \'852\',\n  \'config\' => \n  array (\n    \'direct_to_category_id\' => \'0\',\n    \'target\' => \'_blank\',\n    \'post_year\' => \'0\',\n    \'post_quarter\' => \'0\',\n    \'post_month\' => \'0\',\n    \'post_week\' => \'0\',\n    \'enable_show\' => \'0\',\n    \'orderby\' => \'timestamp\',\n    \'orderby_desc\' => \'0\',\n    \'list_title_length\' => \'120\',\n    \'list_title_length_mobile\' => \'40\',\n    \'list_title_length_dot\' => \'0\',\n    \'list_title_length_mobile_dot\' => \'0\',\n    \'list_pages_template\' => \'\',\n    \'list_pages_template_mobile\' => \'page_template_mobile\',\n    \'view_pages_template\' => \'base_page_template\',\n    \'summary\' => \'<p>盐城市第一人民医院急诊科是严格按照《江苏省急诊医学科建设管理规范》建立的急诊抢救－EICU－急诊病房一体化管理模式的临床科室，符合江苏省综合性三甲医院急诊建设标准。主要承担着全市急危重症患者的医疗救治任务，以及相关的教学、科研和住院医师规范化培训工作。</p>\r\n<p>科室设有分诊处、诊室、急诊抢救室、输液中心、EICU、急诊观察病房、急诊病房等。科室拥有床边监护仪、呼吸机、除颤仪、心肺复苏器、CRRT机、血糖检测仪，以及各种静脉微量泵等多种先进医疗设备及...</p>\',\n    \'linkurl\' => \'/html/994/2021-07-14/content-2284.html\',\n    \'need_login\' => \'0\',\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n    \'fee\' => \n    array (\n      \'credit\' => \'\',\n    ),\n  ),\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'list_all_model\' => \'0\',\n  \'page_size\' => \'30\',\n  \'display_order\' => \'0\',\n  \'list_template\' => \'article/big_list_keshi2\',\n  \'list_template_mobile\' => \'article/list_mobile\',\n  \'view_template\' => \'article/view\',\n  \'view_template_mobile\' => \'article/view_mobile\',\n  \'item_template\' => \'common/ico_title/list016\',\n  \'item_template_mobile\' => \'mobile/list