INSERT INTO `p8_admin_log` VALUES ('2100','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'满江红 用陈毅元帅《梅岭三章》意\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">作者：中华诗词学会会员、盐城市诗词学会副秘书长、盐城市一院纪委原书记于洪蔚</span></p><p><br/></p><p><span style=\"font-family: 宋体, SimSun; font-size: 24px;\">夜幕沉沉，雾幛起，枪声零落。陷重围，几番挣脱，几番追索。梅岭周旋千百转，江南杀敌东西泊。命悬日，留语寄同袍，衷情托！</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 24px;\">求真理，崇先觉，扶华夏，哀民瘼。历九死何悔，志比文岳。别后诸君当奋起，死生不负曾经约。惊雷动，大捷报泉台，金波酌！</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d5797351c695b083\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333872');
INSERT INTO `p8_admin_log` VALUES ('2101','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'沁园春\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">作者：中华诗词学会会员、盐城市诗词学会副秘书长、盐城市一院纪委原书记于洪蔚</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><br/></span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 24px;\">一路高歌，百年风雨，百载辉煌。忆南湖波涌，红船帆启。黄洋炮响，翠竹帷张。逐鹿中原，投鞭江汉，血染山河图救亡。新中国，似初升旭日，辉耀东方！</span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 24px;\">守成续写华章。赶考去，须毋学闯王。有雄文数卷，安能失路；初心一颗，岂畏强梁？漫道雄关，扬眉进击，至伟功成梦国强。华诞至，看神州处处，赤帜飘扬！</span></p><p><br/></p><p><br/></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'b927217d6f172791\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333872');
INSERT INTO `p8_admin_log` VALUES ('2102','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'武陵春 都城南村\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: center;\"><span style=\"font-size: 20px;\">作者：于洪蔚<br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 20px;\"><br/></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-size: 20px;\">前度东风今赴约，再过野人家。</span><span style=\"font-size: 20px;\">绿绕孤村水拍沙，蛱蝶闹芳华。</span></p><p style=\"line-height: 3em; text-align: center;\"><span style=\"font-size: 20px;\">惆怅春光眉不展，不遇去年娃。</span><span style=\"font-size: 20px;\">爱恨由来灼灼花。</span><span style=\"font-size: 20px;\">拾瓦砾，掷啼鸦！</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8135ef40aff674e0\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333873');
INSERT INTO `p8_admin_log` VALUES ('2103','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'送温暖，共叙桑榆情，颂党恩，助推双胜利\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\"></span></p><p><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">秋高气爽，桂子飘香。为深入贯彻习近平新时代中国特色社会主义思想，及时学习党的十九届五中全会精神，以“送温暖，共叙桑榆情，颂党恩，助推双胜利”为主题，进一步丰富我院离退休党员干部职工的精神文化生活。在院党委的关怀下，11月6日，我院老干部支部组织全院140多名离退休党员同志赴常熟沙家浜红色文化教育和历史文化教育基地，开展党日活动现场教学。<br/> 在沙家浜革命历史纪念馆，老同志参观新四军抗日游击队江南斗争的事迹陈展，回顾了党领导广大军民不畏强敌、英勇抗敌的光辉过去。切实体验了党作为抗日战争和民族解放的中坚，付出的巨大努力与牺牲。一代一代共产党人，一个个烈士的事迹，让大家深受教育，也格外珍惜来之不易的幸福生活，并对我党全面达小康、再战“十四五”的伟大新征程由衷充满信心。<br/> 不忘初心跟党走，歌颂党恩向前进。此次的党日活动，院老干部支部在医院党委的指导下，精心准备、认真组织，行程安排紧凑合理。整个活动受到了广大老党员同志们的交口称赞，大家纷纷表示要发挥余热。桑榆虽晚，依旧是栋梁，要继续为医院的基层党建添砖加瓦，为推动医院高质量发展、建成地区性医疗中心贡献力量。<br/> <img src=\"http://www.ycsyy.com/upload/images/2020/11/96304e7b3a27531a.jpg\" title=\"jinfa1.jpg\" alt=\"jinfa1.jpg\"/></span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'c026e8285a2c813c\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1626333873');
INSERT INTO `p8_admin_log` VALUES ('2104','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'《我看新中国成立70周年》主题征文之七律\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">【七律】贺盐城申遗成功</span><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">于洪蔚</span><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">大洋西岸古城东，</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">万顷滩涂生意融。</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">连海蒹葭春雨碧，</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">染霜蒿草艳阳红。</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">鹤翔天宇英姿展，</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">鹿斗荒原霸气雄。</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">忽报申遗成大事，</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">保全生态立奇功。</span><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">【七律】</span><span style=\"font-family: 宋体;\">新中国七十大庆感怀</span></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">于洪蔚</span><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">喜庆新华七十年</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">初心回望志弥坚</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">迎风红矩歌盈路</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">逆水龙舟弓满弦</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">赫赫雄狮大梦醒</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">巍巍华夏小康圆</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">长天当展鲲鹏翼</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">再续辉煌吾辈前</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">【七律】</span><span style=\"font-family: 宋体;\">无题</span></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">于洪蔚</span><span style=\"font-size: 20px;\"></span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">新洋港水波光碧</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">两岸新容映眼前</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">高铁通途平地起</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">群楼高耸半空悬</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">聚龙湖畔人如织</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">盐渎公园柳似烟</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">风起老槐频颔首</span></p><p style=\"text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">应叹沧海几桑田</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f001a9c232f242fd\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333874');
INSERT INTO `p8_admin_log` VALUES ('2105','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'《忆往昔、看今朝、谈发展》征文之幸福生活的梦想\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p><br/></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">我出身在40年代，六岁上学读书，说是读书实为关关水（防水），那时读书经常受战争影响，每月都有几次跑反，所谓跑反就是国民党兵从盐城龙冈下来抢劫抓夫，我们都跑得远远的，远离这瘟神。<br/></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">我七岁参加儿童团，还赶上了土地改革，我们儿童团由民兵带队拿着长茅或棍子协助民兵站岗放哨，斗地主时开大会我们儿童团跟着民兵举手喊口号，为斗地主分田地助威。贫下中农分了田日子越过越好，个个都感谢共产党毛主席领导得好，使我们分了田当家作主人，过去那种吃不饱穿不暖的日子一去再也不复返。我特别记得我乡指导员王候青同志在土改总结会上说：现在斗地主分田地农民翻身做主人只是我们走的第一步，我们今后还要领导大家建设社会主义，我们的生活越过越好，到时候我们的生活中怎样呢？那就是吃的油，穿的绸，在家住高楼，楼上楼下电灯电话、出门汽车一跨。在那时乡亲们都当着笑话谈，有的说痴人说梦话，瞎吹牛，那种生活不可能实现的。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">解放了，在共产党的领导下，经过几代人几十年的努力奋斗，特别是改革开放以来，政策变了。拨乱反正，激发了全国人民的干劲。人们吃的、住的、穿的、用的，出行都发生翻天覆地的变化，新的农村，新的城市改变了人们幸福生活的梦想。不到共产主义实现，我们的幸福生活的梦想就实现了。让我们在以习近平总书记为着党中央领导之下，使我们的梦想经过全国人民的共同努力过得越来越好。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">作者：周增宽</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'bf4a70d3786a2a27\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333874');
INSERT INTO `p8_admin_log` VALUES ('2106','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'《忆往昔、看今朝、谈发展》征文之先锋岛漫步\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em; text-align: left; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">岛者，四面皆水也。我们盐城市区就有这样一个岛，叫先锋岛。</span><br/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2016年初夏的一个傍晚，我——一个年逾古稀的退休老人沿着先锋岛小海路上的人行道向南慢慢走去。过去（指六十多年前，下同），我们一家八口人住在先锋岛北岸的一间不足20平方米的平房里。现在，子女们都已成家立业，搬出居住。我和我的老伴两个人仍旧住在先锋岛河北岸，但却是三室两厅两卫130平方米的楼房。我是在先锋岛河边上长大的，在这里度过了我的少年、青年和中年时代，现在已进入老年。我一边走着，一边想着，一边看着。我想起了先锋岛的过去，看着先锋岛的现在，也猜想着先锋岛的未来。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">过去，先锋岛及其周边地方全是平房，其中还有不少草房。而如今，由盐城市国投集团投资建设的先锋国际广场一期工程已在几年前正式建成并投入使用。从东到西，十几层的楼房一字排开，十分壮观。站在广场高台上，遥望南天，心旷神怡。此外，在先锋岛西侧靠近泰山庙处，一直到北河边，还有四幢很有古典风格的二层小楼。目前，先锋岛国际广场二期工程正在紧张的施工之中。一幢层高为全市之最的48层的酒店写字楼已打好基础，三幢28层高的住宅楼已建到11层；二期工程完成后还有三期工程，那就是拟在泰山庙西侧兴建的供奉文殊菩萨的“台山寺”，我们拭目以待。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">过去，我们上小学时，沿着河边狭窄的泥路，走过木质的东越河桥，踏上先锋岛最东侧的低洼的“小海滩”，向木质的登瀛桥走去。如遇下雨天，那就苦不堪言，河边的泥路泥泞难行，即是手扶竹杆，也难免滑倒，变成“泥猴”；如遇大雨，经过“小海滩”时只能缓慢的涉水而过。而现在，先锋岛东侧是建在原东越河桥址上面并在空中穿过“小海滩”的钢筋混凝土的高架大桥与前后水泥路相连，南北贯通，机动车四车道，两侧是非机动车道、再两侧是高出中间路面的宽阔的人行道；原“小海滩”东侧现已建成河边公园，而西侧是大型地下停车场；先锋岛南面是东西走向的崭新的建军西路，而新建的西越河桥不仅在两侧留下了宽阔的人行道，而且在两侧人行道上各建有五座古色古香的凉亭，行人可在此休息、遮阳、避雨。扬州有座五亭桥，而这就是我们盐城的十亭桥。在先锋岛河北岸过去是狭窄的泥路，而如今是东西贯通的由毓龙东路延伸而来的毓龙西路，柏油路面，宽阔平坦，一直延伸到西环路。过去，在先锋岛北侧河面的中段没有桥，而如今，在先锋岛中段与河北岸盐城市第一人民医院住院处大门之间的河面上新建了一座水泥大桥，为广大群众的两岸通行带来了极大的方便；过去，先锋岛及周边地方的群众要进入市区，只有向东的登瀛桥一桥可通，而在先锋岛南侧河面上无桥可通。而现在在南侧河面上先后架起了两座宽阔的水泥大桥，一座直通盐马路，另一座直通水上新村，既分流了车辆，也极大地方便了群众的出行。至今，先锋岛四周的河面上与外界相连的共有六座大桥，除登瀛桥和东、西越河桥外，另外三座大桥不知为何至今没有桥名？</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">过去，在先锋岛及其周边地方，不要说汽车，就是连自行车也很难看到，居民群众出行全靠两条腿，当时大家戏称为“11路”汽车。而现在，以先锋岛为始发站的公交车有21路、32路、85路、208路计四路；而经过先锋岛的公交车有2路、3路、4路、5路、8路、12路、20路、22路、30路、33路以及K8路、K401路、K501路、K601路、B支1路等计十五路公交车，极大地方便了广大群众的出行。同时，盐城市人民政府在公共交通上实施了惠民政策，60周岁以上70周岁以下的老人可以办理“老人卡”半价乘车，而70周岁以上的老人则可办理“寿星卡”，免费乘车；此外，市人民政府为了方便广大群众出行，在大力发展公交的同时，在全市区免费提供公共自行车供市民群众使用，只要办张卡，要骑车随时可以取用，一小时内分文不收。而且站点众多，仅先锋岛及周边就有四个公共自行车站点，既方便，又环保。这么多的公交车，这么多的公共自行车，这么好的优惠政策，在过去，是想也不敢想的。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">过去，先锋岛上商店极少。好象有一家小酱园店，南河边上有一个蔬菜批发零售小门市。大约在1958年以后，有了一家只有2小间门面的百货店和一家小布店。而如今，岛上有大型商业零售企业——“大润发”超市，吃、穿、用等日用消费品琳琅满目、应有尽有，还有“新世界”大型百货商场，此外，还有不少小型商店；在饮食业方面，过去先锋岛上仅有一家只有两、三张桌子的小饭店和一家熟食店。而如今，不但有“江苏国际宴会中心”这样大型的酒店，也有“乡村小灶”式的中、小型饭店和自助餐厅，还有“有名堂”美食和“尚客优品”中式快餐；“大润发”超市内设有“大娘水饺”店，广场高台上的“洋快餐”——“肯德基”和玉石文化街上的饮食店，生意也不错。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">过去，先锋岛及其周边地方没有文化娱乐场所。而现在，有现代化的电影城、动漫城，在先锋岛南侧河岸上还新建了名为“万龙欢乐园”的大型游乐场，既有少年儿童玩耍的乐园，也有成年人才能参与的常常令人惊心动魄的游乐项目。值得一提的是，盐城市国投集团的有关部门还在高台的东北角为先锋社区的群众免费搭建了一个群众文化舞台。每到夏秋季节双休日的晚上，先锋社区及周边喜爱淮剧的群众就会在这个群众舞台上演唱淮剧的唱段，而台下也常常挤满了观众。作为盐城市老年大学淮剧唱腔班优秀学员的我，也登上这个群众舞台演唱过淮剧唱段。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">我从西侧走下高台，慢慢向泰山庙走去。新四军重建军部旧址——泰山庙是全国重点文物保护单位，也是全国爱国主义教育示范基地、全国中小学爱国主义教育基地。在泰山庙门前，我驻足良久。缅怀老一辈无产阶级革命家们的丰功伟绩，我深深地鞠了一躬。离开泰山庙，我沿着向北的人行道继续向前走，没多远就到了东西越河桥之间，新建的直通北岸的大桥桥头，站在桥头上抬头向北岸望去，高大的病房楼赫然在目，这里就是与全市人民息息相关的人民健康的守护神——“盐城市第一人民医院”。医院成立于1948年1月，当时叫“盐阜医院”，有工作人员23人。因医院全部药品和器械装于2只木箱中，一担就能挑走，所以被当地群众称为“一担挑”医院。解放后院名改为“盐城专区中心卫生院”、“盐城专区医院”，1958年改为“盐城医专附属医院”（至今仍有很多群众称市一院为“附医”即由此而来），医专停办后改名为“盐城地区人民卫生院”，后又改为“盐城地区人民医院”，1983年5月地改市后即用现名。过去，医院在剧场路原址时，仅有140张床位，病床设在平房中，还有部分草房，年收治住院病人2200人次左右，门诊为一幢2层的小楼，年门急诊9万多人次，职工总数152人，89名技术人员中，仅有2名副主任医师。医疗设备器械比较简单，只有几个科室。1963年以后，医院从剧场路原址分期分批迁至现址。60多年来，特别是近20年以来，市一院的医疗事业有了突飞猛进的发展，如日中天。现在，8层门诊大楼在前，12层综合病房大楼在后，10层内科病房大楼在东侧，3层科技楼在西侧；中间是一个很大的停车场。在市一院北院区还有两幢6层病房楼和一幢“高压氧治疗中心”。目前，医院占地面积13万平方米，建筑面积14万平方米，现有职工2294人，其中硕士生导师35人，博士30人，硕士研究生328人，具有高级职称372人，中级职称363人。现有34个临床科室，23个医技科室，60个护理单元，实际开放床位2100张。医院设有全市的急救中心、临床检验中心、医学特检中心、糖尿病防治中心、心脏介入中心和微创中心以及体检中心，并建立了烧伤整形、血液净化、影像诊断、介入放射等研究室和内科、外科、妇产科、儿科、五官科、影像科、护理等临床教研室。市一院积极开展对外合作，先后与南京、上海、北京等地的国内知名医院建立了8个临床诊疗中心，聘请15位著名医学专家担任首席顾问，极大地促进了医院学术水平和医疗技术的提高。市一院大力引进国内外先进医疗设备，现拥有万元以上的医疗设备达2308台件，设备固定资产4.48亿元，进口大型设备有：3.0T磁共振2台、1.5T磁共振1台、高档256排螺旋CT等4台、带高级CT功能大型DSA2台、高能直线加速器2台、PET—CT1台，医疗设备达到国内先进水平。医院实行信息化管理，建立了医生工作站，满足了较高层次的工作需要。市一院注重发挥人才优势，不断加快技术创新步伐，专科建设、临床教学、医学科研都取得了很大进步，2015年获市科技进步奖5项、省新技术引进奖2项、市新技术引进奖11项。在研省级以上课题达18项，发表学术论文273篇。现有省“333”工程人才14人、市级医学人才38人，有7人被评为省、市“有突出贡献中青年专家”。市一院重视学科建设，建成了一批特色鲜明的重点专科：呼吸内科、骨科、普外科、心内科、检验科等5个科室被评为江苏省临床重点专科；肿瘤科、血液科、神经内科、影像科等4个科室通过省级重点专科评审；业务科室全部列为市级临床重点专科；肾脏内科等18个科室被评为市医学创新团队；内分泌科等13个科室通过了GCP认证，具备Ⅱ期药物临床试验资质。市一院一直坚持以病人为中心，以质量为核心，大力加强医院现代化建设，努力为社会提供优质、高效、便捷、廉价的医疗服务。2015年诊治门急诊病人105.19万人次，收治住院病人8.95万人次，平均住院人次费用为12638元，比全省同级医院平均费用少1044元。医疗服务受到广大病员和社会各界一致好评。2015年，在第三方组织的出院病人满意度调查中，综合满意度平均达93.67%。市一院先后被评为全国医药卫生系统先进集体、省级文明单位、省文明医院、全省“诉调对接”工作先进集体、省“全面改善医疗服务，推进医德医风建设”专项行动医疗服务先进单位、省城乡医院对口支援工作先进集体、江苏省群众满意的医疗卫生机构。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院的宗旨是“治病救人、造福社会”；医院的愿景与目标是“建设高水平的区域医疗中心”；医院广大工作人员恪守“厚德修身，精术济世”的医院院训，发扬“严谨、博学、团结、奉献”的医院精神，坚持“生命所系，健康相托”的服务理念。市一院现在已建设成为一所集医疗、教学、科研、康复、急救为一体的三级甲等综合性医院，1999年正式挂牌南通医学院第四附属医院。我作为市一院一名退休职工，我为今天的市一院感到无比自豪。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">突然，我的手机响了，传来了悠扬悦耳的江南丝竹的乐曲声，原来是我的老伴催我回家吃晚饭了。我看着手中的智能手机，想起了过去少年时玩的矿石收音机，我一路走来，想着过去，看着现在，也憧憬着未来……。我走在回家的路上，耳边响起了我的心声：盐城市广大人民在中共盐城市委、市人民政府的正确领导下，坚持邓小平理论、以三个代表重要思想、科学发展观和习近平总书记系列重要讲话为指引，坚持走中国特色的社会主义道路，坚持改革开放，认真贯彻执行党中央的路线、方针、政策，在全市的经济建设和各项社会事业的建设上取得了巨大的成就。而先锋岛及周边地方发生的巨大变化，就是全市发生巨大变化的一个缩影。我们这一代人虽然已至暮年，但我坚定的相信，我们的子孙后代将会在中国共产党的领导下，一代接一代地去创造更加辉煌灿烂无限美好的明天。“英特纳雄耐尔”一定会实现，我作为一个有着近四十年党龄的中共党员，我对此深信不疑。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">我走向楼梯拾级而上，到家门口时按响了门铃……</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本文作者：冯国标</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院退休职工</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">医院办公室原主任、主任中医师</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">手机：15365669869</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7e14142d7a93568e\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333875');
INSERT INTO `p8_admin_log` VALUES ('2107','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'《忆往昔、看今朝、谈发展》征文之戒指\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">我颈上围着铂金项链，腕上套着和田玉镯，左无名指上戴着钻石戒指，我七十大几了，身上还有些珠光宝气，真是老来俏啦！我最喜欢的还是那只戒指，因为其来历不平常。</span><br/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">那天早饭后，老伴对我说：“上街逛逛，看看热闹吧！”我有些诧异：老头子样样都好，就是不肯陪我逛街，今天怎么这么好？原来，前些日子我在他面前曾提到科室里谁谁的钻石戒指很好看，价格也不贵等等。他心里明白，很可能我也希望有一只钻石戒指。爱美之心人皆有之嘛！如今家庭经济条件大改善，我们银婚纪念日快到，他要还我一只戒指。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">在那个困难的年代结婚，非但没有一星半点东西作纪念，反而在后来因为急需，把母亲给我的作结婚纪念品的一只金戒指卖掉了。这件事像一根尖尖的鱼卡子卡在他的喉头整整40个年头吐不出来，又咽不下去，肯定难受。今天，他要把这卡子拔掉。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">他口袋里装了几千块钱，陪着我往建军路大街上走去。大街上熙熙攘攘，人流如潮，大商场门口张灯结彩，半空红色气球飘舞，一片繁荣景象。几个著名的商场的珠宝专柜都侦察过了，货比三家嘛，最后走进一个最华丽的珠宝店，美女营业员热情接待我们 。我有点舍不得，认为不必花钱太多。老伴坚定地说：“难得买一只，分量要足一点，纯度要高一点，式样要新一点。”终于花5000元买了一只两人都看中的铂金钻戒，还多一些钱，索性又买了一条铂金钻石项链，对，这是利息。营业员把这两件宝物分别用红绸布包好，装进一个精美的小盒子，恭恭敬敬送交给我。我眼盯小盒子，喜形于色。我俩仿佛回到那刚刚结婚的甜蜜岁月。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">午后回到那130多平米的“高知楼”， 喜鹊登枝，彩蝶纷飞，清风送爽，花香扑鼻。我心潮起伏，久久不能平静，我细心地把戴在手指上戒指轻柔地脱下，细细品味一番。又轻柔地戴上，那钻戒在暮春的阳光照耀下折射出一道道绚丽的光茫，这光茫与眼里的泪花交织成一个个变幻莫测的五彩光环，我俩都沉浸在无限的甜蜜幸福之中！同时也勾起了那辛酸的往事的回忆……</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">那是1977年秋，小孩上中学离家远，急需一辆自行车。那年头，自行车是“四大件”之首，买自行车谈何容易，不但要有钱，还要有购车票。好不容易从一位当乡长的朋友处弄来一张购自行车票，但是买车的的现金实难筹措。五口之家还要给双方的父母一点赡养费。虽说工作已二十多年，但两人的工资加起来只八十五元五角，哪有余钱来买车？朋友、同事景况都差不多，不可能有余钱借贷。苦思冥想，我把目光盯到了手指上的金戒指上，当即对老伴说：“自行车确实需要，金戒指好看，不好吃，不好用，你拿去卖了买自行车吧！”老伴本来不答应，后来也勉强同意了。隔了几天，我抹下手指上的金戒指交给了老伴，一同去一家银行，一位三十多岁的女营业员。从柜台里探出头来，我说明来意后，她向我细细打量`一番，验明正身，觉得这戒指来路没问题。接着又盯着戒指观赏一番说：“这戒指成色不错嘛，怎舍得脱手的？” “实在没办法啊，因为急用，还是忍痛割爱。”我回答。“按银行收购价，还要打一点折。”我连忙说：“可以”。这只戒指重三钱三，换得人民币100多元，买一辆自行车还不够数，不得已，又把家里一些零碎的银首饰全部补上去，这才凑够了那120元。我俩立即奔赴市五交化公司，选中一辆上海产28型永久牌自行车。看着这魂牵梦萦的自行车，心情真是既高兴又苦涩。那个年代买回一辆自行车的轰动效应远大于现在买一辆“桑塔那” ！左邻右舍象看新娘子一样来参观我家的新宠，夸奖我真有办法，我也露出了苦恼人的笑。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">将近四十个年头过去了，那辆金戒指换来的自行车早已完成了它的历史使命，但是，如今仍然放在车库里没有丢弃。这破旧的自行车，因为它是我手上的金戒指换来的，是知识分子苦难岁月的见证。那钻石戒指则成了改革开放使知识分子迎来了第二个春天的见证。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">叮铃铃的电话声打断了我的沉思，原来是国际旅行社来电，通知我们去西欧旅游起程的日期……</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2016年5月24日</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本文作者：黄和珍</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院退休职工</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">医院B超室主治医师</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">手机：13961992095</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f41b471bfbd11724\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333875');
INSERT INTO `p8_admin_log` VALUES ('2108','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'《忆往昔、看今朝、谈发展》征文之搬家\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">1961年秋我毕业分配来到盐城医学专科学校附属医院（今盐城市第一人民医院），至今已55个年头，这期间共搬家4次，每次都有个动人的故事。</span><br/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1964年春我结婚了，度假回来，同室的两位同事不声不响，搬出去了，从此，这间14平米的宿舍成了我的新房。后来两个小家伙先后在这里诞生，紧接着父母亲也来了，小家庭成了大家庭，三代同堂，挤于一室。一只煤球炉置于走廊便是厨房，一只旧木箱兼作饭桌、书桌和衣柜。家中唯一的高挡设备就是花76元买来的结婚纪念品，一台美多牌收音机。但是，这样安定的日子也没过多久。1970年春，享受“知青” 上山下乡的待遇，一辆大卡车把我和全家人拉到西乡龙岗公社，这是我家的第一次搬迁。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">龙岗的新家被安排在已停办的一所农业中学的破教室里，窗没玻璃，灯没电，睡地铺，烧锅箱，生活在儿时听我奶奶说的“风扫地，月点灯，西瓜灶，滚龙床” 的 困境里。后来经两次调整，才搬进了约30平未的简易房里。那年头，兴“四大件”，即收音机、手表、自行车、缝纫机，收音机有了，其它三样都要票。我的第一块国产手表是老弟花100元在西安买的。缝纫机呢，那年，供销社3个月只有一台缝纫机计划。我与供销社主任挂钩半年了，好容易得到取货通知，岂知另一住主任又通知了一位买主，二人争购一台机，怎么办？聪明的主任想了个折衷的办法---抓阄，结果我胜利了，终于圆了缝纫机梦，这台花145元买来的蝴蝶牌缝纫机，今天已很少使用，由于来之不易，老伴仍把它当“历史文物”珍藏着。自行车呢？没门！</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">真是三十年河东，三十年河西。一个破天荒的机遇终于到来了！1979年的4月，改革开放尹始，和风拂煦，彩云朵朵，在这明媚的春天，我终于搬回老家---盐城市第一人民医院。住进了新建的“红楼”，这是我市当时唯一的一幢“高知楼”。 70多平米的套间，有独立的厨房和卫生间，从此大小“方便”告别了传统的马桶和痰盂罐。好多知识界的朋友领着小孩来参观，小朋友见了这个新奇物，进水、抽水把它当玩具玩耍。这一年，职称晋升为主治，18年来首次涨工资，由51.5元增至57元。但是，由于改革开放刚刚起步，我的家又是个“多伦（人）多”（7口子），生活仍然窘迫，电视杌是不值钱的黑白14英寸二手货，小孩上学需要自行车，钻天打洞弄到一张票，钱紧张，爱妻把手指上的、她老母给她作结婚纪念的金戒指、十分惋惜地抹了下耒，兑成现金，又借了一点才买了回来。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1983年，机遇又耒！盐城撤县建市，其时工资已翻了番，我从补发的工资中拿出126元买了一只新手表，更令人高兴的是，医院又盖了一幢每室面积达100平米的新高知楼，于是我经历了第三次撇家，从红楼搬进新楼。家俱又翻了新花样，职称升至副高级，工资增加到4位数，比较高档的彩电、冰箱、空调都到了家。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">更令我惊喜的是，千喜之年的春天，锦上添花，医院里5幢100套新的高知楼拔地而起，每单元130平米、三室半两厅、双阳台双卫生间的住宅，还附有15平米的车库。我又获得一次搬家的杌会。这不是一般的搬家，而是乔迁之喜了！搬家那天，如过节一般，贺喜的朋友络绎不绝，鞭炮齐鸣，欢声雷动，我这个过去的臭老九，含着热泪，喜气洋洋又乔迁新居。因是房改房，只花3.5万元就到手了。我又花些钱把它娇装打扮成“星级宾馆”一个样！。我的新家就定格在这满园春色里了。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">如今，我己八十高龄，月退休金七千多，身体爽朗，随心所欲，真是喜不自禁，感慨万千！盐阜大地的多姿风情，滋润了我的肌肤，串场河的清泉哺育了我大半生。这五十年多来我搬家好多次，见证了一个知识分子的家庭从窘迫寒酸，到芝麻开花节节高，生活一天天富起来，精神一天天爽起来。我对盐阜人民奉献太少太少，却得到他们如此丰厚的回报，我深感内疚！我感恩盐阜大地淳朴的父老乡亲；更加感恩伟大的中国共产党！</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 2016年5月20日</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本文作者：朱维清</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院退休职工</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">医院放疗科主任医师</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">手机：15851088206</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'bb126438e0e63dff\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333876');
INSERT INTO `p8_admin_log` VALUES ('2109','2','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'894\',\n  \'title\' => \'关于开展“忆往昔、看今朝、谈发展” 主题征文活动的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\" dir=\"rtl\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">：全院离退休职工</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为贯彻落实中办《关于进一步加强和改进离退休干部工作的意见》（中办发[2016]3号）和盐委老干部[2016]4号文件精神，积极引导全院离退休干部为党和人民事业增添正能量，为建设新盐城作出新的贡献，有效推进我院的“夕阳生金辉、厚德耀盐城、增添正能量”主题活动的有序开展，特开展本次主题征文活动。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">征文对象：全院离退休职工</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">征文时间：2016年5月</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">征文内容：从作者亲身经历、亲眼所见、亲耳所闻的角度撰写回忆性文章。征文紧紧围绕中国共产党领导人民历经千辛万苦，取得革命、建设、改革伟大胜利所走过的光辉历程，记述盐城的发展变化；叙述作者亲自参与决策、参与建设的经历及取得的经验；描写推动社会发展离退休干部典型事迹；讴歌党的十八大以来新发展、新变化。每篇1500字左右，必须史料真实，文理通顺、言简意赅。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">征文组织：5月1日至5月30日为稿件征集阶段。6月初进行稿件评选，优秀征文将上报市老干部局，由市老干部局将优秀征文汇编成册。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">征文需在5月30日前将纸质文档和电子文档交到老干部支部办公室。凡参加者均给予一定的奖励。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 老干部支部</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"> 2016年5月9日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'[db:作者]\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'\',\n  \'timestamp\' => \'\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'ec9949f6ac9e934f\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1626333876');
