INSERT INTO `p8_cms_item_article_` VALUES ('2065','article','943','3','admin789','李锋','','0','副主任医师','<!--#p8_attach#-->/cms/item/2021_07/14_09/dccb026e90dae5b6.jpg','','','6','男，麻醉手术科副主任医师，硕士研究生。擅长各种小儿麻醉，熟练掌握各种危重、疑难病人的麻醉处理，熟练掌握纤维支气管镜操作技术。近3年，主持开展新技术1项，解决严重困难气道患者手术麻醉地问题，并取得良好的社会效益和经济效益；2016年申请并承担盐城市市级科研项目1项；荣获江苏省医学新技术引进二等奖1项，盐城市新技术引进奖一等奖、二等奖各1项；发表学术论文6篇。','','','','','','1','admin789','0','1626225960','1626225960','1626225960','1626225960','1','','','0','0','0','0','0','','','','a:1:{s:8:\\\"allow_ip\\\";a:5:{s:7:\\\"enabled\\\";s:1:\\\"0\\\";s:9:\\\"collectip\\\";a:0:{}s:7:\\\"beginip\\\";s:0:\\\"\\\";s:5:\\\"endip\\\";s:0:\\\"\\\";s:9:\\\"ruleoutip\\\";a:0:{}}}','','','','','');
