INSERT INTO `p8_cms_item_article_addon` VALUES ('2236','3058','1','','<!--#p8_attach#-->/cms/item/2021_08/20_15/e225afc8220dd3db.jpg.thumb.jpg','肺癌（肺结节）多学科联合诊疗（MDT）是综合肺癌患者各方面身体状况和客观条件，依循证医学理念多学科合作为患者提供合理的、规范的、个性化治疗方案。','192.168.162.110','192.168.162.110','1618902140','<p><span style=\"\">肺癌（肺结节）多学科联合诊疗（MDT）是综合肺癌患者各方面身体状况和客观条件，依循证医学理念多学科合作为患者提供合理的、规范的、个性化治疗方案。</span></p>','','','','','');
