INSERT INTO `p8_admin_log` VALUES ('3147','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区洗衣房蒸汽管道、新风机组表冷器等项目维修招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align:left;text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">盐城市第一人民医院主院区洗衣房蒸汽管道、新风机组表冷器等项目资金已落实，我院现拟对该项目进行公开招标，诚邀潜在的投标人参加投标，择优选择施工队伍。</span></p><p style=\"margin-left:28px;text-align:left;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.</span><span style=\"font-size:16px;font-family: 宋体\">项目概况与招标内容</span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.1</span><span style=\"font-size:16px;font-family:宋体\">项目名称：盐城市第一人民医院主院区洗衣房蒸汽管道、新风机组表冷器等项目维修。</span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.2</span><span style=\"font-size:16px;font-family:宋体\">招标内容：主院区洗衣房蒸汽管道、烘干机凝结水排水管道、蒸汽凝结水管道维修及加装保温层，影像科新风机组、住院大厅风机盘管、住院大厅新风机组表冷器维修，负二层职工之家排污泵采购安装；北区DSA2室空调电机更换、五官科医院手术室净化空调控制面板更换等<span style=\"background:white\">。具体内容详见招标人提供的工程量清单。</span></span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.3</span><span style=\"font-size:16px;font-family:宋体\">服务地点：<span style=\"background:white\">盐城市第一人民医院主院区（人民南路66号）、北区（毓龙西路166号）、五官科医院（南城河路12号）。</span></span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.4</span><span style=\"font-size:16px;font-family:宋体\">工期要求：<span style=\"text-decoration:underline;\"> 25 </span>日历天，具体开工日期以招标人的通知为准</span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.5</span><span style=\"font-size:16px;font-family:宋体\">质量要求：符合国家“合格”标准，质保期2年，质保期内<span style=\"background:white\">工程量清单</span>内容发生故障，由中标人负责修复，所有费用均由中标人承担。</span></p><p style=\"text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">1.6</span><span style=\"font-size:16px;font-family:宋体\">发包方式：包工包料。</span></p><p style=\"text-align:left;text-indent:32px;line-height:25px\"><strong><span style=\"font-size:16px;font-family:宋体\">2</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、投标人应当具备的主要资格条件：</span></strong></p><p style=\"text-align:left;text-indent:32px;line-height:25px\"><a name=\"_Hlk68512902\"></a><span style=\"font-size:16px;font-family:宋体\">1</span><span style=\"font-size:16px;font-family: 宋体\">、投标人应具有独立法人资格，能够独立承担民事责任的能力；</span></p><p style=\"text-align:left;text-indent:32px;line-height:25px\"><span style=\"font-size:16px;font-family:宋体\">2</span><span style=\"font-size:16px;font-family: 宋体\">、投标人须是建筑行政主管部门核发的建筑机电安装工程专业承包资质三级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"text-align:left;text-indent:32px;line-height:25px\"><strong><span style=\"font-size:16px;font-family:宋体\">3</span></strong><strong><span style=\"font-size:16px;font-family: 宋体\">、投标报名及招标文件的获取</span></strong></p><p style=\";margin-bottom:0;text-indent:32px;line-height:25px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名并领取招标文件，投标报名时间</span><span style=\"font-family:宋体;color:blue;background: white\">2021</span><span style=\"font-family:宋体;color:blue;background:white\">年4月9日8时至2021年4月14日18时</span><span style=\"font-family: 宋体;color:black;background:white\">。联系人：刘永杰，0515-69057406；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:25px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">4</span><span style=\"font-family:宋体;color:black;background:white\">、本项目采用竞争性谈判方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:25px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">5</span><span style=\"font-family:宋体;color:black;background:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:25px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">6</span><span style=\"font-family:宋体;color:black;background:white\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:25px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">其余内容详见招标文件。</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;color:black;background:white\"> </span><span style=\"font-family:宋体;color:black;background:white\">招标人：盐城市第一人民医院</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;color:black;background:white\"> </span><span style=\"font-family:宋体;color:black;background:white\">时间：2021年4月8日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'总务处\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-04-08\',\n  \'timestamp\' => \'2021-04-08\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e13bb82058f4f148\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627522708');
