INSERT INTO `p8_admin_log` VALUES ('3170','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院放射诊疗设备预评价和控制效果评价服务项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">1</span><span style=\"font-family:宋体;color:black;background:white\">、盐城市第一人民医院放射诊疗设备预评价和控制效果评价服务项目，资金已落实，招标人为盐城市第一人民医院，现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2</span><span style=\"font-family:宋体;color:black;background:white\">、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.1</span><span style=\"font-family:宋体;color:black;background:white\">项目名称：盐城市第一人民医院放射诊疗设备预评价和控制效果评价服务项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.2</span><span style=\"font-family:宋体;color:black;background:white\">项目地点：盐城市五官科医院、儿童医院</span><span style=\"font-family:宋体;background:white\">。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.3</span><span style=\"font-family:宋体;color:black;background:white\">招标内容：盐城市五官科医院1台CT机、儿童医院1台CT机，共计两台设备放射诊疗设备预评价和控制效果评价，包括但不限于：</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">（1）卫生：放射诊疗设备预评价和控制效果评价（含批复）、办理放射诊疗许可证；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">（2）环保：办理环评备案及防护检测、办理辐射安全许可证；招标人保留对上述暂定招标内容适当调整的权利。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3</span><span style=\"font-family:宋体;color:black;background:white\">、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.1</span><span style=\"font-family:宋体;color:black;background:white\">投标人是具有独立法人资格的单位，能独立承担民事责任；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.2</span><span style=\"font-family:宋体;color:black;background:white\">投标人具备职业卫生（放射卫生）技术服务机构乙级及以上级资质；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.3</span><span style=\"font-family:宋体;color:black;background:white\">投标人具有社会辐射环境检测机构乙级资质；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.4</span><span style=\"font-family:宋体;color:black;background:white\">投标人资质需经省级及以上卫生主管部门和环保主管部门认可；</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">4</span><span style=\"font-family:宋体;color:black;background:white\">、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名并领取招标文件，投标报名时间2021年2月23日8时至2021年3月1日18时。联系人：胡荣艳，0515-69057406；手机：13815581495。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">5</span><span style=\"font-family:宋体;color:black;background:white\">、本项目采用竞争性谈判方式。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">6</span><span style=\"font-family:宋体;color:black;background:white\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">7</span><span style=\"font-family:宋体;color:black;background:white\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监督审查室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">其余内容详见招标文件。</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;color:black;background:white\"> </span><span style=\"font-family:宋体;color:black;background:white\">招标人：盐城市第一人民医院</span></p><p style=\"text-align:right;background: white\"><span style=\"font-family:宋体;color:black;background:white\"> </span><span style=\"font-family:宋体;color:black;background:white\">时间：2021年2月23日 </span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'招标采购中心\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-02-23\',\n  \'timestamp\' => \'2021-02-23\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8c7724d81d6d78b3\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627522720');
