INSERT INTO `p8_admin_log` VALUES ('3172','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'[db:来源]\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">因医院实际工作需要，于2021年2月5日进行项目招标，经评标小组评审，中标结果如下：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">一、患者满意度第三方社会评价项目 </span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">中标单位：重庆至道医院管理股份有限公司</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">中标价：18.9万元</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">二、服务品质持续提升咨询项目 </span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">中标单位：重庆先数科技有限公司</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">中标价：17.5万元</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\"></span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监督审查室联系，电话0515-66696810。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\"></span></p><p style=\"text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">盐城市第一人民医院</span></p><p style=\"text-indent: 2em; text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\"> 2021年2月6日</span></p><p><br/></p>