INSERT INTO `p8_admin_log` VALUES ('3258','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'858\',\n  \'title\' => \'盐城市第一人民医院2020年公开招聘医学人才补充公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'人事处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); line-height: 33px; font-family: &quot;microsoft Yahei&quot;; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: left; text-indent: 43px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">根据2020年8月12日发布的《盐城市第一人民医院2020年公开招聘医学人才公告》，目前部分招聘岗位报名人数严重不足，未达到开考比例。为确保盐城市儿童医院正常有序运转，经研究决定，对上述公告中未达到开考比例的19个岗位发布补充公告，进行补充报名，具体事项安排如下：</span></p><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); line-height: 33px; font-family: &quot;microsoft Yahei&quot;; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: left; text-indent: 36px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">1</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">、补充报名时间：2020年9月21日至9月25日，上午8:00—11:30，下午2:30—5:30。</span></p><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); line-height: 33px; font-family: &quot;microsoft Yahei&quot;; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: left; text-indent: 40px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">2</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">、调整后的招聘岗位表详见附件。</span></p><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); line-height: 33px; font-family: &quot;microsoft Yahei&quot;; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: left; text-indent: 40px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">3</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">、《</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 21px;\">盐城市第一人民医院2020年公开招聘医学人才公告</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: black; font-family: 仿宋; font-size: 20px;\">》中的其他事项保持不变，按原定的程序和要求执行。</span></p><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); line-height: 33px; font-family: &quot;microsoft Yahei&quot;; fon