INSERT INTO `p8_admin_log` VALUES ('3274','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'858\',\n  \'title\' => \'盐城市第一人民医院2019年下半年 公开招聘医学人才补充公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'人事处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">根据</span>2019<span style=\"font-family: 宋体;\">年</span>11<span style=\"font-family: 宋体;\">月</span>7<span style=\"font-family: 宋体;\">日发布的《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<span style=\"font-family: 宋体;\">〕</span>9<span style=\"font-family: 宋体;\">号），盐城市第一人民医院部分招聘岗位（上海及南京招聘点）未达成开考条件或未完成招聘计划。因工作需要，经研究决定，对上述公告中肿瘤内科等</span>63<span style=\"font-family: 宋体;\">个岗位重新组织公开招聘，现发布补充公告如下：</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">1. <span style=\"font-size: 20px; font-family: 宋体;\">补报名时间：</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年</span>12<span style=\"font-size: 20px; font-family: 宋体;\">月</span>26<span style=\"font-size: 20px; font-family: 宋体;\">日至</span>12<span style=\"font-size: 20px; font-family: 宋体;\">月</span>28<span style=\"font-size: 20px; font-family: 宋体;\">日（周六正常接受报名）；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">2. <span style=\"font-size: 20px; font-family: 宋体;\">报名地点：盐城市第一人民医院主院区（人民南路</span>166<span style=\"font-size: 20px; font-family: 宋体;\">号，门诊楼四楼人事处）；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">3. <span style=\"font-size: 20px; font-family: 宋体;\">联系电话：</span>0515-66696806<span style=\"font-size: 20px; font-family: 宋体;\">；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">4. <span style=\"font-size: 20px; font-family: 宋体;\">笔试、面试时间及地点将由盐城市第一人民医院另行通知；</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">5. <span style=\"font-size: 20px; font-family: 宋体;\">调整后招聘岗位表，详见附件。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">6. <span style=\"font-size: 20px; font-family: 宋体;\">《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">〕</span>9<span style=\"font-size: 20px; font-family: 宋体;\">号）中的其他条件保持不变。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体;\">特此公告。</span></span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">附件：</span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-size: 20px;\">1<span style=\"font-size: 20px; font-family: 宋体;\">、</span><a href=\"http://wsj.yancheng.gov.cn/module/download/downfile.jsp?classid=0&filename=8cb79e644915433da492867f67156c2d.xls\"><span style=\"font-size: 20px; font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); background: white;\">盐城市第一人民医院2019年下半年公开招聘医学人才岗位表（补充）</span></a></span></p><p style=\"text-align: left; text-indent: 40px; line-height: 3em;\"><span style=\"font-size: 20px;\">2<span style=\"font-size: 20px; font-family: 宋体;\">、</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年</span>11<span style=\"font-size: 20px; font-family: 宋体;\">月</span>7<span style=\"font-size: 20px; font-family: 宋体;\">日盐城市人力资源和社会保障局发布的《盐城市卫生健康委员会部分直属事业单位</span>2019<span style=\"font-size: 20px; font-family: 宋体;\">年下半年赴外公开招聘医学人才公告》（盐人社事招公告核〔</span>2019<sp