INSERT INTO `p8_admin_log` VALUES ('3276','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'858\',\n  \'title\' => \'盐城市一院2019年公开招聘编外护理专业技术人员（第二批）拟录用人员名单公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'人事处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">根据《盐城市第一人民医院2019年公开招聘编外护理专业技术人员公告（第二批）》公告要求，在盐城市卫生健康委员会指导下，盐城市第一人民医院依次组织了2019年第二批编外护理人员招聘报名、复审、笔试、面试、实践操作考核、体检、政审等招聘工作环节，现根据公告将拟录用的58名护理人员名单面向社会公示。</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 0em;\">一、</span><span style=\"font-family: 宋体; font-size: 20px; text-indent: 0em;\">应届生本科</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">蔡银婷 陈荣誉 陈俞静 季倩 吕国跃 秦吕敏 徐榕梓 杨健峰</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张进 庄朋朋</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\"><br/></span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">二、应届生大专</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">卞玉霞 卜雪 陈<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>枫 陈晓梦 丁<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>洁 杲禹香 葛梦琦 谷建娇</span></p><p style=\"text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">胡<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>婧 花璐皇甫立胜吉<span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span>琳 李青青 李<span style=\