INSERT INTO `p8_admin_log` VALUES ('3280','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'858\',\n  \'title\' => \'2019年临床医技专业技术人员招聘进入体检、考察人员名单公布\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'人事处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-top: 8px; text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-size:20px;font-family:宋体;color:black\">根据《盐城市第一人民医院2019年临床医技毕业生招聘公告》，我院于7月13日和7月27日分别进行了招聘笔试和面试，现将进入体检、考察人员名单予以公布（按姓名汉语拼音排序），<span style=\"background:white\">根据体检和考察结果，确定拟聘用人员名单。</span></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-size:20px;font-family:宋体;color:black\">对本公布名单如有异议可向医院纪委监察室反映。时间7月27日-8月1日。</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-size:20px;font-family:宋体;color:black\">监督电话：0515-66696810(市第一人民医院纪检监察室)；</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: left; line-height: 3em; text-indent: 2em;\"><span style=\"font-size:20px;font-family:宋体;color:black\">咨询电话：0515-66696806（市第一人民医院人事处）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size:20px;font-family:宋体\">一、临床及医技科室1-临床医学本科</span></p><p style=\"text-indent: 2em;\"><span style=\"color: rgb(89, 93, 96); font-family: 宋体; font-size: 20px;\">陈政旭</span><span style=\"color: rgb(89, 93, 96); font-family: 宋体; font-size: 20px;\">洪豆<span style=\"color: rgb(89, 93, 96); font-family: 宋体; font-size: 20px;\">余婷婷<span style=\"color: rgb(89, 93, 96); font-family: 宋体; font-size: 20px;\">张杰</span></span></span></p><p style=\"line-height: 