INSERT INTO `p8_admin_log` VALUES ('3302','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市儿童医院二期工程项目环境影响评价 第一次公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-family: 仿宋;\">根据《中华人民共和国环境影响评价法》、《环境影响评价技术导则</span> <span style=\"font-family: 仿宋;\">总纲》、《环境影响评价公众参与办法》的相关规定，对《盐城市儿童医院二期工程项目环境影响评价报告书》工作相关信息，以便广大公众在环境保护方面提出更多的意见和建议。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">一、建设项目名称及范围</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">项目名称：盐城市儿童医院二期工程项目</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">项目地址：盐城市亭湖区毓龙路北侧，迎宾北路东侧地块</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">项目建设内容及规模：二期工程总建筑面积约</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">70000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，其中：地上建筑面积</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">50000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，地下建筑面积</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">20000</span><span style=\"font-size: 16px; font-family: 仿宋;\">平方米，项目总投资</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">50000</span><span style=\"font-size: 16px; font-family: 仿宋;\">万元，医院床位设置</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">500</span><span style=\"font-size: 16px; font-family: 仿宋;\">张，停车位</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">300</span><span style=\"font-size: 16px; font-family: 仿宋;\">辆。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">二、建设单位名称及联系方式</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">建设单位：盐城市第一人民医院</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">单位地址：江苏省盐城市毓龙西路</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">166</span><span style=\"font-size: 16px; font-family: 仿宋;\">号</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">联系人：霍主任</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">联系电话：</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">0515-66698027</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">三、承担评价工作的环境影响评价机构名称和联系方式</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">环评单位：江苏南大环保科技有限公司</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">单位地址：江苏省南京经济技术开发区恒竞路</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">27</span><span style=\"font-size: 16px; font-family: 仿宋;\">号</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">联系人：王工</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">联系电话：</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">025-68568026</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">电子邮箱：</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">wanghj@nuep.com.cn</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">四、环境影响评价工作的工作程序和主要工作内容：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">本次环境影响评价工作按照《中华人民共和国环境保护法》、《中华人民共和国环境影响评价法》、《环境影响评价技术导则</span> <span style=\"font-size: 16px; font-family: 仿宋;\">总纲》等相关法律、条例、技术规范开展。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">本次评价主要工作内容包括：总则、建设项目概况、建设项目工程分析、环境现状调查分析、环境影响预测与评价、污染防治措施、环境经济损益分析、环境管理与监测计划、评价结论。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">五、征求公众意见的主要事项：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">1</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您是否知道</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">/</span><span style=\"font-size: 16px; font-family: 仿宋;\">了解该区域；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">2</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您认为是否有必要开展本次环境影响评价工作；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">3</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您对区域目前的建设持何态度，并简要说明原因；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">4</span><span style=\"font-size: 16px; font-family: 仿宋;\">）根据您掌握的情况，认为目前区域对环境质量造成的危害</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">/</span><span style=\"font-size: 16px; font-family: 仿宋;\">影响方面及程度；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">5</span><span style=\"font-size: 16px; font-family: 仿宋;\">）您对该区域环保方面有何建议和要求；</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">6</span><span style=\"font-size: 16px; font-family: 仿宋;\">）从环保角度出发，您对区域今后发展持何种态度，并简要说明原因。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">六、公众提出意见的方式和途径</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">公众提交意见时，应当提供有效的联系方式，鼓励采用实名方式提交意见并提供常住地址。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">公众可以通过电话等方式，在规定时间内将填写的公众意见表提交建设单位，反映与环境保护有关的意见和建议。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">在环境影响报告书征求意见稿编制过程中，公众均可向建设单位提出与环境影响评价相关的意见。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">七、公众意见表的网络链接：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">见附件</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">八、提交公众意见表的方式和途径：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: 仿宋;\">自本公告之日起</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">10</span><span style=\"font-size: 16px; font-family: 仿宋;\">个工作日内，公众可向建设单位电子邮箱提交电子版公众意见表，或将纸质版公众意见表邮寄或送至建设单位联系地址。</span></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 16px; font-family: 仿宋;\">九、公示时间：</span></strong></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">自本公示信息发布之日起十个工作日。</span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: 仿宋; font-size: 18px;\">附件：</span><a href=\"http://www1.ycsyy.com/upload/files/2020/7/5877a570ac90660.docx\" title=\"建设项目环境影响评价公众意见表(盐城市儿童医院二期工程)20200721.docx\" style=\"color: rgb(0, 102, 204); font-size: 18px; font-family: 宋体, SimSun; text-decoration: underline;\"><span style=\"font-family: 宋体, SimSun;\">建设项目环境影响评价公众意见表(盐城市儿童医院二期工程)20200721.docx</span></a></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-size: 16px; font-family: 仿宋;\"><br/></span></p><p style=\"text-align:left;text-indent:32px\"><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 18px;\"></span></p><p style=\"text-align:right;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\"> </span><span style=\"font-size: 16px; font-family: 仿宋;\">盐城市第一人民医院</span></span></p><p style=\"text-align:right;text-indent:32px\"><span style=\"font-size: 18px;\"><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">2020</span><span style=\"font-size: 16px; font-family: 仿宋;\">年</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">7</span><span style=\"font-size: 16px; font-family: 仿宋;\">月</span><span style=\"font-size: 16px; font-family: &quot;Times New Roman&quot;, serif;\">21</span><span style=\"font-size: 16px; font-family: 仿宋;\">日</span></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2020-07-21\',\n  \'timestamp\' => \'2020-07-21\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d4cd4f75c986f594\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524488');
INSERT INTO `p8_admin_log` VALUES ('3303','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院2020年度市级部门预算公开\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'财务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-family: 宋体, SimSun;\">第一部分　部门概况</span></strong></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 黑体;\">一、单位主要职能。</span></span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院，系南京大学附属盐城第一人民医院、南通大学第四附属医院、徐州医科大学附属临床医院、江苏医药职业学院第一附属医院、第一临床学院，创建于1948年，经过近七十年的发展，现已成为一所集医疗、教学、科研、康复、急救为一体的国家三级甲等综合性医院。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院共设三个院区：北区（毓龙西路166号）、南区（人民南路66号）、五官科院区（南城河路12号）；医院现有总占地面积20.73万平方米，建筑面积36万平方米；资产总值35.95亿元；目前实际开放床位3100张，职工3200人。</span></p><p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院设置临床科室34个、医技科室23个、护理单元103个、职能处室24个，拥有省级临床重点专科14个、市级医学重点学科18个、市级临床重点专科30个、全国综合医院示范中医科1个、通过国家药物临床试验认证机构认证科室13个。医院同时拥有省区级胸痛、卒中、创伤、孕产妇急危重症救治中心，省级紧急医学救援基地医院，设有全市的急救中心、临床检验中心、医学特检中心、糖尿病防治中心、心脏介入中心和微创中心等医学中心。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院有南通大学、徐州医科大学硕士培养点18个，博士培养点1个；有博士生导师1名，硕士生导师35名，副教授11名，兼职教授、副教授176名；医院现有医学博士97名，医学硕士553名；高级职称477人；被确认为省“333”工程人才12名、省“科教强卫”工程人才8名、市“515”人才11名。</span></p><p style=\"margin-bottom: 0px; text-align: justify; text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院拥有万元以上医疗设备近4000台件，医疗设备总额8.3亿元，先进的大型设备有进口西门子64层PET—CT 1台、核磁共振5台、炫速双源CT2台、高档螺旋CT6台、高能直线加速器3台、SPE CT 2台，医院同时拥有全球领先的德国轨道物流系统和全市唯一的贝克曼实验室自动化流水线系统，有集成度较高的覆盖全院的信息系统。</span></p><p style=\"line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院加强对外协作，与国内外多家知名医院、学科和医学院校建立了友好合作关系。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 黑体;\">二、2020年度部门主要工作任务及目标。</span></span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">1、2020年财务工作计划</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">年门诊量≥145万人次；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">年住院工作量≥14.50万人次；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">药占比≤35%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">卫生材料收入占医疗收入比≤17%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">检查、检验收入占医疗收入比≤20%；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">医院总收入＞26.25亿，医院总支出＜25.75亿；</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2、严格执行财务、物价、医保、税务、工商等政策、法律法规。</span></p><p style=\"text-indent: 37px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3、实行全面预算管理，药品、卫材、人员等支出实施预算管理，预算管理符合率达96%；门急诊、住院病人均次费用控制在规定指标内。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">4、寻求新的经济增长点：大力开展新技术新项目，努力提高新设备的使用率；扩大病人数量：改善就诊环境，向上和大医院联营，向下和社区结对。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">5、调整收入、支出结构：提高业务收入中技术劳务性收入的比例，降低药品收入占医药收入的比重，降低卫生材料支出占医药收入的比重；适度提高人员支出占业务支出的比重，降低药品、卫材、管理费用支出占业务支出的比重。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">6、控制费用：控制每出院病人药品费用及材料费用，控制医保费用，控制应收账款和应付账款及贷款规模。</span></p><p style=\"background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">7、总体规划，做好与城南医院一体化管理。建立统一的财务管理，做好成本核算、绩效考核分配。</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 21px; font-family: 宋体;\">第二部分　<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">2020</span>部门预算表</span></strong></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-indent: 42px; line-height: 3em;\"><span style=\"color: red; font-family: 宋体, SimSun; font-size: 20px;\">详见附件</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 21px; font-family: 宋体;\">第三部分　2020部门预算情况说明</span></strong></span></p><p style=\"text-indent: 47px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、收支预算总表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本表反映部门年度总体收支预算情况。根据《盐城市财政局关于批复2020年市级部门预算的通知》（盐财预〔2019〕4号）填列。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度收入、支出预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">525032.06</span>万元，与上年相比收、支预算总计各增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">28064</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">11.97</span>%。主要原因是原城南医院并入我院后业务收入、业务支出等较上年大幅增长。其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（一）收入预算总计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元。包括：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1．财政拨款收入预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一般公共预算收入预算<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，与上年相比减少<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款收入2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2．其他资金收入预算总计<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">257218.27</span>万元。与上年相比增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">29999.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">13.20</span>%。主要原因是城南医院并入我院后业务收入较2019年有较大幅度增长。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（二）支出预算总计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元。包括：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1．卫生健康（类）支出<span style=\"text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\">262516.03</span>万元，主要用于人员经费、卫生材料、药品费用等支出。与上年相比增加<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">28064</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">11.97</span>%。主要原因是人员经费、卫生材料、药品费用等大幅增加。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、收入预算情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本年收入预算合计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元，其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一般公共预算收入<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">2.02</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其他资金<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">257218.27</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">97.98</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><em><span style=\"font-size: 19px; font-family: 宋体;\">（可用饼图显示本年收入结构图）</span></em></span></p><p style=\"text-indent: 37px; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2020/2/46941ac3248722c7.png\" title=\"image.png\" alt=\"image.png\"/></p><p style=\"text-indent: 37px; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">图1：收入预算图</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、支出预算情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本年支出预算合计<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">262516.03</span>万元，其中：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">基本支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">257998.03</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">98.28</span>%；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">3558</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">1.36</span>%；</span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">其他支出<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">960</span>万元，占<span style=\"font-size: 19px; font-family: 宋体, SimSun; text-decoration: underline;\">0.56</span>%</span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><em><span style=\"font-size: 19px; font-family: 宋体;\">（可用饼图显示本年支出结构图）</span></em></span></p><p style=\"margin-left: 7px; text-indent: 28px; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2020/2/6a232f9fa2eeb0f4.png\" title=\"image.png\" alt=\"image.png\"/></p><p style=\"margin-left: 7px; text-align: center; text-indent: 28px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">图2：支出预算图</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、财政拨款收支预算总表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度财政拨款收、支总预算<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">10595.52</span>万元。与上年相比，财政拨款收、支总计各减少<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、财政拨款支出预算表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年财政拨款预算支出<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">5297.76</span>万元，占本年支出合计的<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">2.02</span>%。与上年相比减少<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1935.20</span>万元，增长<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">-26.76</span>%。主要原因是城南医院4亿贷款贴息5年到期，减少财政贴息拨款支出2000万元。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、财政拨款基本支出预算表情况说明</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年度财政拨款基本支出预算<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1739.76</span>万元，其中：人员经费<span style=\"font-size: 20px; font-family: 宋体, SimSun; text-decoration: underline;\">1739.76</span>万元。主要包括：基本工资、津贴补贴、奖金、社会保障缴费、伙食补助费、绩效工资、其他工资福利支出、离休费、退休费、抚恤金、生活补助、医疗费、奖励金、住房公积金、提租补贴、其他对个人和家庭的补助支出。</span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><strong><span style=\"font-size: 19px; font-family: 宋体;\">第四部分　名词解释</span></strong></span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、财政拨款：指由一般公共预算、政府性基金预算安排的财政拨款数。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、一般公共预算：包括公共财政拨款（补助）资金、专项收入。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、财政专户管理资金：包括专户管理行政事业性收费（主要是教育收费）、其他非税收入。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、其他资金：包括事业收入、经营收入、其他收入等。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、基本支出：包括人员经费、商品和服务支出（定额）。其中，人员经费包括工资福利支出、对个人和家庭的补助。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、项目支出：包括编入部门预算的单位发展项目、市直发展项目支出安排数等。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、“三公”经费：指市级部门用一般公共预算财政拨款安排的因公出国（境）费、公务用车购置及运行费和公务接待费。其中，因公出国（境）费指单位公务出国（境）的住宿费、旅费、伙食补助费、杂费、培训费等支出；公务用车购置及运行费指单位公务用车购置费及租用费、燃料费、维修费、过路过桥费、保险费、安全奖励费用等支出；公务接待费指单位按规定开支的各类公务接待（含外宾接待）支出。</span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 16px; text-indent: 2em;\"><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-family: 宋体, SimSun; font-size: 20px;\" href=\"http://www1.ycsyy.com/upload/files/2020/2/ce479b27b33eae6.xls\" title=\"盐城市第一人民医院2020年部门预算公开表.xls\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院2020年部门预算公开表.xls</span></a></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span><br/></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2020-02-15\',\n  \'timestamp\' => \'2020-02-15\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1df4400565f85504\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1627524488');
INSERT INTO `p8_admin_log` VALUES ('3304','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'市一院召开廉政风险防控“学、议、行”专题点评会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'监察室\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 21px;\">12月25日下午，我院组织召开廉政风险防控“学、议、行”工作专题点评会，对重点部门、关键岗位的廉政风险点排查、防控工作进行分析评议。会上，药学部、设备处、医务处的负责同志分别就本部门贯彻落实9月30日全市医疗机构高值耗材专项整治暨卫生健康系统廉政风险防控“学、议、行”部署推进会的精神，开展廉政风险防控工作在会上作了交流发言，李明祥、柏宏坚、嵇友林等分管院领导分别进行了点评。出席会议的市纪委监委派驻纪检监察组、市卫健委领导予以现场指导，充分肯定了三部门的风险排查及防控工作，并就我院下一步廉政风险防控工作提出要求。会议开得很成功，取得了预期的效果，为全院面上风险防控工作的扎实推进提供了指导意义。</span></p><p style=\"text-indent: 43px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-12-26\',\n  \'timestamp\' => \'2019-12-26\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1b62dcb8f33f428d\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524489');
INSERT INTO `p8_admin_log` VALUES ('3305','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'市一院召开四季度工休座谈会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'监察室\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">进一步改善医疗服务是“不忘初心、牢记使命”主题教育活动的重要一环。找差距、抓落实，查找出病员和家属不满意的问题立行立改，切实改善群众看病就医体验，努力提升群众获得感和满意度是医院工作永恒的主题。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">12月18日，医院在主院区门诊四楼会议室召开四季度病员工休座谈会。主院区内科病区16名病员（家属）代表以及医院行风办、护理部、医务处、总务处、财务处、药学部、医保办等职能处室负责人参加会议。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上，医院行风办工作人员向大家介绍了医院基本概况，并邀请病员（家属）对医院各项工作进行现场评议，排查医院医疗服务中群众不满意、不方便、不放心的环节。与会人员纷纷表示，医院环境优雅舒适，医护人员服务态度好、医疗技术精、医德医风佳。他们还点名表扬了吉亚军、张由美、张扣东、卞伟纲、周晓宁、戴夕超、周欢欢、周芬、郑洁、周君珺、徐海艳、沈丽丽等医护人员。同时，对医院各项工作提出6条意见和建议。相关科室责任人员在现场对部分问题进行解答，对于需要多部门协调解决的问题，行风办随即汇总整理，迅速落实整改措施。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-12-20\',\n  \'timestamp\' => \'2019-12-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'79ef8903756dc15b\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524490');
INSERT INTO `p8_admin_log` VALUES ('3306','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'市一院对行政处室副科职干部进行任前廉政教育\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'监察室\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2019年12月19日，市一院14名行政处室副科级干部经过领导干部选拔任用程序上任。院党委、纪委通过任前廉政谈话、廉政知识考试、警示案例教育、签订廉洁从政承诺书、发放廉洁从政口袋书等“五个一”形式，在干部走上新岗位前吹廉风、划红线，进一步筑牢新任干部拒腐防变的思想道德和纪法防线。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">纪委书记卞学梅在谈话中对新上岗的同志提出了五点要求。一是要坚决贯彻执行党的路线方针政策和上级决策部署，严守党的政治纪律、组织纪律和工作纪律，始终做到讲党性、守规矩、做表率。二是要严格遵守国家法律法规、医院各项规定制度，不徇私枉法、不违规办事，始终做到依法行政、按制度办事。三是要严格落实中央“八项规定”精神，始终保持求真务实、谦虚谨慎、勤俭节约的良好作风。四是要模范遵守中央和省、市关于廉洁自律的各项规定，增强防腐防变意识。五是要履职尽责，切实履行“一岗双责”，顾全大局，带好团队，勤奋工作，认真落实职责范围内的廉政建设工作。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">新上任的同志纷纷表示，一定不辜负组织嘱托，不忘初心，牢记使命，严以律己，廉洁从政，做一个让组织放心、群众满意的好干部。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">市一院对新任干部进行廉政教育，已经常态化。这既是履行干部选拔要求的必经程序，也是强化党风廉政建设的一项重要举措，充分体现了党组织对干部的关心和爱护。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-12-20\',\n  \'timestamp\' => \'2019-12-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'60dc03281206acd6\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524490');
INSERT INTO `p8_admin_log` VALUES ('3307','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院与耀州区开展对口协作管理\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">12月5日，盐城市第一人民医院与耀州区人民医院对口协作管理会议正式召开。会议宣布了耀州区人民医院新的领导班子。2019年6月，在苏陕协作的大背景下，盐城市第一人民医院与耀州区人民医院正式建立对口支援关系，盐城市一院前后选派2批次共计10位专家默默扎根耀州，潜心“传帮带”，积极开展新技术，取得了10多项医疗技术新突破，填补了多项技术空白。这次两家医院对口协作管理，是医改新形势的要求，更是加强苏陕对口支援，推动健康扶贫工作，助力耀州百姓健康的一件大事，对推动耀州卫生健康事业发展、提高医疗服务水平具有重要的意义。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-12-06\',\n  \'timestamp\' => \'2019-12-06\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8b10cc198d8491cc\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524490');
INSERT INTO `p8_admin_log` VALUES ('3308','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'南京大学医学院附属盐城第一医院正式揭牌！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月21日，南京大学和盐城市人民政府举行合作签约仪式，南京大学医学院附属盐城第一医院正式揭牌。盐城市第一人民医院与南京大学医学院将在学科建设、医疗技术、人才培养、科研交流和医院管理等方面紧密合作，更好地承担服务全市百姓健康的使命和责任。盐城市委书记、市人大常委会主任戴源，盐城市委副书记、市长曹路宝，江苏省教育厅副厅长王成斌，江苏省卫生健康委副巡视员祁爱平，南京大学党委常委、副校长、医学院院长张峻峰共同为“南京大学医学院附属盐城第一医院”揭牌。盐城市副市长葛启发、南京大学、盐南高新区和市有关部门及单位负责同志、盐城市第一人民医院领导班子等出席签约揭牌仪式。盐城市委副书记、市长曹路宝，南京大学党委常委、副校长、医学院院长张峻峰，江苏省卫生健康委副巡视员祁爱平，江苏省教育厅副厅长王成斌分别致辞。南京大学党委常委、副校长、医学院院长张峻峰和盐城市政府副市长葛启发代表双方签约。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-22\',\n  \'timestamp\' => \'2019-11-22\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'914787d804fe246a\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524491');
INSERT INTO `p8_admin_log` VALUES ('3309','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'韩国延世大学世福兰斯医院合作医院在盐城市一院正式挂牌！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月21日下午，韩国延世大学世福兰斯医院李炳奭院长一行6人来盐，就盐城市第一人民医院与韩国延世大学世福兰斯医院建立合作医院关系举行揭牌仪式。盐城市人大常委会副主任周键云和韩国延世大学世福兰斯医院院长李炳奭为盐城市第一人民医院与韩国延世大学世福兰斯医院合作医院揭牌。盐城市第一人民医院院长查文章和韩国延世大学世福兰斯医院院长李炳奭签署合作协议。盐城市卫生健康委员会主任徐杰在讲话中指出，韩国延世大学世福兰斯医院在很多学科领域方面技术领先，与我市医疗机构有很大的合作空间，特别是与市第一人民医院有广泛的合作基础，在医院管理交流、人才培养、技术开发上都可以进行合作，建立多学科、多层次交流机制，努力搭建优势学科创新平台，促进医疗技术水平进步。目前，我市正在实施“健康盐城”建设，努力提高全市的医疗健康水平。市委、市政府十分重视中韩卫生健康产业的合作，市卫健委将全力支持盐城医疗界与韩国延世大学医院的合作，为其在盐城的发展提供一切便利条件。盐城市第一人民医院党委书记张慧娟和韩国延世大学世福兰斯医院院长李炳奭分别致辞。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-22\',\n  \'timestamp\' => \'2019-11-22\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'be9bf590583c98c0\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524491');
INSERT INTO `p8_admin_log` VALUES ('3310','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'联合国糖尿病日∣防控糖尿病，我们在行动！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11月14日是“联合国糖尿病日”，今年的主题是“家庭与糖尿病”，旨在号召糖尿病患者家属与糖尿病患者一起抗击糖尿病，养成良好的生活习惯。11月14日上午，盐城市第一人民医院内分泌科携手临床营养科、药剂科的专家和志愿者走进盐南高新区新都街道社区卫生服务中心为就诊患者义诊宣传。11月14下午，盐城市一院临床营养科主任医师赵桐为食堂厨师及配餐员讲解《中国糖尿病膳食指南(2017)》。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-15\',\n  \'timestamp\' => \'2019-11-15\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'7702a435e5110280\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524492');
INSERT INTO `p8_admin_log` VALUES ('3311','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'2019年盐城市急诊质量管理专业委员会年会成功举行\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11月7日-8日，由南通大学第二附属医院、盐城市医院协会主办，盐城市第一人民医院承办的国家级继续教育项目‑---休克规范化诊疗新进展学习班暨第二届盐城市急诊急救医学论坛、2019年盐城市急诊质量管理专业委员会年会在盐城市港府洲际酒店隆重举行。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">本次会议开幕式由盐城市第一人民医院急诊科副主任陈茂刚主持。会议开幕式上，盐城市第一人民医院查文章院长、盐城市中医药管理局陶惠副局长等领导分别作重要讲话，描绘出盐城市第一人民医院急诊科未来发展的美好蓝图，为盐城市急诊学科的建设和发展指明了前进方向。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">本次会议参会人员达150人次，会议现场学术氛围浓厚，讲题内容精彩纷呈，会场井然有序，参会代表听课认真，讨论热烈。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-09\',\n  \'timestamp\' => \'2019-11-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d5c214c190dea1fa\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524492');
INSERT INTO `p8_admin_log` VALUES ('3312','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院开展感控全员培训考核工作\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为进一步落实国家卫生健康委发布的《国家卫生健康委关于进一步加强医疗机构感染预防和控制工作的通知》和《江苏省三级综合医院评审标准实施细则》中的要求，10月份，盐城市一院分阶段开展医院感染防控知识的全员培训与考核工作。全院各临床科室（病区）根据通知要求，积极组织医务人员参加了培训学习，在感染防控相关基础知识，包括医院感染基本概念、标准预防概念、手卫生、消毒隔离、抗菌药物管理、无菌操作、安全注射、医疗废物分类、多重耐药菌防控等方面，利用“问卷星”VIP系统等培训考核平台进行考核。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-04\',\n  \'timestamp\' => \'2019-11-04\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'6bf22aaa51f1f5c8\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524493');
INSERT INTO `p8_admin_log` VALUES ('3313','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院启动2019年度盐城市第一人民医院优秀科研人才培育基金\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为充分调动我院广大医务和科研人员从事科研活动的积极性，挖掘我院的科研潜力，发现和培养优秀科研人才，今年，我院出台下发了《盐城市第一人民医院培育基金管理办法（试行）》,并于10月底正式启动2019年度盐城市第一人民医院院内培育基金申报工作。设立培育基金项目是为促进学科发展，人才成长，分设重点项目和青年项目，坚持“个人申请，专家评审，择优资助，宁缺毋滥”的原则，每年在财务预算中单独列支一定额度的科研培育基金、专款专用。院科技处负责统一管理培育基金项目，包括组织申报、评审、立项、实施、检查、协调和验收等工作。设立培育基金项目将进一步提升我院国家级项目申报的数量和质量，鼓励开展国家级项目的前期研究工作，促进医院与科研院所的合作，为申报国家级项目提供基础研究积累，实现各类国家级项目立项数的突破。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><strong><span style=\"font-size: 19px; font-family: 仿宋;\"></span></strong></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-03\',\n  \'timestamp\' => \'2019-11-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'565262e24d7be995\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524494');
INSERT INTO `p8_admin_log` VALUES ('3314','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院医联体——响水县港城医院正式运行\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">11月1日，由盐城市第一人民医院结成的全省首家市县镇三级紧密型医联体——响水县港城医院投入运营，为陈家港镇及周边地区居民提供高水平的医疗服务。开业当天，盐城市第一人民医院院长查文章带领骨科、耳鼻喉科、心血管内科等12个专科的专家，为患者提供免费的诊疗服务、健康咨询、康复指导等。此次义诊共接诊患者700余人，发放健康宣教手册500份，免费测血糖130人次、测血压225人次。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-11-02\',\n  \'timestamp\' => \'2019-11-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'61c57d0a03c756fe\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524494');
INSERT INTO `p8_admin_log` VALUES ('3315','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'南京大学访问团赴盐城市第一人民医院考察调研\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">10月22日下午，南京大学党委常委、副校长、医学院院长张峻峰率团赴盐城市第一人民医院考察调研，南京大学医学院党政领导班子、盐城市人民政府副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院领导班子等陪同考察，双方就深化合作事宜开展交流讨论，达成广泛共识。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张峻峰一行先后参观了盐城市第一人民医院临床技能培训中心等临床、教学、科研场地与设施，并与相关负责同志进行了交流座谈。座谈会上，盐城市第一人民医院院长查文章首先汇报了医院基本情况、教学工作及双方合作方案。盐城市卫健委主任徐杰简要介绍了盐城市医疗卫生发展建设情况。他强调，盐城市近年来正大力推进医疗卫生事业建设，南京大学与盐城市第一人民医院携手合作是盐城市医疗卫生事业发展的重要契机，希望通过与南京大学的深度合作，进一步完善医院人才队伍建设、学科建设和体制机制建设，全力提升盐城市医疗服务水平。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">张峻峰对盐城市委市政府的大力支持以及盐城市第一人民医院为开展合作所做的前期准备工作表示感谢。他指出，以临床为中心，教学与科研辅助的“一体两翼”格局，将有助于医院向更高水平发展。南大医学院要与盐城市第一人民医院在前期共酌基础上，“做实、做快、做好”相关合作事宜，在科研合作平台、人才培养机制和临床诊疗体系等方面进行深度探索，全面落实体制机制建设，通过互派人员开展学习交流、共同筹划科研年会等一系列举措，加强人才队伍方面的合作，加快实现医院科研水平的提升。张峻峰强调，双方的合作对南京大学来说是一个新的起点，也是南京大学服务国家、服务江苏、服务盐城的一个方向，医学院更应当以此为契机，不忘初心、牢记使命，以全新的战略高度、全局高度、历史高度谋发展，为早日实现习近平总书记提出的“第一个南大”的宏伟目标作出应有的贡献。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-10-23\',\n  \'timestamp\' => \'2019-10-23\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'02e8c98aae18f354\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524495');
INSERT INTO `p8_admin_log` VALUES ('3316','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院开展2019年“手卫生宣传日活动”\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为迎接全球洗手日的到来，将手卫生的基本理念和服务宗旨充分展现在医院的每一个角落，10月15日,盐城市第一人民医院开展手卫生宣传日活动，在全院人员中普及手卫生知识，提高全员手卫生依从性与正确性，进而预防与控制医院感染的发生，保障医疗质量及患者的安全。2019年手卫生宣传日活动的主题是:“养成良好卫生习惯，从正确洗手做起”。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">本次活动由院感染管理处、院办、医务处、护理部、宣传处、信息处等科室联合举办。活动内容和形式丰富多彩，在多个院区设置宣传活动，现场运用多媒体、宣传画等方式进行手卫生知识宣传；邀请医院手卫生表演组现场表演一场活力动感的手卫生操；医务人员执行手卫生规范签名承诺活动；开展手卫生相关知识培训及有奖知识竞答活动。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-10-16\',\n  \'timestamp\' => \'2019-10-16\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'c15f6d06d12a1c35\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524496');
INSERT INTO `p8_admin_log` VALUES ('3317','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院召开高值医用耗材专项整治暨廉政风险防控“学、议、行”活动部署动员会议\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为贯彻落实9月30日全市医疗机构高值医用耗材专项整治暨卫生健康系统廉政风险防控“学、议、行”活动部署推进会精神，进一步推动高值医用耗材专项整治工作，不断深化医院廉政风险防控工作，10月9日下午，市一院召开高值医用耗材专项整治暨廉政风险防控“学、议、行”活动部署动员会议。院领导班子全体成员、中层干部、护士长、主诊医师、副主诊医师参加了此次会议。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-10-10\',\n  \'timestamp\' => \'2019-10-10\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'ecf59e757de3304a\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524496');
INSERT INTO `p8_admin_log` VALUES ('3318','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院致敬祖国七十华诞！精彩文艺演出点燃爱国激情\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为隆重庆祝中华人民共和国成立70周年，弘扬医院光荣传统，展示医院发展成果，表达一院人热爱党、热爱祖国、热爱人民的高尚情怀，9月29日下午，盐城市第一人民医院用一场精彩的文艺演出，向祖国母亲70周年华诞献礼。文艺演出设置了三个篇章，精心编排的舞蹈、倾情献唱的金曲、生动有趣的小品和情景剧、百人合唱团等节目精彩纷呈，为中华人民共和国成立70周年呈上了一份特殊的礼物。演出中还举行了“最美白衣天使”颁奖仪式，表彰了一批在医疗、教学、科研、急救等方面取得丰硕成果，在平凡的岗位上作出不平凡业绩的先进、楷模。盐城市政协副主席、市卫健委副主任孙长春，盐城市委宣传部副部长、市文明办主任周柏青，盐城市卫健委主任徐杰，盐城市一院党委书记张慧娟，盐城市一院院长查文章为“最美白衣天使”代表颁奖。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-30\',\n  \'timestamp\' => \'2019-09-30\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'31ab4993d0b9aeeb\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524497');
INSERT INTO `p8_admin_log` VALUES ('3319','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院召开“不忘初心、牢记使命”主题教育动员部署大会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">9月19日下午，盐城市第一人民医院召开“不忘初心、牢记使命”主题教育动员部署大会。院领导班子全体成员，各党支部书记、支委、党小组长、党建联络员，部分党员代表参会。会上，盐城市第一人民医院院长查文章主持会议, 盐城市第一人民医院党委书记张慧娟动员讲话，盐城市卫生健康委员会主任、委主题教育第一指导组组长徐杰讲话。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">徐杰主任在讲话中强调，根据中央精神和省市委、委党委部署，市直卫生健康系统“不忘初心、牢记使命”主题教育已经全面启动。开展“不忘初心、牢记使命”主题教育，一要紧扣主题主线，强化统筹安排；二要坚持以上率下，强化领导示范；三是抓牢基层基础，强化支部引领；四是立足见底见效，强化问题整改；五是秉持从严从实，强化整体推进。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-20\',\n  \'timestamp\' => \'2019-09-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'07a63ad6d179f49a\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524497');
INSERT INTO `p8_admin_log` VALUES ('3320','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院开展“心连心”医院博士专家团走基层活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为隆重庆祝中华人民共和国成立70周年，大力弘扬以爱国主义为核心的伟大民族精神，盐城市第一人民医院把开展“不忘初心、牢记使命”主题教育与开展以“我和我的祖国”为主题的宣传教育活动紧密结合起来，向中华人民共和国成立70周年献礼。9月18日，盐城市第一人民医院组织一批医学博士、专家组成“心连心”博士专家团赴响水县人民医院开展义诊、讲学、查房、手术、会诊等走基层活动。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">今年7月，盐城市第一人民医院与响水县人民医院举行医联体签约仪式，在医疗技术、医疗服务、人才培养、重点科室建设等方面开展合作共建达成协议。此次盐城市第一人民医院“心连心”博士专家团走基层（响水站）活动共接诊450人次，进一步推进了医联体深度合作，提升了基层医疗服务能力，让老百姓获得更加优质的医疗服务和健康保障。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-19\',\n  \'timestamp\' => \'2019-09-19\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e5c9c6c95b7bd7f5\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524498');
INSERT INTO `p8_admin_log` VALUES ('3321','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'对抗死神的秘密武器——“魔肺”来了！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">9月12日，盐城市第一人民医院重症医学科独立开展了盐城地区首例ECMO（体外膜肺氧合）治疗技术，为救治重症患者争取了宝贵的时间和机会，开启了“重生”之门。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">ECMO是一门顶尖体外生命支持技术，它代表一个医院，甚至一个地区、一个国家的危重症急救水平。盐城市第一人民医院重症医学科于2019年4月引进ECMO设备，在副院长、重症医学科主任医师邓义军的带领下，由副主任医师陈建军等专家组建成立ECMO团队，团队成员进行多次的理论培训和模拟演练，确保安全有效地实施ECMO技术。救治过程中，ECMO功不可没，更离不开团队齐心协作、精心救治和日夜守护。该病例系我市首次独立开展应用ECMO技术救治呼吸衰竭患者，标志着盐城市一院乃至我市急诊急救医疗水平再上新台阶。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-13\',\n  \'timestamp\' => \'2019-09-13\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e1bc893d95c9b7ad\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524499');
INSERT INTO `p8_admin_log` VALUES ('3322','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'中瑞学术交流，智慧碰撞火花\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">8月27日--9月7日，瑞士圣加仑州医院Gerhard  Hildebrandt教授在盐城市第一人民医院开展为期10天的会诊病人、指导手术、教学查房、学术研讨等中瑞神经外科学术交流活动。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-08\',\n  \'timestamp\' => \'2019-09-08\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4681d2c78cff625c\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524499');
INSERT INTO `p8_admin_log` VALUES ('3323','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院发出“迎国庆展形象、做新时代文明人”倡议书\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">城市因文明而美丽,人民因城市而自豪。新的时代，需要更高层次的文明实践;创建文明城市，呼唤每个人的积极参与。为深入推进文明城市创建工作，不断提升广大职工文明服务意识和水平，展现新时代我院医护人员的职业形象，在中华人民共和国成立70周年华诞来临之际，盐城市第一人民医院全国青年文明号——门诊药房、全国巾帼文明岗——消毒供应中心向全院职工郑重发出倡议书，倡议全院职工加强理论学习，提升道德修养；遵守社会公德，树立文明形象；着装规范得体，言行文明礼貌；弘扬雷锋精神，营造文明氛围。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-02\',\n  \'timestamp\' => \'2019-09-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'12dd6fd7b04eed02\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524500');
INSERT INTO `p8_admin_log` VALUES ('3324','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'“GPS”精准定位，神经外科医生成功“拆弹”\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">近日，盐城市第一人民医院神经外科成功开展了全市首例神经导航下脑膜瘤切除术。市一院神经外科副主任郭俊博士等医务人员利用新引进的高端神经导航系统，成功切除了患者的脑部肿瘤。这种“精准治疗”新技术大大降低了对正常脑组织的干扰损伤，使手术更加微创化、精准化，标志着我市神经外科技术水平迈上新台阶。该技术改变了传统的开颅手术模式，实现了脑内尤其是脑深部病灶的准确定位，对于提高脑肿瘤的切除率，降低手术并发症与死亡率具有重要的临床价值。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-01\',\n  \'timestamp\' => \'2019-09-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'414247fed463ed34\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524500');
INSERT INTO `p8_admin_log` VALUES ('3325','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市医学会全科医学分会成立\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步推进盐城市全科医学的建设和全科医师的培养，改善基层医疗卫生服务体系，8月17日～18日，盐城市医学会全科医学会分会成立大会暨第一届全科医学学术会议隆重举办。此次活动由盐城市医学会主办，盐城市第一人民医院具体承办。会议邀请了江苏省医学会全科医学分会主任委员黄敏，江苏省人民医院老年医学科主任、全科医学科主任汤琪云，东南大学附属中大医院陈文姬教授，盐城市一院全科医学科李大林教授等著名专家出席会议并进行了精彩讲座，与工作在基层一线的全科医生们共同讨论、分享和交流。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">会上,盐城市医学会秘书长杨益国宣布成立全科分会, 盐城市第一人民医院党委书记张慧娟与江苏省医学会全科医学分会主任委员黄敏为盐城市医学会全科医学分会揭牌，盐城市第一人民医院党委委员、副院长柏宏坚与副院长邓义军为盐城市第一人民医院全科医学研究所揭牌。盐城市第一人民医院党委书记张慧娟、江苏省医学会全科医学分会主任委员黄敏、江苏省医学会全科医学分会主任委员黄敏分别致辞。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-09-01\',\n  \'timestamp\' => \'2019-09-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'6ddf2ec77ba98d16\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524501');
INSERT INTO `p8_admin_log` VALUES ('3326','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城首例单倍体造血干细胞移植在盐城一院获成功\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">近日，盐城市第一人民医院血液科首次成功采用单倍体造血干细胞移植技术救治一名ph阳性急性淋巴细胞白血病患者。患者于2019年8月21日顺利出舱，手术宣告成功。盐城首例单倍体造血干细胞移植成功，标志着我市治疗恶性血液病技术取得新的突破。患者的家人给市一院血液科、药学部、输血科、麻醉手术科的医务人员送上锦旗表示感谢。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-22\',\n  \'timestamp\' => \'2019-08-22\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'abf048736368bae3\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524501');
INSERT INTO `p8_admin_log` VALUES ('3327','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院召开2019年中国医师节庆祝大会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年8月19日是第二个中国医师节。为大力弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的崇高精神，不断激发广大医务工作者的职业荣誉感和使命感，全心全意为病员服务，积极投身医疗、教学、科研工作之中，8月19日下午，盐城市第一人民医院召开2019年中国医师节庆祝大会，表彰了一批在医学技术进步发展中取得优异成绩的先进集体和先进个人。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">离退休的老前辈、老专家受邀参会。盐城市第一人民医院党委副书记、副院长朱向军宣读表彰决定，老、中、青三代医生代表发言，全体参会医师重温《中国医师宣言》。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院院长查文章在致辞中指出，希望广大医师继续弘扬“敬佑生命、救死扶伤、甘于奉献、大爱无疆”的崇高精神，强质量、重创新，鼓干劲、谋突破，全面推动医院高质量发展。一要有强烈的求知欲，练就过硬的技术和本领；二要有创新意识，在创新中学习，在创新中思考，在创新中发现和解决问题；三要德术并举，打造附医特色品牌，体现附医内涵和精神；四要加强团队协作，利用团队之力，更好地为患者服务。任重而道远，作为附医人，要共同携手、凝心聚力，写好高质量发展的新篇章，为人民的美好生活、百姓的健康而不懈努力，以优异的成绩迎接新中国成立70周年！</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">党委书记张慧娟在讲话中强调，“中国医师节”的设立，这意味着莫大的荣誉和重大的责任，同时也对医师们提出了新要求，充满了新期待，激励大家更加尊敬自己的职业，更加自律、自省、自重，为人民提供更好的健康服务。希望广大医师不忘初心、牢记使命，做好医院改革的促进者；严谨求精、追求卓越，做好人民健康的守护者；立德修身、弘扬医德，做好职业操守的捍卫者；实干苦干，勇于担当，做好医院发展的建设者。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-20\',\n  \'timestamp\' => \'2019-08-20\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'72a7c372251e3d23\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524501');
INSERT INTO `p8_admin_log` VALUES ('3328','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'开展青年内镜医师培训，提升早癌发现率和病变精准诊断率\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为提升盐城地区早癌发现率和病变精准诊断率，进一步提高青年医师内镜技术水平，8月15日～16日，奥林巴斯-鼓楼医院青年内镜医师培训项目盐城站ESD第三期培训班在盐城市第一人民医院成功举办。南京鼓楼医院消化内科凌亭生教授、杨天博士等受邀，来院指导开展消化道早癌诊疗技术、病理诊断技术、ESD及其衍生技术等。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院消化内科主任杨红梅主持活动。南京鼓楼医院消化内科凌亭生教授、病理科孙琦、消化内科杨天博士、盐城市第一人民医院消化内科副主任吴旭东分别作了精彩的学术讲座。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">从传授知识要点，到手把手带教，凌亭生教授和杨天博士等专家对内镜诊断、ESD及衍生技术进行了指导和演示。从进镜的基本步骤，到观察要点，到注意寻找发白发红的部位，到开启NBI进行结构观察，每一步操作详细、精准、专业。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-17\',\n  \'timestamp\' => \'2019-08-17\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8ed3e80cb9f523dd\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524502');
INSERT INTO `p8_admin_log` VALUES ('3329','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'不辱使命，载誉而归！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年7月，由盐城市一院牵头组建的中国第14期援圭亚那医疗队圆满完成一年援外任务载誉而归，他们以高度的责任感和使命感，发扬救死扶伤的人道主义精神，为受援国人民提供了专业的医疗服务，赢得了当地政府的赞誉和人民的信任，为国家赢得了荣誉。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">医疗队在圭亚那工作期间，医疗队门诊接诊16400人次； 病房查房28665人次；手术3920人次； 抢救危重病人560例； 针灸1142人次；麻醉1218例；出具病理和影像报告11349例；开展新技术和新项目43项；他们的医疗技术、医疗服务被圭亚那国家电视台、《Kaieteur News》《Times》等多家主流媒体宣传报道20余次，被人民日报、央视、健康报等国家级媒体报道30余次，国内网络媒体、省、市级电视台及报刊等报道100余次 。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-06\',\n  \'timestamp\' => \'2019-08-06\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5435387b0cd74f3a\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524503');
INSERT INTO `p8_admin_log` VALUES ('3330','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院开展母乳喂养大型义诊活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">母乳是婴儿最理想、最安全的天然食物，也是大自然赐予人类的珍贵礼物。为保护、促进和支持母乳喂养，自1992年起，国际母乳喂养行动联盟(WABA)将每年8月1日-7日确定为世界母乳喂养周，旨在呼吁世界各国重视母乳喂养对母婴、对家庭、对社会的重要意义，以促进儿童的健康生存与发展。今年世界母乳喂养周主题是：“助力父母，成功母乳喂养”，强调以母亲和婴儿为核心，遵循生命最初1000天的时间轴，所有支持母乳喂养的医务人员及社会各界，需加强各环节协作，为母婴家庭传递一致信息并提供支持措施，帮助父母实现成功母乳喂养的愿望。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为了给新手爸妈们提供丰富的育儿知识，帮助准妈妈们掌握正确母乳喂养方式，促进社会和公众对母乳喂养重要性的正确认识和支持母乳喂养，8月5日上午，盐城市第一人民医院产科、营养科联合开展大型义诊活动，为孕产妇及家属宣传科普哺乳期乳房护理措施、指导孕期及哺乳期妇女膳食和不同阶段的婴幼儿科学喂养等知识。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-06\',\n  \'timestamp\' => \'2019-08-06\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'53a36ec578bc2c6f\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524504');
INSERT INTO `p8_admin_log` VALUES ('3331','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'市纪委书记王小红来院调研全面从严治党、党风廉政建设工作\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">8月1日下午，市委常委、市纪委书记、市监委主任王小红到盐城市第一人民医院调研全面从严治党、党风廉政建设工作情况，并主持召开座谈会。市纪委常委、秘书长张国锋等参加活动。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">座谈会前，王小红实地察看盐城市第一人民医院主院区门诊部、住院部等，了解近年来医院专科技术、人才培养、科研教学等情况以及主院区建设情况。盐城市卫生健康委员会主任徐杰介绍盐城市第一人民医院主院区二期工程建设情况，盐城市第一人民医院党委书记张慧娟介绍医院人才建设情况，盐城市第一人民医院院长查文章介绍国家标准化代谢性疾病管理中心盐城分中心——盐城市糖尿病防治中心，走访外科第一党支部党员活动室，调研基层党支部党建、党风廉政建设工作开展情况，观看盐城市第一人民医院《落实全面从严治党主体责任，切实提升医院党建工作水平》专题党建及党风廉政建设工作成果展示宣传展板。<br/> 座谈会上，市委常委、市纪委书记、市监委主任王小红听取有关部门和单位相关工作情况汇报并提工作要求。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-02\',\n  \'timestamp\' => \'2019-08-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'491435ecc6d34da6\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524505');
INSERT INTO `p8_admin_log` VALUES ('3332','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'洗消有术︱2019江苏消化内镜清洗消毒巡讲——盐城站圆满召开\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步加强医疗机构内镜清洗消毒工作，保障医疗质量和医疗安全，7月27日，“洗消有术”2019江苏消化内镜清洗消毒巡讲——盐城站在盐城市第一人民医院成功举办。盐城市第一人民医院护理部主任汤琪春、南京鼓楼医院消化内镜中心护士长李雯分别致辞。盐城市第一人民医院感染管理处主任谭思源、南京鼓楼医院消化内镜中心护士长李雯、南京医科大学第二附属医院消化医学中心护士长吴萍、江苏省人民医院内镜中心护士长丁静给学员们呈现了一场场知识盛宴，授课内容丰富、形式多样。精彩授课后，在南京鼓楼医院内镜中心蔡培、江苏省人民医院内镜中心张道权等讲师的带领下，学员们开展了规范流程操作演练，分享具有操作性的消化内镜清洗消毒经验。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-01\',\n  \'timestamp\' => \'2019-08-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'abb88ecdd0ead60f\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524505');
INSERT INTO `p8_admin_log` VALUES ('3333','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'开展深度合作，共建高水平教学科研基地！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-bottom: 0px; text-align: justify; background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">7月22日，盐城市党政代表团到南京大学考察，江苏省政协副主席、南京大学党委书记胡金波会见盐城市委书记戴源、市长曹路宝一行。盐城市副市长葛启发、盐城市卫生健康委员会主任徐杰、盐城市第一人民医院党委书记张慧娟、院长查文章、副院长邓义军等随行到南京大学考察，学习借鉴南京大学在医疗卫生领域的科研成果和先进经验，进一步深化南京大学医学院与盐城市第一人民医院的交流合作。作为教育部直属的重点综合性大学，南京大学在教学、科研和社会服务等各个领域保持良好的发展态势，各项办学指标和综合实力均位居全国高校前列。此次，盐城市第一人民医院随盐城市党政代表团到南大考察，双方将在学科建设、医疗技术、人才培养、科研交流和医院管理等方面开展全面合作。</span></p><p style=\"margin-bottom: 0px; text-align: justify; background: white; line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">会上，江苏省政协副主席、南京大学党委书记胡金波，盐城市委书记、市人大常委会主任戴源，盐城市委副书记、市长曹路宝分别作重要讲话。盐城市第一人民医院院长查文章汇报市一院教学科研的基本情况，南京大学科技处处长秦猛、南京大学医学院党委书记韩晓冬等介绍科研教学及与盐城市一院洽谈合作情况</span><span style=\"font-family: 宋体, SimSun; font-size: 13px; color: rgb(120, 124, 129); letter-spacing: 0px; background: white;\">。</span></span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-23\',\n  \'timestamp\' => \'2019-07-23\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'2fa26766d4811489\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524506');
INSERT INTO `p8_admin_log` VALUES ('3334','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院多学科综合门诊正式开诊！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为进一步规范肝癌患者的诊疗，为患者提供全方位、专业化、规范化、个体化的诊断和治疗，7月12日上午，盐城市第一人民医院肝癌多学科综合门诊（MDT）正式开诊，标志着市一院多学科诊疗工作向规范化、常态化迈出重要的一大步，4名患者在整个多学科诊疗服务过程中最大程度获益。肝癌多学科综合门诊依托三级甲等综合医院雄厚的技术实力和先进的医疗设备；依据国际国内最新的肝癌诊治指南和规范；整合普通外科、介入科、影像科、肿瘤科、感染科、消化内科、放疗科、病理科等多学科肝癌诊治专家；集手术、介入、放疗、化疗、中医药治疗、靶向治疗等多种手段；为肝癌患者制定最佳的治疗方案。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-13\',\n  \'timestamp\' => \'2019-07-13\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'83d366c5e7d170ea\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524507');
INSERT INTO `p8_admin_log` VALUES ('3335','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'喜讯！盐城市一院再获“全国巾帼文明岗”殊荣！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">7月11日下午，全国巾帼文明岗、江苏省巾帼文明岗授牌暨巾帼消费助力脱贫攻坚活动在盐城市第一人民医院举办。“全国巾帼文明岗”是全国妇联授予优秀职业妇女团体的最高荣誉称号。1997年启动创建至今，盐城市第一人民医院共创建成功全国巾帼文明岗2个，全市仅此一家。会议由盐城市卫生健康委员会党委委员、副主任陈旭东主持，盐城市第一人民医院党委书记张慧娟致辞，盐城市妇联主席盛艳给“全国巾帼文明岗”——盐城市第一人民医院消毒供应中心、“江苏省巾帼文明岗”——重症医学科二病区授牌，盐城市妇联主席盛艳讲话，盐城市第一人民医院消毒供应中心护士长陈艳代表发言，盐城市妇联副主席胡忠秀宣读“巾帼有情 爱心消费”倡议书。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">会上，盐城市妇联主席盛艳指出，积极创建巾帼文明岗，大カ培树先进典型，对提升医务人员职业光荣感、责任感，树立医疗卫生行业良好社会形象具有重要意义。一要不断丰富争创内涵。融岗位技能、业务特色、服务质量、社会效益为一体，引导女职工既立足本职、争创佳绩，又参与社会公益和志愿服务，打造具有鲜明特色的文明岗、示范岗、公益岗。二要不断放大示范效应。做优爱心服务，做实便民服务，切实把全国、省巾帼文明岗建成我市卫健系统文明服务的窗口和行业形象的样板。三要不断提升争创水平。加强党建带妇建，搭建联谊、交流、学习平台，保护好、引导好女职工的积极性、创造性，为她们更好地施展才华和展示才干创造机会，为办好人民满意的医院贡献巾帼力量、展示巾帼风采。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-12\',\n  \'timestamp\' => \'2019-07-12\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'89c8b63f9d7d1833\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524507');
INSERT INTO `p8_admin_log` VALUES ('3336','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'医联体签约暨合作共建推进会正式召开\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为贯彻落实《国务院办公厅关于推进医疗联合体建设和发展的指导意见》《省医改办关于进一步推进医疗联合体建设和发展的实施意见》《关于印发江苏省医疗联合体建设规划（试行）的通知》和市委、市政府要求，推进我市医联体规范有序建设与发展，7月5号上午，在市卫健委和响水县委的组织下，盐城市第一人民医院、响水县人民医院、响水县港城医院医联体签约暨合作共建推进会在响水县人民医院正式召开。盐城市第一人民医院查文章院长、嵇友林副院长、闻国华副院长、邓义军副院长及相关职能部门负责人、省级重点临床专科学科带头人代表参加了此次会议。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">会议得到了市卫健委、响水县委、县政府、响水卫健委各级领导的高度重视，市卫健委徐杰主任、响水县委崔爱国书记、响水县人民政府桑良举副县长等领导做了重要讲话，并对盐城市第一人民医院和响水县人民医院共建医联体，促进响水县人民医院及响水县医疗卫生事业全面进步及造福当地百姓等表示了良好祝愿及充分肯定。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">响水县人民医院王洪华院长表达了对各级领导和盐城市第一人民医院领导和专家的到来表示了热烈的欢迎，同时介绍了响水县人民医院及响水县港城医院的建设、发展情况，并对通过医联体共建，促进响水县院各项工作有力推进表达了强烈的企盼。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">盐城市第一人民医院查文章院长表达了对各级领导支持医联体工作的衷心感谢，也代表盐城市第一人民医院领导班子和广大员工对建设好医联体的信心和决心。查院长介绍了医联体建设的具体方案和措施，重点提出了技术、质量、安全要达到两个医院间完全同质化管理的要求，并表示要通过专科共建、成立名医工作室、成立专科联盟、派驻医务人员等多种方式，达到提升响水县院综合服务能力的最终目标。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-06\',\n  \'timestamp\' => \'2019-07-06\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'50d6f8572b0f7736\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524507');
INSERT INTO `p8_admin_log` VALUES ('3337','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'坚持党建引领，推进医院发展！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6月以来，盐城市第一人民医院按照市委统一部署，扎实开展系列特色党建活动，切实为即将开展的“不忘初心、牢记使命”主题教育夯实基础。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展特色党日活动。6月12日，盐城市第一人民医院机关第二党支部在新四军重建军部旧址——泰山庙开展党日活动，重温抗战时期的重大历史事件，在党旗前庄重宣誓，回顾入党初心，并为5名党员过政治生日。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展党建目标责任制考核。6月下旬，由盐城市第一人民医院组织处牵头成立党建工作考核小组，对全院11个基层党支部2018年度党建工作进行综合检查考核，严格奖惩，充分体现党要管党、从严治党的要求。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">开展意识形态工作专题学习。6月19日下午，盐城市第一人民医院召开党委理论学习中心组学习（扩大）会议，院领导班子成员、党员代表共计300余人接受了意识形态专题教育。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">举办“不忘初心、牢记使命”主题教育培训班。6月20日，41名党员干部赴革命圣地河北省平山县，在西柏坡、白求恩柯棣华纪念馆、正定古城等地，通过两天的现场教学，完成了一次润泽心灵的红色之旅，感悟革命精神，缅怀革命先烈，传承红色基因。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">隆重集会纪念建党98周年。7月1日，盐城市一院召开纪念中国共产党成立98周年暨“七一”表彰大会。重温党的光辉历程，表彰先进典型，激励全院各级党组织和广大党员不忘初心、牢记使命，勇担推动医院高质量发展走在苏北苏中前列的历史使命。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体; font-size: 20px;\">今年以来，盐城市第一人民医院党委全面贯彻落实习近平新时代中国特色社会主义思想和党的十九大精神，坚持以人民健康为中心，强化党建引领，切实将党建工作与医院改革发展同谋划、共部署、齐推进，按照市委要求，充分发挥基层党支部战斗堡垒作用和党员先锋模范带头作用，以坚强的党性、务实的作风、精湛的技术和优质的服务为全市人民的健康保驾护航，为推动医院高质量发展走在苏北、苏中前列不懈奋斗，努力用优异的业绩向新中国建国七十周年献上一份厚礼。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-02\',\n  \'timestamp\' => \'2019-07-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'482fbd479e02e178\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524508');
INSERT INTO `p8_admin_log` VALUES ('3338','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院召开纪念建党98周年暨“七一”表彰大会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">98载风雨征程，98载沧桑巨变。值此中国共产党建党98周年华诞，7月1日下午，盐城市第一人民医院召开纪念中国共产党成立九十八周年暨“七一”表彰大会。会上，盐城市一院党委副书记、院长查文章宣读国家、省、市级先进集体及先进个人名单; 盐城市一院党委副书记、副院长朱向军宣读《关于表彰院级先进党支部、优秀党务工作者和优秀共产党员的决定》; 为先进基层党支部、优秀共产党员、优秀党务工作者等颁奖; 优秀党员代表、先进集体发言。会议最后，盐城市一院党委书记张慧娟提工作要求：一要守初心，担使命，在主题教育活动中走在前；二要强堡垒，当先锋，在医院高质量发展中作表率；三要找差距，抓落实，在迎评和完成全年目标任务上冲在先。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-07-02\',\n  \'timestamp\' => \'2019-07-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d515ca26ef719310\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524509');
INSERT INTO `p8_admin_log` VALUES ('3339','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'我市首个疼痛专科紧密型医联体正式成立！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为加快盐城市疼痛学科的发展，提高各级医院疼痛科的科室建设和医疗技术水平，6月28日上午，盐城市第一人民医院疼痛专科紧密型医联体暨盐城市第一人民医院疼痛专科紧密型医联体亭湖区新洋疼痛诊疗服务中心揭牌成立！</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">会上，中华医学会疼痛学分会主任委员刘延青教授致辞，盐城市第一人民医院院长查文章教授、盐城市亭湖区政府副区长张洪国、盐城市卫健委基层处处长滕平、盐城市医学会副秘书长朱加祥讲话。张洪国副区长和查文章院长共同为盐城市第一人民医院疼痛专科紧密型医联体亭湖区新洋疼痛诊疗服务中心揭牌。盐城市第一人民医院疼痛科主任医师薛祥云和盐城市亭湖区新洋社区卫生服务中心院长吉联军签订医联体合作协议。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-29\',\n  \'timestamp\' => \'2019-06-29\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'674fdf642066c232\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524510');
INSERT INTO `p8_admin_log` VALUES ('3340','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'2019年盐城地区神经外科学术沙龙活动成功举办！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为搭建神经外科的交流平台，研讨与分享神经外科的治疗心得与手术技巧，促进盐城地区神经外科领域不断发展，6月28日下午，由江苏省临床重点专科——盐城市第一人民医院神经外科主办的2019年盐城地区神经外科学术沙龙会议隆重召开。会议多角度探讨神经学科“脑肿瘤、脑血管、功能神经疾病”三个专题的发展方向，多名神经外科学专家分享了临床经验及科研方面的最新突破。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-29\',\n  \'timestamp\' => \'2019-06-29\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1533a8701c64c7cd\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524511');
INSERT INTO `p8_admin_log` VALUES ('3341','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'国家级“青年文明号”新鲜出炉！盐城市一院门诊药房光荣上榜！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 47px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">日前，共青团中央、最高人民法院、发展改革委等21家全国创建“青年文明号”活动组委会成员单位联合印发《关于命名2017-2018年度全国青年文明号的决定》（中青联发【2019】5号）, 命名在2017—2018 年度全国青年文明号创建周期中, 符合创建条件且表现突出的创建集体为“2017—2018 年度全国青年文明号”，盐城市第一人民医院门诊药房上榜！</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-28\',\n  \'timestamp\' => \'2019-06-28\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'d912f9c3cf5af268\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524511');
INSERT INTO `p8_admin_log` VALUES ('3342','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院开展青年医务人员廉洁从业教育主题活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为推动省、市卫健委“自省自警、拒腐防变”警示教育向深走实，结合医院“5·10”警示教育系列活动的有关要求，促进青年医务人员增强廉洁自律意识，牢固树立“不想腐”的思想自觉，强化青年医务人员“知敬畏、严纪律、尽职责”的廉洁从业观念，6月5日，盐城市一院召开青年医务人员廉洁从业教育专题会议，医院500余名青年医务人员参加了会议。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">会前，全体参会人员在“不忘初心、牢记使命、廉洁行医、优质服务”为主题的承诺签名墙上郑重签名，彰显了医院青年医务工作者为人民健康服务的责任与担当。会上，与会人员观看了2018年度国家“最美医生”先进事迹宣传片、医疗卫生领域系列腐败案件《见利忘义 泯灭良心》警示教育片等。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px; text-indent: 37px;\">盐城市一院纪委书记卞学梅提工作要求：青年医务人员要加强品德修为，争做人民满意的好医生。一是志存高远，砥励前行，为人民的健康卫生事业建功立业；二是不忘初心，牢记使命，始终把人民的利益放在第一位；三是追求进步，政治坚定，在复杂的环境中不迷失方向；四是严以律己，遵纪守法，做一个清清白白、堂堂正正的好人。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-06\',\n  \'timestamp\' => \'2019-06-06\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'b1f21eb682dc6965\',\n  \'views\' => \'[db:浏览量]\',\n  \'veri