INSERT INTO `p8_admin_log` VALUES ('3343','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市一院举办“爱在一院 快乐成长”少儿才艺展示活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">为了庆祝“六一”国际儿童节，关爱医院职工子女成长，搭建才艺展示平台，6月2日，盐城市第一人民医院举办了“爱在一院 快乐成长”少儿才艺展示活动。本次活动共征集19幅书画作品，15个现场才艺展示节目。舞蹈、歌曲、乐器演奏、跆拳道表演等形式多样，丰富多彩。医院关工委驻会主任于洪蔚，市卫健委妇联主席、团委书记张健羽，为小朋友送去了节日的祝贺和殷切的希望。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-03\',\n  \'timestamp\' => \'2019-06-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'53a146006f23908c\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524512');
INSERT INTO `p8_admin_log` VALUES ('3344','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'盐城市第一人民医院举办阿尔茨海默病大型义诊活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6月1日上午，盐城市第一人民医院在南区举办了阿尔茨海默病大型义诊活动。此次义诊活动以“康复服务，健康中国”为主旨，由中国康复医学会举办，盐城市康复医学会、盐城市一院康复医学科具体承办。活动旨在筛查和早期发现轻度认知障碍患者，并通过专业视角给予患者及家属诊治意见，解答疑惑，向就诊患者及家属讲解居家照护常识、沟通技巧、认知训练方法等，进一步提高广大群众对阿尔茨海默病的认识和了解，为预防疾病打下良好的基础。</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-06-02\',\n  \'timestamp\' => \'2019-06-02\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'862ecbcdd69e7888\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524512');
INSERT INTO `p8_admin_log` VALUES ('3345','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'加强中韩医疗交流合作，打造国际化诊疗技术平台\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为发展盐城市第一人民医院与韩国延世大学Severance医院之间的学术研究以及职员之间的交流合作，5月26日，盐城市第一人民医院与韩国延世大学Severance医院签署了友好姐妹医院合作协议（MOU）。市委书记、市人大常委会主任戴源，市卫健委主任徐杰等参加了签约仪式。市委书记、市人大常委会主任戴源提出要求：希望双方进一步增进了解互信，在人员、技术等方面开展深层次合作。要按照协议要求，加快项目推进力度，为盐城健康医疗事业注入新的活力，不断提升我市健康医疗事业发展水平。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-27\',\n  \'timestamp\' => \'2019-05-27\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5191d8f4b2f1dc13\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524513');
INSERT INTO `p8_admin_log` VALUES ('3346','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'搭建科研新平台，勇攀医学新高峰！\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'宣传处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">为搭建科研技术平台，不断提高临床科研水平，增强自主创新能力和核心竞争力，5月21日下午，我市卫生系统首个科研类专家团队工作室——“南通大学生命科学院刘东专家团队工作室”在盐城市第一人民医院揭牌成立。盐城市一院将充分整合中心实验室、转化医学中心、血液病实验室等，搭建科研技术平台，聘请刘东教授作为实验室建设和临床科研工作首席顾问，将进一步提升医院特色专科、重点学科的科研水平，切实为广大群众提供更加优质的医疗服务。</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-05-22\',\n  \'timestamp\' => \'2019-05-22\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'b1165e3d1234d190\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627524513');
