INSERT INTO `p8_admin_log` VALUES ('3494','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部开展党员支部大会\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'离退休干部处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年10月21日，市一院老干部党支部召开支部党员大会，组织离退休党员同志认真学习了以下内容：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.《习近平新时代中国特色社会主义思想学习纲要》；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.《习近平关于“不忘初心、牢记使命”论述摘编》；</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.习近平在庆祝中华人民共和国成立70周年大会上的讲话。</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/e366c9f5ae776cec.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/c7579cdc0447b819.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2019年10月21日下午，市一院老干部党支部开展了党组织统一活动日活动，支部书记为党员们上了专题党课、组织离退休党员开展“四重四亮”活动。</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/2869975c3cd31623.png\" border=\"0\"/></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">上图为支部书记朱临梅正在为离退休党员同志上党课</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">下图为离退休党员李明在重读入党志愿</span></p><p style=\"text-indent: 2em; line-height: 3em; text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/7bed8c0ab0dfb97b.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党员候玉珍同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/37ba57e88444c2ed.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党员支部委员王仁英同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/9e1b150a33c24999.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党员支部委员周正旺同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/4dccd51baf18d5b7.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党员支部委员杭荣辉同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/3717f5047052290a.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党支部支部委员李祥同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/7b8fb022d2da3e96.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">离退休党员范长志同志在重读入党志愿</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/6c049a0103e7fe63.png\" border=\"0\"/></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">支部书记带领离退休党员一起重温入党誓词</span></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/93302b104d3c7b75.png\" border=\"0\"/></p><p style=\"text-align: center;\"><img src=\"http://www1.ycsyy.com/upload/images/2019/11/c83740034c9bffef.png\" border=\"0\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-10-21\',\n  \'timestamp\' => \'2019-10-21\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'a1baa1289127d5a8\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1627528372');
INSERT INTO `p8_admin_log` VALUES ('3495','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'895\',\n  \'title\' => \'老干部支部召开“我看新中国成立70周年新成就”专题研讨交流活动\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'离退休干部处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为迎接新中国成立70周年，激发离退休干部爱党爱国热情，我院老干部支部于2019年8月1日召开了“我看新中国成立70周年新成就”专题研讨交流活动。作为共和国成长的参与者、见证者，老同志们共同回忆新中国崛起历程，心潮澎湃，感慨万千，言谈话语充满正能量。</span></p><p><img src=\"http://www1.ycsyy.com/upload/images/2019/11/6c46b0e5503f39bb.png\" title=\"image.png\" alt=\"image.png\" width=\"371\" height=\"261\" style=\"width: 371px; height: 261px;\"/><img src=\"http://www1.ycsyy.com/upload/images/2019/11/5c225232b8a13b46.png\" title=\"image.png\" alt=\"image.png\" width=\"286\" height=\"261\" style=\"width: 286px; height: 261px;\"/><img src=\"http://www1.ycsyy.com/upload/images/2019/11/8ef888763bd1608c.png\" title=\"image.png\" alt=\"image.png\" width=\"445\" height=\"260\" style=\"width: 445px; height: 260px;\"/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2019-08-01\',\n  \'timestamp\' => \'2019-08-01\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f179ec7c2cbbc1a8\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => 