INSERT INTO `p8_admin_log` VALUES ('3506','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于举办庆祝中国共产党成立100周年征文比赛的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'离退休干部处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 32px; text-align: left;\"><span style=\"font-family: 宋体; font-size: 19px;\">各位离退休老同志：</span><br/></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">接省委、市委老干部局、离退休干部工委的通知精神，为庆祝中国共产党成立</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family:宋体\">周年，引导离退休干部讲好党的故事，传承红色基因，弘扬革命精神，特面向我院全体离退休老同志组织开展庆祝中国共产党成立</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family:宋体\">周年征文比赛。现将有关事项通知如下：</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">一、征文主题：围绕庆祝建党</span><span style=\"font-size:19px\">100</span><span style=\"font-size:19px;font-family: 宋体\">周年，结合亲身经历和所学所思，讲述在党的领导下投身革命、建设、改革的历史瞬间、感人故事和切身体会，讴歌和反映我们党的辉煌成就和宝贵经验，特别是展现新时代在以习近平同志为核心的党中央的坚强领导下，党和国家各项事业取得的历史性成就、发生的历史性变化，我市推进“四新盐城”建设取得的显著成绩，大力弘扬党的光荣传统和优良作风，唱响共产党好、社会主义好、改革开放好、伟大祖国好、各族人民好的主旋律。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">二、征文时间：从通知之日起到</span><span style=\"font-size:19px\">2021</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family: 宋体\">月</span><span style=\"font-size: 19px\">22</span><span style=\"font-size:19px;font-family:宋体\">日为止。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">三、征文体裁：回忆录、散文、纪实文学、学习心得均可，字数一般不超过</span><span style=\"font-size:19px\">3000</span><span style=\"font-size:19px;font-family:宋体\">字。要求内容真实、语言流畅、情感真挚，须是原创作品。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">四、投稿邮箱：</span><span style=\"font-size:19px\">654151792@qq.com</span><span style=\"font-size:19px;font-family:宋体\">，邮件主题命名为“征文比赛</span><span style=\"font-size:19px\">+</span><span style=\"font-size:19px;font-family: 宋体\">作品题目</span><span style=\"font-size: 19px\">+</span><span style=\"font-size:19px;font-family:宋体\">作者姓名”。</span></p><p style=\"text-indent:37px;line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">五、奖项设置：一等奖</span><span style=\"font-size:19px\">1</span><span style=\"font-size:19px;font-family: 宋体\">名，二等奖</span><span style=\"font-size:19px\">2</span><span style=\"font-size:19px;font-family: 宋体\">名，三等奖</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family: 宋体\">名，优秀奖若干名，对获奖者将发放一定的奖品。</span></p><p style=\"line-height:32px\"><span style=\"font-size:19px;font-family:宋体\">（本次活动的解释权属于医院离退休干部处）</span></p><p style=\"line-height: 32px; text-align: right;\"><span style=\"font-size:19px\"> </span></p><p style=\"text-indent: 271px; line-height: 32px; text-align: right;\"><span style=\"font-size:19px;font-family:宋体\">盐城市第一人民医院离退休干部处</span></p><p style=\"line-height: 32px; text-align: right;\"><span style=\"font-size:19px\">  2021</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family:宋体\">月</span><span style=\"font-size:19px\">23</span><span style=\"font-size:19px;font-family: 宋体\">日</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-04-23\',\n  \'timestamp\' => \'2021-04-23\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'a150df85c46cdaa5\',\n  \'views\' => \'[db:浏览量]\',\n  \'verifier\' => \'admin5\',\n)','1627528526');
INSERT INTO `p8_admin_log` VALUES ('3507','2','','','','0','0','','admin5','添加内容','/admin.php/cms/item-add','192.168.162.110','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'896\',\n  \'title\' => \'关于离退休职工年度体检的通知\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'离退休干部处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p><span style=\"font-family: 宋体; font-size: 18px;\"></span></p><p><span style=\"font-size: 18px;\"><br/></span></p><p style=\"text-indent: 0em; text-align: left;\"><span style=\"font-size: 18px;\">各位离退休老同志：</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 18px;\">大家好！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 18px;\">经医院统一安排定于2021年4月1日至2021年4月6日（7：00-9：00）为全院离退休职工健康体检。请大家在规定的时间内携带本人身份证直接到体检中心参加体检。本次体检仅限本人，不得转让！体检结束后，务必将体检指引单留交体检中心，否则无法获取体检结果。体检报告在规定体检时间结束一周后，到院老年活动中心领取！请看到通知的老同志相互转告！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size: 19px; font-family: 宋体;\"><br/></span></p><p style=\"text-indent:280px\"><span style=\"font-size: 18px;\"></span></p><p style=\"text-indent: 280px; text-align: right;\"><span style=\"font-family: 宋体; font-size: 18px;\">离退休干部处</span></p><p style=\"text-indent: 299px; text-align: right;\"><span style=\"font-size: 18px;\">2021.3.17</span></p><p><br/></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'editer\' => \'[db:编辑]\',\n  \'list_order\' => \'2021-03-17\',\n  \'timestamp\' => \'2021-03-17\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-ad