INSERT INTO `p8_admin_log` VALUES ('5241','0','cms','item','add','0','879','','宣传处','添加内容 | 盐城市一院圆满完成民兵训练考核工作','/index.php/cms/item-list-category-879-page-2','172.16.100.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'879\',\n  \'title\' => \'盐城市一院圆满完成民兵训练考核工作\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:29.4pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312;color:black;letter-spacing:.35pt;background:white\">为加强基层民兵队伍建设，提高民兵队伍应对自然灾害、处置突发事件的反应能力，<span>11</span>月<span>30</span>日，<span>2021</span>年基干民兵训练考核工作正式拉开帷幕。盐城市第一人民医院<span>14</span>名骨干医护人员组成市直医疗救护排，全程参与此次民兵训练及综合考核。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\';text-indent:29.4pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312;color:black;letter-spacing:.35pt;background:white\">此次训练共<span>4</span>天，针对考核内容进行集中强化训练，主要训练装卸载、机动、共同基础（射击、手榴弹投掷、卫生与救护、戴防毒面具、队列）、装备操作（北斗手持机操作、工程机械操作）和分队任务行动（预编装备操作、医疗保障开设、伪装防护行动、饮食保障行动）。<span>12</span>月<span>3</span>日由省委组织部、省退役军人事务厅、和省军区战备建设局、动员局、整治工作局、保障局共<span>12</span>人组成的考核组对此次训练进行综合考核。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\\\'Calibri\\\',\\\'sans-serif\\\'\"><span style=\"font-size:14.0pt;font-f