INSERT INTO `p8_admin_log` VALUES ('5426','0','core','','login','0','859','','','登录后台 | 盐城市第一人民医院北院区5号楼消防报警及联动控制系统招标公告','//admin.php/core-login','172.16.100.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北院区5号楼消防报警及联动控制系统招标公告\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt;\">盐城市第一人民医院北院区5号楼消防报警及联动控制系统</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">已具备招标条件，现通过公开招标方式邀请潜在投标人参加本项目的投标。现就有关事项公告如下：</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">一、项目概况：</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">1</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、项目名称：</span><span style=\"font-size: 14pt;\">盐城市第一人民医院北院区5号楼消防报警及联动控制系统</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">项目。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">2</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、项目地点：盐城市毓龙西路</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">166</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">号。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">3</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、项目规模：工程造价约</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">29.8</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">万元。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">4</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、招标范围：</span><span style=\"font-size: 14pt;\">盐城市第一人民医院北院区5号楼消防报警及联动控制系统</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">项目，</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">具体内容详见招标人提供的设计图纸和工程量清单</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">5</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、工期：</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">30</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">日历天</span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">，质保期：不少于</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">2</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">年</span></span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">6</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、质量要求：</span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">符合国家</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">“</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">合格</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">”</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">标准，并通过消防验收合格</span></span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">二、投标人须具备以下条件：</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">1</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、投标人资格要求：</span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">投标人须是具有建设主管部门核发的消防设施工程专业承包二级及以上资质的独立法人，并具有有效的安全生产许可证</span></span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">2</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、</span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">项目负责人须同时具有机电工程专业二级及以上注册建造师资格证书和有效的安全生产考核合格证</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">(B</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">类</span></span><span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">)</span></span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">3</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、投标人须确保授权委托人和项目负责人为本单位的正式职工，并确保从投标截止日当月向前连续</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">6</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">个月均在本单位缴纳养老保险</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">(</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">例：如开标日期为</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">2022</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">年</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">1</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">月</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">**</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">日，则投标单位需保证以上人员</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">2021</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">年</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">7</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">月</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">—12</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">月养老保险均在本单位缴纳。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">4</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、财务要求：投标人应具有独立订立合同的能力，未处于财产被接管、冻结和破产状态。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">5</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">、本次招标不接受联合体投标。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">三、投标保证金：本项目投标保证金数额为人民币</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">5000</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">元整，具体详见招标文件。</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">四、投标报名：</span></p>\r\n<span style=\"font-size: 14pt;\">\r\n</span>\r\n<p style=\"margin: 0cm 0cm 0.0001pt; text-align: justify; font-size: 10.5pt; text-indent: 24pt; line-height: 1.5;\"><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">请符合上述资质条件的投标人携带单位介绍信</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">(</span><span style=\"font-size: 14pt; line-height: 1.5; color: black;\">或授权委托书，须注明联系人、电话及邮箱</span><span style=