INSERT INTO `p8_cms_item_article_addon` VALUES ('2404','3227','1','','/ewebeditor/uploadfile/image/2022/03/18/20220318102550799_s.jpg','3月9日上午，农工党盐城市一院基层委员会组织神经内科、心血管内科、内分泌科、感染科、急诊科等专家党员赴中建三局盐城项目部开展“学雷锋”宣教义诊活动。活动现场，专家党员发挥专业特长，积极与前来就诊的患者沟通，详细了解他们的健康情况，提出合理的治疗方案及建','172.16.100.2','172.16.100.2','1647570423','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;font-family:仿宋,仿宋_GB2312\">3</span><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">月<span>9</span>日上午，农工党盐城市一院基层委员会组织神经内科、心血管内科、内分泌科、感染科、急诊科等专家党员赴中建三局盐城项目部开展“学雷锋”宣教义诊活动。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">活动现场，专家党员发挥专业特长，积极与前来就诊的患者沟通，详细了解他们的健康情况，提出合理的治疗方案及建议，并耐心细致地为他们讲解疾病防控要点。期间，专家党员们还结合实例及课件，就“外伤应急处理”“<span>CPR</span>”等主题为项目安全管理人员及现场人员作了实操讲座。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Calibri\',\'sans-serif\';text-indent:28.0pt\"><span style=\"font-size:14.0pt;\r\nfont-family:仿宋,仿宋_GB2312\">市一院基层委员会主委王军表示，此次义诊宣教活动是为弘扬雷锋精神，为广大患者提供便民服务而开展的一次活动，得到了患者及现场工作人员的一致认可，今后基层委将进一步加强义诊、宣教活动力度，努力为提升基层医疗水平添砖加瓦。</span></p>\r\n<a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102550799.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102550799_s.jpg\" border=\"0\" style=\"width: 424px; height: 377px;\">&ensp;&ensp;&ensp;&ensp;</a><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102602938.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102602938_s.jpg\" border=\"0\" style=\"width: 490px; height: 378px;\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</a>\r\n<div><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102602938.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</a><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102610276.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102610276_s.jpg\" border=\"0\" style=\"width: 425px; height: 399px;\">&ensp;&ensp;&ensp;&ensp;</a><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102619639.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102619639_s.jpg\" border=\"0\" style=\"width: 487px; height: 397px;\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;</a></div>\r\n<div><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102641133.jpg\" target=\"_blank\">&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;&ensp;<img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102641133_s.jpg\" border=\"0\" style=\"width: 426px; height: 392px;\">&ensp;&ensp;&ensp;&ensp;</a><a href=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102650613.jpg\" target=\"_blank\"><img src=\"/ewebeditor/uploadfile/image/2022/03/18/20220318102650613_s.jpg\" border=\"0\" style=\"width: 487px; height: 394px;\"></a></div>','','','','','');
INSERT INTO `p8_cms_item_article_addon` VALUES ('2405','3228','1','','/ewebeditor/sysimage/icon16/doc.gif','为进一步规范、高效做好政府采购工作，提高我院招标质量，依据《中华人民共和国招标投标法》、《中华人民共和国政府采购法》等相关管理规定，拟对盐城市第一人民医院招标代理公司进行入围遴选，采取公开比选的方式择优选择专业招标代理公司，建立我院采购代理机构库。一','172.16.100.2','172.16.100.2','1647829319','<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';margin-top:15.6pt;text-indent:24.0pt;line-height:\r\n24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">为进一步规范、高效做好政府采购工作，提高我院招标质量，依据《中华人民共和国招标投标法》、《中华人民共和国政府采购法》等相关管理规定，拟对盐城市第一人民医院招标代理公司进行入围遴选，采取公开比选的方式择优选择专业招标代理公司，建立我院采购代理机构库。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';margin-top:15.6pt;text-indent:24.0pt;line-height:\r\n24.8pt\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">一、项目概况及总体要求</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">1.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">项目名称：盐城市第一人民医院招标代理服务入围遴选。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">2.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">项目内容：盐城市第一人民医院工程、服务、货物招标代理（不含医疗设备），本次遴选入围代理商数量<span>3</span>家（其中盐城市区有固定办公场所和组织招标必备的场地及设备设施的≥<span>1</span>家）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">3.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">服务时间：三年。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">二、投标单位资格条件</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">投标单位除应具备《中华人民共和国政府采购法》第二十二条规定的条件外，还必须同时具备以下资格：</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">1.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">具有独立法人资格，有独立承担民事责任的能力，具有相应经营范围；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">2.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">具有良好的商业信誉和健全的财务会计制度，具有专业的开标、评标场所，具有与本项目招标代理相适应的技术专家，能确保招标工作规范进行；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">3.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">有依法缴纳税收和社会保障资金的良好记录；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">4.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">参加采购活动前三年内，在经营活动中没有重大违法记录；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">5.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">在江苏省公共资源交易平台信用信息中无不良记录；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">6.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">在江苏政府采购网完成政府采购代理机构网上登记（提供网页截图）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">7.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">代理商提供拥有编制磋商文件和组织评标的相应专业能力的情况说明或证明文件，包含但不限于：</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">(1)</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">承诺为盐城市第一人民医院委托的招标代理项目指定专人负责；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">(2)</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">项目负责人应具有一级</span><span style=\"font-size:12.0pt;font-family:宋体\">注册造价师<span style=\"color:black\">资质；</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">(3)</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">提供为上述团队成员缴纳的近一个月的社会保险缴费清单，加盖社保中心公</span><span style=\"font-size:12.0pt;font-family:宋体\">章或电子签章（可使用互联网版本）；</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体\">(4)</span><span style=\"font-size:12.0pt;font-family:宋体\">代理商须提供法定代表人授权书原件、法定代表人身份证复印件、授权代表身份证复印件（身份证原件备查）；提供为授权代表人缴纳的近一个月的社会保险缴费清单材料，加盖社保中心公章或电子签章（可使用互联网版本）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体\">8.</span><span style=\"font-size:12.0pt;font-family:宋体\">代理商曾以不正当手段骗取中标，被建设行<span style=\"color:black\">政主管部门或司法部门查处的，将被拒绝参与本项目投标。</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">9.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">代理商应具有的场地和设备设施要求（提供相应的场地所有权或使用权证明材料，开评标场所（含设备设施）的照片）。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">10.</span><span style=\"font-size:12.0pt;font-family:宋体;color:black\">本项目<b>不接受</b>联合体投标。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">三、报名时间</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体\">自<span>2022</span>年<span>3</span>月<span>21</span>日至<span>2022</span>年<span>3</span>月<span>26</span>日止，工作日期间每天<span>9</span>：<span>30</span>——<span>11</span>：<span>30</span>，<span>14</span>：<span>00</span>—<span>17</span>：<span>00</span><span style=\"color:black\">，逾期不再接受报名。</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">四、报名资料</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.8pt\" align=\"left\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">见第四部分附件与遴选文件格式。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-align:left;text-indent:24.0pt;\r\nline-height:24.8pt\" align=\"left\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">五、报名地点</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">盐城市第一人民医院（主院区）人民南路<span>66</span>号，门诊楼四楼招标采购中心</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:black\">联系人：徐老师<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>联系方式：<span>0515-66696855</span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><b style=\"font-family: \" times=\"\" new=\"\"><span style=\"font-size: 12pt; font-family: 宋体;\">六、投标证金</span></b></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt;line-height:24.8pt\"><span style=\"font-size:12.0pt;font-family:宋体\">本项目招标人要求投标人提交投标保证金金额为人民币<span>1000</span>元整。具体缴纳方式详见招标文件。</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:10.0pt;line-height:1.5;font-size:12.0pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt\"><span>&nbsp;</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:10.0pt;line-height:1.5;font-size:12.0pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt\"><span><br>\r\n</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;text-indent:10.0pt;line-height:1.5;font-size:12.0pt;font-family:\'Times New Roman\',\'serif\';text-indent:24.0pt\"><span>附件：<img border=\"0\" src=\"/ewebeditor/sysimage/icon16/doc.gif\"><a href=\"/ewebeditor/uploadfile/file/2022/03/21/20220321105956041.docx\" target=\"_blank\">盐城市第一人民医院招标代理服务项目遴选文件.docx</a></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';margin-top:78.0pt;text-indent:266.0pt;line-height:\r\n23.0pt\"><span style=\"font-size:14.0pt;font-family:宋体\">盐城市第一人民医院</span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt;text-align:justify;text-justify:inter-ideograph;font-size:10.5pt;font-family:\'Times New Roman\',\'serif\';text-indent:280.0pt;line-height:23.0pt\"><span style=\"font-size:14.0pt;font-family:宋体\">2022</span><span style=\"font-size:14.0pt;font-family:宋体\">年<span>3</span>月<span>21</span>日</span></p>','','','','','');
INSERT INTO `p8_cms_item_article_addon` VALUES ('2406','3229','1','','/ewebeditor/sysimage/icon16/xls.gif','各经营企业：根据我院实际工作的需要，经医院领导研究同意，拟为新生儿科婴儿奶粉进行比选，欢迎经营企业前来参与。项目名称：1. 婴儿乳蛋白部分水解配方奶粉（抗过敏配方）2. 早产/低出生体重婴儿配方奶粉3. 婴儿配方奶粉(0-6月）有意愿参与本次比选经营企业请于2022年','172.16.100.2','172.16.100.2','1648451616','<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin:0cm;margin-bottom:.0001pt;line-height:30.0pt\"><span style=\"font-family:宋体;color:#333333\">各经营企业：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:0cm;margin-bottom:10.0pt;margin-left:0cm;layout-grid-mode:char;font-size:11.0pt;font-family:\'Tahoma\',\'sans-serif\';text-indent:24.0pt;line-height:30.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:#333333\">根据我院实际工作的需要，经医院领导研究同意，拟为新生儿科婴儿奶粉进行比选，欢迎经营企业前来参与。</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:0cm;margin-bottom:10.0pt;margin-left:0cm;layout-grid-mode:char;font-size:11.0pt;font-family:\'Tahoma\',\'sans-serif\';text-indent:24.0pt;line-height:30.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:#333333\">项目名称：</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:0cm;margin-bottom:10.0pt;margin-left:0cm;layout-grid-mode:char;font-size:11.0pt;font-family:\'Tahoma\',\'sans-serif\';margin-left:0cm;text-indent:24.0pt;line-height:30.0pt\"><span style=\"font-size:12.0pt;font-family:宋体;color:#333333\">1.<span style=\"font:7.0pt \'Times New Roman\'\">&nbsp; </span></span><span style=\"font-size:12.0pt;font-family:宋体;color:#333333\">婴儿乳蛋白部分水解配方奶粉（抗过敏配方）</span></p>\r\n<p style=\"margin-right:0cm;margin-left:0cm;font-size:12.0pt;font-family:\'Calibri\',\'sans-serif\';margin-top:0cm;margin-right:0cm;margin-bottom:10.0pt;margin-left:0cm;layout-grid-m