INSERT INTO `p8_admin_log` VALUES ('5576','0','cms','item','update','2027','939','','宣传处','修改内容 | 陈小平','/index.php/cms/item-list-category-939-page-1','172.16.100.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=2027&verified=1\',\n  \'cid\' => \'939\',\n  \'title\' => \'陈小平\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2021-07-13 17:29:49\',\n  \'sub_title\' => \'主任医师\',\n  \'source\' => \'\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'http://www.ycsyy.com/attachment/cms/item/2022_04/04_10/90d45866af32b0fc.jpg.thumb.jpg\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'女，医学博士，主任医师，硕士研究生导师，盐城市第一人民医院妇产科副主任。\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p></p>\r\n妇产科学博士，盐城市第一人民医院妇产科学科带头人，妇产科副主任，研究生导师，主任医师。江苏省333工程高层次培养对象兼考核优秀人才，江苏省医学会妇产科分会委员，江苏省医学会妇科内镜委员会委员，盐城市135工程医学重点人才，盐城市医学会妇产科分会副主任委员。美国纽约凯瑟琳癌症中心，澳大利亚墨尔本MERCY妇产医院，台北荣民总医院访问学者。获江苏省科技厅课题一项，盐城市科技局课题5项，相关研究成果曾获华夏医学科技奖，淮海科技进步二等奖，盐城市新技术引进一等奖等，以第一作者于SCI收录杂志发表论文7篇，中华妇产科杂志等国内核心期刊发表论文30余篇。从事妇产科临床20余年，擅长达芬奇机器人手术，妇科子宫内膜异位症，子宫肌瘤，腺肌症，宫颈病变，疤痕妊娠，子宫内膜占位息肉等腹腔镜宫腔镜微创手术，各类妇科恶性肿瘤如卵巢癌，宫颈癌，子宫内膜癌彻底的分期手术及化疗，靶向等综合治疗。&nbsp;&nbsp;\r\n<p></p>\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'2021-07-13 17:29:49\',\n  \'views\' => 0,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'config\' => \n  array (\n    \'allow_ip\' => \n    array (\n      \'enabled\' => \'0\',\n      \'collectip\' => \'\',\n      \'beginip\' => \'\',\n      \'endip\' => \'\',\n      \'ruleoutip\' => \'\',\n    ),\n  ),\n  \'custom_a\' => \'擅长达芬奇机器人手术，妇科子宫内膜异位症，子宫肌瘤，腺肌症，宫颈病变，疤痕妊娠，子宫内膜占位息肉等腹腔镜宫腔镜微创手术，各类妇科恶性肿瘤如卵巢癌，宫颈癌，子宫内膜癌彻底的分期手术及化疗，靶向等综合治疗。\',\n  \'model\' => \'article\',\n  \'verified\' => \'1\',\n  \'id\' => \'2027\',\n  \'iid\' => \'2027\',\n  \'page\' => \'1\',\n  \'_referer\' => \'http://192.168.162.72/admin.php/cms/item-list\',\n  \'credit_type\' => \'0\',\n  \'credit\' => \'1\',\n  \'action\' => \'update\',\n  \'attachment_hash\' => \'bcd44094af848aa5\',\n  \'verifier\' => \'admin789\',\n)','1649661307');
