INSERT INTO `p8_cms_item` VALUES ('1382','article','【盐阜大众报】市一院医疗队进驻武汉同济中法医院','','0','','842','','','1','[db:作者]','','[db:编辑]','admin','','昨天，记者从武汉获悉，市第一人民医院驰援武汉抗击新型冠状病毒感染的肺炎医疗队于2月2日晚抵达武汉，在经过不到一天的安顿、培训后，已经于3日晚进驻武汉同济中法医院，并开始收治重症隔离患者。 　　利用吃晚饭的时间，医疗队队长颜春燕通过电话告诉记者，所有队员2','1','','1','0','[db:来源]','0','0','0','1580745600','','1625821877','1627979993','1580745600','1','ycyyo','0','1','1','1','0','0','0','0','','','','','','','','','','');
