INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('5847','0','cms','item','update','3297','885','','宣传处','修改内容 | 参观廉政文化教育基地，筑牢廉政思想防线！','/index.php/cms/item-list-category-885-page-4','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-update?model=article&id=3297&verified=1\',\n  \'cid\' => \'885\',\n  \'title\' => \'参观廉政文化教育基地，筑牢廉政思想防线！\',\n  \'title_color\' => \'\',\n  \'timestamp\' => \'2022-06-09 14:32:25\',\n  \'sub_title\' => \'\',\n  \'source\' => \'监督审查室\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'summary\' => \'为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“510”廉政教育活动部署，5月27日，盐城市一院组织党员干部90余人赴东台参观廉政文化教育专线，接受廉政实境教育。在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切\',\n  \'addon_title\' => \'\',\n  \'url\' => \'\',\n  \'verify_frame\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><span style=\"font-size: 15pt;\">为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院</span><span style=\"font-size: 15pt;\">“</span><span style=\"font-size: 15pt;\">510</span><span style=\"font-size: 15pt;\">”</span><span style=\"font-size: 15pt;\">廉政教育活动部署，</span><span style=\"font-size: 15pt;\">5</span><span style=\"font-size: 15pt;\">月</span><span style=\"font-size: 15pt;\">27</span><span style=\"font-size: 15pt;\">日，盐城市一院组织党员干部</span><span style=\"font-size: 15pt;\">90</span><span style=\"font-size: 15pt;\">余人赴东台参观廉政文化教育专线，接受廉政实境教育。</span></span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\">在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切身感受生命激情，重温峥嵘岁月，提高党性修养。</span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><span style=\"font-size: 15pt;\">在黄海森林公园党风廉政教育基地，通过图文和实物相结合的方式，聆听廉政故事，让大家现场感悟</span><span style=\"font-size: 15pt;\">“</span><span style=\"font-size: 15pt;\">艰苦奋斗、科学求真、守正创新、绿色发展</span><span style=\"font-size: 15pt;\">”</span><span style=\"font-size: 15pt;\">的黄海林工精神。</span></span></p>\r\n<span style=\"font-size: 15pt;\">\r\n</span>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\">此次廉政文化实境教育，是一次深刻的学习、是一次心灵的洗礼、更是一次思想的熏陶。大家纷纷表示，在今后工作、学习、生活中，将不断加强自身党性锻炼，遵守党章党纪，廉洁行医，认真履职尽责服务好患者，做一名德技双馨的医务人员。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: center; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><img width=\"553\" height=\"312\" align=\"center\" src=\"/ewebeditor/uploadfile/image/2022/06/09/20220609143554279001.png\"></span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: center; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 15pt;\"><br>\r\n</span></p>\r\n<p></p>\',\n  ),\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'list_order\' => \'2022-06-09 14:32:25\',\n  \'views\' => 0,\n  \'verify\' => 1,\n  \'html\' => \'1\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'con