INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('5933','0','cms','item','add','0','841','','宣传处','添加内容(前台) | 参观廉政文化教育基地，筑牢廉政思想防线','/index.php/cms/item-list-category-841-page-5','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/u.php/cms/item-add?model=article\',\n  \'cid\' => \'841\',\n  \'title\' => \'参观廉政文化教育基地，筑牢廉政思想防线\',\n  \'title_color\' => \'\',\n  \'html\' => \'1\',\n  \'author\' => \'\',\n  \'editer\' => \'\',\n  \'timestamp\' => \'2022-06-01\',\n  \'source\' => \'监督审查室\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'thumb_width\' => \'328\',\n  \'thumb_height\' => \'247\',\n  \'level_time\' => \'不选则永久有效\',\n  \'level\' => \'\',\n  \'summary\' => \'\',\n  \'sub_title\' => \'\',\n  \'verify_frame\' => \'\',\n  \'custom_a\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">为进一步增强党员干部职工廉洁自律意识，根据市纪委监委、市卫健委及医院“<span>510</span>”廉政教育活动部署，<span>5</span>月<span>27</span>日，盐城市一院组织党员干部<span>90</span>余人赴东台参观廉政文化教育专线，接受廉政实境教育。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党性教育基地，全体党员同志重温入党誓词，唱响《歌唱祖国》，切身感受生命激情，重温峥嵘岁月，提高党性修养。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">在黄海森林公园党风廉政教育基地，通过图文和实物相结合的方式，聆听廉政故事，让大家现场感悟“艰苦奋斗、科学求真、守正创新、绿色发展”的黄海林工精神。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">此次廉政文化实境教育，是一次深刻的学习、是一次心灵的洗礼、更是一次思想的熏陶。大家纷纷表示，在今后工作、学习、生活中，将不断加强自身党性锻炼，遵守党章党纪，廉洁行医，认真履职尽责服务好患者，做一名德技双馨的医务人员。</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><br>\r\n</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: center; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\"><img width=\"553\" height=\"311\" align=\"center\" src=\"/ewebeditor/uploadfile/image/2022/06/10/20220610105214855001.png\"></span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; text-indent: 28pt; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p style=\\\'margin: 0cm 0cm 0pt; text-align: justify; font-family: \"Calibri\",\"sans-serif\"; font-size: 10.5pt; -ms-text-justify: inter-ideograph;\\\'><span style=\"font-family: 方正仿宋_GBK; font-size: 14pt;\">&nbsp;</span></p>\r\n<p></p>\',\n  ),\n  \'views\' => \'\',\n  \'url\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'action\' => \'add\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'attachment_hash\' => \'00097fa99e78e03f\',\n  \'verify\' => 1,\n  \'verifier\' => \'宣传处\',\n  \'attribute\' => \n  array (\n    6 => 6,\n  ),\n)','1654829585');
