\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'4936287ecc402dc3\',\n  \'views\' => \'868\',\n  \'verifier\' => \'ycyyo\',\n)','1654844674');
