INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6024','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院发电机组维保项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院发电机组维保项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">1、盐城市第一人民医院发电机组维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">2、项目概况与招标内容</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">2.1项目名称：盐城市第一人民医院发电机组维保项目。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区（毓龙西路166号）、五官科医院（南城河路12号）。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">2.3招标内容：盐城市第一人民医院发电机组维保项目，主院区发电机组6台（4台1000KW、2台200KW），北区发电机组1台300KW，五官科医院发电机组1台150KW。3、投标人资格要求</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">投标人营业执照经营范围包含与发电机组维修或维保等相关内容。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">4、投标报名及招标文件的获取</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年10月14日8时至2020年10月18日18时。联系人：刘永杰，0515-69057406；手机：13626222271。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">5、本项目采用综合评估法。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-align: left; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">其余内容详见招标文件。</span></p><p style=\"text-align: right;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\">招标人：盐城市第一人民医院<br />时间：2020年10月14日</span><br /><br /><br /><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-10-14\',\n  \'timestamp\' => \'2020-10-14\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'c3c8ec06e7b538c3\',\n  \'views\' => \'785\',\n  \'verifier\' => \'ycyyo\',\n)','1654844677');
