INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6034','5','cms','item','add','0','859','','ycyyo','添加内容 | 中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:21pt; \"><span style=\"color:#333333; font-family:\">因医院实际工作需要，于2020年9月26日进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:21pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#333333; font-family:\">一、盐城市第一人民医院主院区停车场倒闸改造等项目</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:21pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#333333; font-family:\">中标单位：江苏德安智慧技术工程有限公司</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:21pt; \"><span style=\"color:#333333; font-family:\">中标价：15.88万元 </span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:21pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#333333; font-family:\">二、盐城市第一人民医院主院区中央空调风口清洗等项目</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:21pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#333333; font-family:\">中标单位：江苏诚锐暖通设备有限公司</span></p><p style=\"line-height:31.5pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:21pt; \"><span style=\"color:#333333; font-family:\">中标价：6万元 </span></p><ol style=\"margin-