INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6036','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院主院区停车场倒闸改造等项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区停车场倒闸改造等项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#000000; font-family:\">1、</span><span style=\"color:#000000; font-family:\">盐城市第一人民医院主院区停车场倒闸改造等项目</span><span style=\"color:#000000; font-family:\">，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#000000; font-family:\">2</span><span style=\"color:#000000; font-family:\">、</span><span style=\"color:#000000; font-family:\">项目概况与招标内容</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">2.1项目名称：</span><span style=\"color:#000000; font-family:\">盐城市第一人民医院主院区停车场倒闸改造等项目</span><span style=\"color:#000000; font-family:\">。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">2.2项目地点：</span><span style=\"font-family:\">盐城市第一人民医院</span><span style=\"font-family:\">主院区（人民南路66号）</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; orphans:0; text-align:justify; text-indent:24pt; widows:0; \"><span style=\"color:#000000; font-family:\">2.3</span><span style=\"font-family:\">招标内容</span><span style=\"font-family:\">：</span><span style=\"font-family:\">主院区停车场倒闸改造等</span><span style=\"font-family:\">。具体内容详见招标人提供的工程量清单。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#000000; font-family:\">3、</span><span style=\"color:#000000; font-family:\">投标人资格要求</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#000000; font-family:\">3.1</span><span style=\"color:#000000; font-family:\">投标人具有建设行政主管部门核发的电子与智能化工程专业承包二级及以上资质的独立法人</span><span style=\"font-family:\">，</span><span style=\"font-family:\">并具有有效的安全生产许可证。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"font-family:\">3.2业绩要求：</span><span style=\"font-family:\">投标人自201</span><span style=\"font-family:\">7</span><span style=\"font-family:\">年</span><span style=\"font-family:\">1</span><span style=\"font-family:\">月1日以来（以合同签订日期为准）</span><span style=\"font-family:\">，</span><span style=\"color:#000000; font-family:\">承担过类似</span><span style=\"font-family:\">停车场改造建设业绩</span><span style=\"font-family:\">（投标时须提供业绩合同原件）。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; \"><span style=\"color:#000000; font-family:\">4、</span><span style=\"color:#000000; font-family:\">投标报名及招标文件的获取</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年9月17日8时至2020年9月21日18时。联系人：刘永杰，0515-85606510；手机：13626222271。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">5</span><span style=\"color:#000000; font-family:\">、</span><span style=\"color:#000000; font-family:\">本项目采用竞争性谈判方式。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-</span><span style=\"color:#000000; font-family:\">66696810</span><span style=\"color:#000000; font-family:\">。</span></p><p style=\"line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">其余内容详见招标文件。</span></p><p style=\"line-height:18pt; margin-bottom:5pt; margin-left:0pt; margin-right:0pt; margin-top:5pt; text-align:right; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">招标人：盐城市第一人民医院</span></p><p style=\"line-height:18pt; margin-bottom:5pt; margin-left:0pt; margin-right:0pt; margin-top:5pt; text-align:right; text-indent:24pt; \"><span style=\"color:#000000; font-family:\">时间：20</span><span style=\"color:#000000; font-family:\">20</span><span style=\"color:#000000; font-family:\">年</span><span style=\"color:#000000; font-family:\">9</span><span style=\"color:#000000; font-family:\">月</span><span style=\"color:#000000; font-family:\">17</span><span style=\"color:#000000; font-family:\">日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-09-17\',\n  \'timestamp\' => \'2020-09-17\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'96c295ccc2b42200\',\n  \'views\' => \'991\',\n  \'verifier\' => \'ycyyo\',\n)','1654844685');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6037','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院主院区3#DSA手术室改造工程招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区3#DSA手术室改造工程招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left;\"><br />&nbsp; 1、盐城市第一人民医院主院区3#DSA手术室改造工程，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。<br />&nbsp; 2、项目概况与招标内容<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.1项目名称：盐城市第一人民医院主院区3#DSA手术室改造工程。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.2项目地点：盐城市第一人民医院主院区（人民南路66号）。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.3招标内容：盐城市第一人民医院主院区3#DSA手术室改造工程,具体内容详见招标人提供的工程量清单。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.4发包方式：包工包料。<br />3、投标人资格要求<br />&nbsp;&nbsp;&nbsp;&nbsp; 3.1投标人须具有有效的机电安装工程专业承包二级及以上资质；<br />&nbsp;&nbsp;&nbsp;&nbsp; 3.2投标人须具有有效的建筑智能化工程专业承包三级及以上资质，或建筑智能化工程设计与施工二级及以上资质；<br />&nbsp;&nbsp;&nbsp;&nbsp; 3.3投标人须具有有效的安全生产许可证。<br />&nbsp;&nbsp;&nbsp;&nbsp; 3.4拟派的项目经理必须具有贰级及以注册建造师证（专业为机电安装），并具有安全生产B类证，且从本工程投标文件递交截止之日起无在建工程。（提供项目经理建造师证书、安全生产B类证、无在建工程承诺书）。投标人需提供项目经理在本单位连续缴纳6个月及以上的社保证明（提供社保证明原件）。<br />4、投标报名及招标文件的获取<br />&nbsp;&nbsp;&nbsp;&nbsp; 请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年9月17日8时至2020年9月21日18时。联系人：刘永杰，0515-85606510；手机：13626222271。<br />5、本项目采用竞争性谈判方式。<br />6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。<br />7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。<br />&nbsp;&nbsp;&nbsp;&nbsp; 其余内容详见招标文件。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 招标人：盐城市第一人民医院<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年9月17日</p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-09-17\',\n  \'timestamp\' => \'2020-09-17\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f93e728609b6c01e\',\n  \'views\' => \'904\',\n  \'verifier\' => \'ycyyo\',\n)','1654844685');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6038','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院主院区中央空调风口清洗等项目招标公告（二次）','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区中央空调风口清洗等项目招标公告（二次）\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">1、盐城市第一人民医院</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">主院区中央空调风口清洗等项目</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">2、项目概况与招标内容</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">2.1项目名称：盐城市第一人民医院</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">主院区中央空调风口清洗等项目</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">2.2项目地点：盐城市第一人民医院</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">主院区（人民南路</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">66号</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">）、</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">北区（毓龙西路166号）</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">2.3招标内容：</span><span style=\"font-family:\">主院区</span><span style=\"font-family:\">风机盘管、空调处理机进出风口</span><span style=\"font-family:\">、风机盘管过滤器及过滤网等</span><span style=\"font-family:\">清洗</span><span style=\"font-family:\">等</span><span style=\"font-family:\">项目</span><span style=\"font-family:\">；委托有资质的第三方检测机构对主院区、北区中央空调系统进行检测，并出具检测合格报告。</span><span style=\"font-family:\">具体内容详见招标人提供的工程量清单</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">2.4发包方式：包工包料。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">3、投标人资格要求</span></p><p style=\"background-color:#ffffff; line-height:22pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; font-family:\">投标</span><span style=\"background-color:#ffffff; font-family:\">人</span><span style=\"background-color:#ffffff; font-family:\">须是</span><span style=\"background-color:#ffffff; font-family:\">具有建设行政主管部门核发的建筑机电安装工程专业承包三级及以上资质</span><span style=\"background-color:#ffffff; font-family:\">的</span><span style=\"background-color:#ffffff; font-family:\">独立法人</span><span style=\"background-color:#ffffff; font-family:\">，</span><span style=\"background-color:#ffffff; font-family:\">并具有</span><span style=\"background-color:#ffffff; font-family:\">有效的安全生产许证</span><span style=\"background-color:#ffffff; font-family:\">。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">4、投标报名及招标文件的获取</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼805室）投标报名，投标报名时间2020年9月17日8时至2020年9月21日18时。联系人：刘永杰，0515-85606510；手机：13626222271。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">5、本项目采用竞争性谈判方式。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"background-color:#ffffff; line-height:24pt; margin-bottom:0pt; margin-left:0pt; margin-right:0pt; margin-top:0pt; text-indent:24pt; \"><span style=\"background-color:#ffffff; color:#000000; font-family:\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">66696810</span><span style=\"background-color:#ffffff; color:#000000; font-family:\">。</span></p><p style=\"background-color:#ffffff; line-