INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6047','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院妇女健康中心及母婴护理服务项目 招标公告（二次）','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院妇女健康中心及母婴护理服务项目 招标公告（二次）\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">1</span><span style=\"font-family:宋体;background:white\">、盐城市第一人民医院妇女健康中心及母婴护理服务项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">2</span><span style=\"font-family:宋体;background:white\">、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">2.1</span><span style=\"font-family:宋体;background:white\">项目名称：盐城市第一人民医院妇女健康中心及母婴护理服务项目。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">2.2</span><span style=\"font-family:宋体;background:white\">项目地点：盐城市第一人民医院主院区（人民南路66号）、北区（毓龙西路166号）<span style=\"color:black\">。</span></span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;background:white\">2.3</span><span style=\"font-family:宋体;background:white\">招标内容：妇女健康中心及母婴护理服务项目，具体内容详见招标人的要求。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">2.4</span><span style=\"font-family:宋体;color:black;background:white\">服务期限：3年。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3</span><span style=\"font-family:宋体;color:black;background:white\">、投标人应当具备的主要资格条件：</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.1</span><span style=\"font-family:宋体;color:black;background:white\">投标人具备独立企业法人资格，且营业执照的经营范围包含母婴护理内容。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">3.2</span><span style=\"font-family:宋体\">本项目不接受联合体投标。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">4</span><span style=\"font-family:宋体;color:black;background:white\">、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年8月27日8时至2020年8月31日18时。联系人：刘永杰，0515-68037830；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">5</span><span style=\"font-family:宋体;color:black;background:white\">、本项目采用“综合评估法”。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;background:white\">6</span><span style=\"font-family:宋体;color:black;background:white\">、投标申请人有违反《盐城市市场廉政准则规定 （试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:32px;background:white\"><span style=\"font-family:宋体;color:black;back