INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6060','5','cms','item','add','0','859','','ycyyo','添加内容 | 中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 18px;\">根据市委市政府要求，须尽快拆除医院北区位于毓龙路北侧、西家属区中心路西侧的发热门诊。医院根据发热门诊的建设规范要求，经院领导及相关职能处室研究决定，在门诊楼西侧与铁栅栏围墙之间重新搭建发热门诊。因发热门诊属于新冠疫情防控的必备场所，院感要求高，按照市财政局关于疫情防控采购便利化的通知，并上报院长办公会、市卫健委、盐城市纪委第十五派驻纪检监察组批复同意，于2020年7月11日进行院内邀请招标。经评标小组评审，中标结果如下：</span></p><p style=\"margin-top:8px;margin-right:0;margin-bottom:16px;margin-left:0;text-indent:28px;vertical-align:baseline\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 18px;\">一、盐城市第一人民医院北区发热门诊设计与施工一体化项目</span></p><p style=\"margin-top:8px;margin-right:0;margin-bottom:16px;margin-left:0;text-indent:32px;vertical-align:baseline\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 18px;\">中标单位：江苏林润建设工程有限公司</span></p><p style=\"margin-top:8px;margin-right:0;margin-bottom:16px;margin-left:0;text-indent:32px;vertical-align:baseline\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 18px;\">中标价： 93.803262万元</span></p><p style=\";margin-bottom:0;text-indent:28px;line-height:42px\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 18px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-align:right;text-indent:28px;line-height:42px\"><span style=\"font-size