INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6063','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院护理员服务招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院护理员服务招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">盐城市第一人民医院护理员服务项目，资金已落实，招标人盐城市第一人民医院，现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">一、项目概况与招标内容</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.1项目名称：盐城市第一人民医院护理员服务项目。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.2项目地点：盐城市第一人民医院主院区、北院区。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.3招标范围：盐城市第一人民医院一对一护理员、团队式护理员服务，具体内容详见招标文件。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.4服务期限：三年，从合同签订之日起计算。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">1.5质量要求：满足患者需求及招标人要求。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">二、投标人资格要求</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-family: 宋体, SimSun; font-size: 18px; color: rgb(51, 51, 51);\">2.1投标人经营范围须包含护理护理服务或与招标内容相关。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">三、投标报名及招标文件的获取</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年7月8日8时至2020年7月13日18时。联系人：刘永杰，0515-68037830；手机：13626222271。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">四、本项目采用“综合评估法”。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">五、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">六、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\";margin-bottom:0;text-indent:36px;line-height:33px;background:white\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">其余内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-indent: 270px; line-height: 33px; background: white; text-align: right;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">招标人：盐城市第一人民医院</span></p><p style=\"margin-bottom: 0px; text-indent: 324px; line-height: 33px; background: white; text-align: right;\"><span style=\"font-size: 18px; color: rgb(51, 51, 51); font-family: 宋体, SimSun;\">时间：2020年7月8日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-07-08\',\n  \'timestamp\' => \'2020-07-08\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'310ea2c2271b49ed\',\n  \'views\' => \'214\',\n  \'verifier\' => \'ycyyo\',\n)','1654844699');
