INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6082','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院主院区冷冻机组维保项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区冷冻机组维保项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">1、盐城市第一人民医院</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">主院区冷冻机组维保</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2、项目概况与招标内容</span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:32px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\">2.1项目名称：盐城市第一人民医院</span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">主院区冷冻机组维保</span></span><span style=\"font-family: 宋体;letter-spacing: 0;font-size: 16px\"><span style=\"font-family:宋体\">项目。</span></span></p><p style=\"margin-top:0;margin-bottom:0;margin-left:0;text-indent:32p