INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6085','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院防风门帘采购及安装项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院防风门帘采购及安装项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">根据医院实际工作需要，我院需采购安装防风门帘，已具备招标条件，拟对该项目进行</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">公开</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">招标，择优选择供货单位。</span></span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1、项目概况</span> </p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1.1 项目名称：盐城市第一人民医院防风门帘采购及安装项目；</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1.2地点：盐城市第一人民医院</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">主院区（人民南路</span></span><span style=\"font-family: 宋体;font-size: 16px\">66号</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">）</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">、北区</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">（毓龙西路</span></span><span style=\"font-family: 宋体;font-size: 16px\">166号</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">）、五官科医院（南城河路</span></span><span style=\"font-family: 宋体;font-size: 16px\">12号</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">）</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">；</span></span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1.3招标内容：自动吸附磁性透明门帘及普通透明软门帘</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">等</span></span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">采购及安装，具体内容详见招标人要求和工程量清单；</span> </span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1.4供货期： 15 日历天，具体开工日期以招标人的通知；</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">1.5质量标准：符合国家及行业合格标准，且质量需等于或优于医院现有的门帘。质保期1年。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">2 投标人应当具备的主要资格条件：</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">2.1投标人营业执照经营范围包含窗帘（或门帘）生产（或销售）等相关内容。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">2.2投标人投标时须携带招标范围内的实物样品（规格不限，材质须达到中档及以上等级品质）。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">3、本项目采用资格后审，竞争性谈判。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">4、投标报名及招标文件的获取</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:13px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路</span>8号8楼810室）投标报名并领取招标文件，投标报名时间2020年</span><span style=\"font-family: 宋体;font-size: 16px\">5</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">月</span></span><span style=\"font-family: 宋体;font-size: 16px\">27</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">日</span>8时至2020年</span><span style=\"font-family: 宋体;font-size: 16px\">5</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">月</span></span><span style=\"font-family: 宋体;font-size: 16px\">31</span><span style=\"font-family: 宋体;font-size: 16px\"><span style=\"font-family:宋体\">日</span>18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span style=\"font-family: 宋体;font-size: 16px\">5、本项目采用竞争性谈判方式。</span></p><p style=\"margin-top:0;margin-bottom:0;text-indent:32px;text-autospace:ideograph-numeric;line-height:29px;background:rgb(255,255,255)\"><span st