INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6090','5','cms','item','add','0','859','','ycyyo','添加内容 | 部分信息化建设项目调研公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'部分信息化建设项目调研公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'设备处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent:28px\"><span style=\"font-size: 18px;\"><span style=\"font-family: 宋体;\">为了确保医用信息化建设的网络安全，按照“公平，公正，公开</span>”<span style=\"font-family: 宋体;\">的原则，对我院急需采购一批网络安全设备与软件，现进行市场调研，欢迎有资质的公司前来报名，现将有关事项公告如下：</span></span></p><p style=\"text-indent:28px\"><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">一、本次招标采购目录</span></p><p><span style=\"font-size: 18px;\">1<span style=\"font-size: 18px; font-family: 宋体;\">、网络安全设备与软件</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px; font-family: 宋体;\">基本要求：按三级等保</span>2.0<span style=\"font-size: 18px; font-family: 宋体;\">版，信息网络安全需要增加安全设备（含态势感知系统、防火墙、漏洞扫描、入侵检测系统、病毒库升级费）。</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\">2<span style=\"font-size: 18px; font-family: 宋体;\">、信息化终端安全管理系统</span>&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\"><span style=\"font-size: 18px; font-family: 宋体;\">基本要求：对医院所有信息终端进行准入、监管。</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 18px;\">&nbsp;</span></p><p><span style=\"font-family: 宋体; font-size: 18px;\">二、供应商资格要求</span></p><p><span style=\"font-si