INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6125','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院北区溴化锂中央空调机组维保项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区溴化锂中央空调机组维保项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-left: 0px; text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">1、盐城市第一人民医院北区溴化锂中央空调机组维保项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-left: 0px; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.</span><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">项目概况与招标内容</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">2.1项目名称：盐城市第一人民医院北区溴化锂中央空调机组维保项目。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体; color: black;\">2.2项目地点：盐城市第一人民医院</span><span style=\"font-size: 20px; font-family: 宋体;\">北区（毓龙西路166号）</span></span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3招标内容：北区2#、3#、4#溴化锂空调机组内腔、传热管清洗、溴化锂溶液再生、补充溶液、更换真空密封件、高低温换热器维修、设备调试等维修保养项目。具体内容详见招标文件及工程量清单。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">2.4质保期：2个供冷期，质保期内如溴化锂机组发生故障，由中标人负责修复，所产生的相关费用由中标人承担。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.投标人资格要求</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.1 投标人具备独立企业法人资格，营业执照的经营范围应包含中央空调维修或保养内容。</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3.2中标人必须派出有从事过溴化锂机组安装、调试及维护等相关经验的专业技术人员；投标时须授权技术人员参加本项目开标。</span></p><p style=\"margin-left: 0px; text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">4.<span style=\"color: black; font: 9px \"Times New Roman\";\">&nbsp; </span>投标报名及招标文件的获取</span></p><p style=\"text-align: left; text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件或授权委托书(注明联系人、联系电话及邮箱)到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年3月25日8时至2020年3月29日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5. 本项目采用“综合评估法”。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"margin-bottom: 0px; text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">招标人：盐城市第一人民医院</span></p><p style=\"margin-bottom: 0px; text-align: right; text-indent: 32px; line-height: 3em;\"><span style=\"color: black; font-family: 宋体, SimSun; font-size: 20px;\">时间：2020年3月25日 </span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-03-25\',\n  \'timestamp\' => \'2020-03-25\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'13fbfea2adf3d77d\',\n  \'views\' => \'246\',\n  \'verifier\' => \'ycyyo\',\n)','1654844732');
