INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6139','5','cms','item','add','0','859','','ycyyo','添加内容 | 新冠肺炎救治急需医疗设备采购公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'新冠肺炎救治急需医疗设备采购公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'设备处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院是我市新型冠状病毒肺炎救治定点医院，近期陆续收治多名新冠肺炎确诊病例，其中包括一名危重病例。经省卫健委治疗专家组现场会诊，建议配备无创呼吸机等专用医疗设备。由于疫情紧急，经医院领导同意，依据《财政部办公厅关于疫情防控采购便利化的通知》的要求，医院邀请多家品牌销售商，进行竞争性谈判；现对采购结果予以公示。</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、无创呼吸机*2台</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">飞利浦伟康V60&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 总价：36万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：南京唐天科技有限公司</span></p><p style=\"line-height: 3em; text-indent: 0em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、呼吸湿化治疗仪*2台</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">费雪派克PT101AZ&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;总价：13.2万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城普康医疗器械有限公司</span></p><p style=\"line-height: 3em; text-indent: 0em;\" dir=\"ltr\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、等离子空气消毒机*13台</span></p><p style=\"line-hei