INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6143','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院布质隔离衣、帽子采购项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院布质隔离衣、帽子采购项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">1、盐城市第一人民医院布质隔离衣、帽子采购项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.1项目名称：盐城市第一人民医院布质隔离衣、帽子采购项目。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体; color: black; background: white;\">2.2项目地点：盐城市第一人民医院</span><span style=\"font-size: 20px; font-family: 宋体; background: white;\">（人民南路66号、毓龙西路166号）</span></span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.3招标内容：布质隔离衣、帽子采购项目，具体内容详见招标人提供的工程量清单。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.4发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3.1投标人营业执照经营范围包含本次招标的相关内容；</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3.2投标人投标时需至少携带2-3种布料样品供招标人选择。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到盐城市第一人民医院北区（毓龙西路166号）总务处投标报名并领取招标文件，投标报名时间2020年2月7日18时止。联系人：凌晨 0515-88508933</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;时间：2020年2月7日&nbsp; </span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-02-07\',\n  \'timestamp\' => \'2020-02-07\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'8f493b81c96e1b4f\',\n  \'views\' => \'229\',\n  \'verifier\' => \'ycyyo\',\n)','1654844743');
