INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6147','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院垃圾房改造工程招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院垃圾房改造工程招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院垃圾房改造工程已具备招标条件，现拟对该工程进行公开招标，择优选择施工单位。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1、工程概况</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.1 工程名称：盐城市第一人民医院垃圾房改造工程；</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.2建设地点：盐城市第一人民医院南区（盐城市人民南路66号）；</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.3招标内容：盐城市第一人民医院垃圾房改造工程施工，具体内容详见招标人提供的设计图纸和工程量清单。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.4发包方式：包工包料。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.5质量标准：符合国家“合格”标准。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">1.6工期：20日历天。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">2、投标人资格要求</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">2.1投标人具备独立企业法人资格；</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">2.2投标人须是具有建设主管部门核发的建筑工程总承包三级及以上（或建筑装饰装修工程专业承包二级及以上）资质的独立法人，并具有有效的安全生产许可证；</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">2.3项目负责人须具有建筑工程专业二级及以上注册建造师资格证书和有效的安全生产考核合格证（B类）。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">3、投标报名</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话和邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年1月17日8时至2020年1月21日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">4、本项目采用竞争性谈判方式。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; color: rgb(51, 51, 51); font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-align: left; text-indent: 37px; line-heigh