INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6149','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院医用耗材招标采购公告（危化品类）','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院医用耗材招标采购公告（危化品类）\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">因我院现危化品供应商的《危险化学品经营许可证》即将到期，经医院领导研究决定，将以江苏省医用耗材阳光采购平台目录为基础，按照“公开、公平、公正”的原则，对部分危化品进行招标采购，欢迎进入江苏省阳光采购平台的生产企业和取得生产企业授权的配送企业参加，现将有关事项公告如下：</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">一、本次招标采购项目名称</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">、</span><span style=\"font-size: 19px;\">75%</span><span style=\"font-family: 宋体; font-size: 19px;\">酒精</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">2</span><span style=\"font-family: 宋体; font-size: 19px;\">、</span><span style=\"font-size: 19px;\">95%</span><span style=\"font-family: 宋体; font-size: 19px;\">酒精</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">3</span><span style=\"font-family: 宋体; font-size: 19px;\">、过氧乙酸</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">4</span><span style=\"font-family: 宋体; font-size: 19px;\">、松节油</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">5</span><span style=\"font-family: 宋体; font-size: 19px;\">、无水乙醇</span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 19px;\">备注：其中</span><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">、</span><span style=\"font-size: 19px;\">2</span><span style=\"font-family: 宋体; font-size: 19px;\">、</span><span style=\"font-size: 19px;\">3</span><span style=\"font-family: 宋体; font-size: 19px;\">项产品必须入围省网平台并提供相应的平台编号。</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">二、供应商资格要求</span></p><p style=\"line-height: 3em; text-indent: 0px; margin-left: 0px;\"><span style=\"font-size: 19px;\">1、</span><span style=\"font-family: 宋体; font-size: 19px;\">具备独立法人的资格。</span></p><p style=\"line-height: 3em; text-indent: 0px; margin-left: 0px;\"><span style=\"font-size: 19px;\">2、</span><span style=\"font-family: 宋体; font-size: 19px;\">具有良好的商业信誉。</span></p><p style=\"line-height: 3em; text-indent: 0px; margin-left: 0px;\"><span style=\"font-size: 19px;\">3、</span><span style=\"font-family: 宋体; font-size: 19px;\">具有独立承担民事责任的能力。</span></p><p style=\"line-height: 3em; text-indent: 0px; margin-left: 0px;\"><span style=\"font-size: 19px;\">4、</span><span style=\"font-family: 宋体; font-size: 19px;\">具有履行合同所必需的设备和专业技术能力。</span></p><p style=\"line-height: 3em; text-indent: 0px; margin-left: 0px;\"><span style=\"font-size: 19px;\">5、</span><span style=\"font-family: 宋体; font-size: 19px;\">近三年内在经营活动中无严重不良记录。</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">三、报名</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">、报名方式：现场报名</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">2</span><span style=\"font-family: 宋体; font-size: 19px;\">、报名时间</span><span style=\"font-size: 19px;\">:2020</span><span style=\"font-family: 宋体; font-size: 19px;\">年</span><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">月</span><span style=\"font-size: 19px;\">14</span><span style=\"font-family: 宋体; font-size: 19px;\">日</span><span style=\"font-size: 19px;\">--2020</span><span style=\"font-family: 宋体; font-size: 19px;\">年</span><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">月</span><span style=\"font-size: 19px;\">20</span><span style=\"font-family: 宋体; font-size: 19px;\">日</span></p><p style=\"line-height: 3em; text-indent: 19px;\"><span style=\"font-family: 宋体; font-size: 19px;\">上午：</span><span style=\"font-size: 19px;\">8</span><span style=\"font-family: 宋体; font-size: 19px;\">：</span><span style=\"font-size: 19px;\">00--11:30&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 宋体; font-size: 19px;\">下午：</span><span style=\"font-size: 19px;\">13:30--17:00</span></p><p style=\"line-height: 3em; text-indent: 19px;\"><span style=\"font-family: 宋体; font-size: 19px;\">法定假日休息</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 19px;\">3</span><span style=\"font-family: 宋体; font-size: 19px;\">、报名地点：盐城市第一人民医院北区招标采购中心办公室（盐城市亭湖区毓龙路</span><span style=\"font-size: 19px;\">166</span><span style=\"font-family: 宋体; font-size: 19px;\">号）</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">四、报名须知：凡具备资格要求的供应商报名时，须携带企业营业执照副本、资质证书、法定代表人身份证明、平台中标的相关资料、取得生产企业授权的委托书等复印件，上述文件必须加盖单位公章。</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">五、公告公示截止时间：</span><span style=\"font-size: 19px;\">2020</span><span style=\"font-family: 宋体; font-size: 19px;\">年</span><span style=\"font-size: 19px;\">1</span><span style=\"font-family: 宋体; font-size: 19px;\">月</span><span style=\"font-size: 19px;\">20</span><span style=\"font-family: 宋体; font-size: 19px;\">日</span></p><p style=\"line-height: 3em; text-indent: 37px;\"><span style=\"font-family: 宋体; font-size: 19px;\">六、本次招标项目联系人：张琳琳</span><span style=\"font-size: 