INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6150','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院主院区、北区零星维修项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院主院区、北区零星维修项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">1、盐城市第一人民医院主院区、北区零星维修项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.1项目名称：盐城市第一人民医院主院区、北区零星维修项目。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 20px; font-family: 宋体; color: black; background: white;\">2.2项目地点：盐城市第一人民医院主院区（人民南路66号）、北区</span><span style=\"font-size: 20px; font-family: 宋体; background: white;\">（毓龙西路166号）。</span></span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.3招标内容：北区新做一辆医疗废物转运车，北区空调班负压吸引储气罐、管道、9号楼太阳能大水箱槽钢支架除锈粉刷，北区血液净化中心四楼玻璃门更换指纹密码锁，主院区护理部实训室增加隔断、窗帘，咖啡厅、影像科玻璃更换，屋面机房玻璃门改造，主院区住院部26号电梯轿厢地板修补等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">2.4发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">3、投标人资格要求</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">投标人须具有有效的建筑装修装饰工程专业承包二级及以上施工资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2020年1月9日8时至2020年1月12日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-88508906。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em;\"><span style=\"color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">其余内容详见招标文件。</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-family: 宋体; color: black; background: white; font-size: 20px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2020年1月9日&nbsp; </span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-01-09\',\n  \'timestamp\' => \'2020-01-09\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'f2c5b04fcfc967ac\',\n  \'views\' => \'571\',\n  \'verifier\' => \'ycyyo\',\n)','1654844747');
