INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6152','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院垃圾房工程招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院垃圾房工程招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院垃圾房工程已具备招标条件，现拟对该工程进行公开招标，择优选择施工单位。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1、工程概况&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.1 工程名称：盐城市第一人民医院垃圾房工程；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.2建设地点：盐城市第一人民医院南区（盐城市人民南路66号）；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.3招标内容：盐城市第一人民医院垃圾房工程施工，具体内容详见招标人提供的设计图纸和工程量清单。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.4发包方式：包工包料。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.5质量标准：符合国家“合格”标准。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">1.6工期：20日历天。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2、投标人资格要求</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.1投标人具备独立企业法人资格；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.2投标人须是具有建设主管部门核发的建筑工程总承包三级及以上资质的独立法人，并具有有效的安全生产许可证；</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2.3项目负责人须具有建筑工程专业二级及以上注册建造师资格证书和有效的安全生产考核合格证（B类）。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">3、投标报名</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话和邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年1月3日8时至2020年1月9日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">4、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">具体内容详见招标文件。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">招标人:盐城市第一人民医院</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体; font-size: 20px;\">2020年1月2日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-01-03\',\n  \'timestamp\' => \'2020-01-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'e3be47479fbaab7e\',\n  \'views\' => \'792\',\n  \'verifier\' => \'ycyyo\',\n)','1654844748');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6153','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目已具备招标条件，拟对该项目进行公开招标，择优选择设计单位。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1、项目概况</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.1 项目名称：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务项目；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.2建设地点：盐城市人民南路66号；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.3工程规模：装修建筑面积约1600m<sup>2</sup>，工程投资约300万元。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.4招标内容：盐城市第一人民医院保健基地（主院区住院部19楼）装修工程设计服务，包含但不限于方案设计、施工图设计以及施工和保修等阶段设计咨询服务等，具体以招标人的书面通知为准。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.5质量标准：符合国家、省及市关于施工图设计深度的规范要求，并满足《建筑工程设计文件编制深度规定(2016年版)》，最终设计成果须确保通过相关部门审查。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">1.6设计时限：满足招标人要求。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2、投标人资格要求</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.1投标人具备独立企业法人资格；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.2投标人须是具有建设主管部门核发的建筑装饰工程设计专项乙级及以上或工程设计综合甲级资质的独立法人；</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.3项目负责人须是本单位在职职工，并具有工程类高级及以上职称。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">2.4本次招标不接受联合体投标。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">3、投标报名</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信（或授权委托书，须注明联系人、电话及邮箱）原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名，投标报名时间2020年1月3日8时至2020年1月9日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。逾期不再接收报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"color: blue; font-family: 宋体, SimSun; font-size: 20px;\">4、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">5、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">6、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 32px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">具体内容详见招标文件。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;时间：2020年1月3日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2020-01-03\',\n  \'timestamp\' => \'2020-01-03\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'13d1db5b6c54535c\',\n  \'views\' => \'910\',\n  \'verifier\' => \'ycyyo\',\n)','1654844749');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6154','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院医用耗材招标采购公告（危化品类）','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院医用耗材招标采购公告（危化品类）\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因我院现危化品供应商的《危险化学品经营许可证》即将到期，经医院领导研究决定，将以江苏省医用耗材阳光采购平台目录为基础，按照“公开、公平、公正”的原则，对部分危化品进行招标采购，欢迎进入江苏省阳光采购平台的生产企业和取得生产企业授权的配送企业参加，现将有关事项公告如下：</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、本次招标采购项目名称</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">75%</span><span style=\"font-size: 19px; font-family: 宋体;\">酒精</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">95%</span><span style=\"font-size: 19px; font-family: 宋体;\">酒精</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3</span><span style=\"font-size: 19px; font-family: 宋体;\">、过氧乙酸</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">4</span><span style=\"font-size: 19px; font-family: 宋体;\">、松节油</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">5</span><span style=\"font-size: 19px; font-family: 宋体;\">、无水乙醇</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-size: 19px; font-family: 宋体;\">备注：其中</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3</span><span style=\"font-size: 19px; font-family: 宋体;\">项产品必须入围省网平台并提供相应的平台编号。</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、供应商资格要求</span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1、</span><span style=\"font-size: 19px; font-family: 宋体;\">具备独立法人的资格。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有良好的商业信誉。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有独立承担民事责任的能力。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">4、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有履行合同所必需的设备和专业技术能力。</span></span></p><p style=\"margin-left: 0px; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">5、</span><span style=\"font-size: 19px; font-family: 宋体;\">近三年内在经营活动中无严重不良记录。</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、报名</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名方式：现场报名</span></span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名时间</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">:2019</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">12</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">31</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">--2020</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">月</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">7</span><span style=\"font-size: 19px; font-family: 宋体;\">日</span></span