INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6158','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院盐城市儿童医院拟采购信息化建设设备调研公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院盐城市儿童医院拟采购信息化建设设备调研公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'设备处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">盐城市儿童医院由盐城市第一人民医院代筹建，拟采购一批信息化建设所需设备，将于近期开展市场调研询价，欢迎符合条件的设备生产企业、经营企业前来参与。</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">项目名称及初步要求</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\"><span style=\"font-family: 宋体, SimSun; line-height: 150%; color: black;\">一、</span><span style=\"font-family: 宋体, SimSun; line-height: 150%; color: black; background: white;\">租用万兆裸光纤两条（含万兆光模块）</span></span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp; 区间：盐城市第一人民医院南区中心机房至盐城市儿童医院门诊楼主机房</span></p><p style=\"margin-bottom: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\">二、网络交换机(包含安装调试服务):盐城市儿童医院由盐城市第一人民医院筹建，进行信息系统一体化建设，要求交换机需与医院现有网络光模块完全兼容，与现院区网络OSPF路由协议，MSTP协议，VRRP等网络协议完全兼容，现交换机采购统一品牌为H3C。</span></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><img src=\"http://localhost/upload/images/2019/12/55c3e55e22ff8e24.png\" title=\"image.png\" alt=\"image.png\" /></p><p style=\"margin-bottom: 0px; line-height: 3em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体, SimSun; font-size: 20px;\"></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、<span style=\"font-family: 宋体, SimSun; line-height: 150%; color: black; background: white;\">医院主机房UPS电源1台（推荐品牌：艾默生、APC、梅兰日兰、易斯特)参数要求：UPS容量</span><span style=\"font-family: 宋体, SimSun; line-height: 150%; color: black;\">≥</span><span style=\"font-family: 宋体, SimSun; line-height: 150%; color: black; background: white;\">6KVA，单进单出主机，双变换工频在线式设计结构，带隔离变压器，输入范围220V±25%单相相三线 ，输入频率50Hz±10%，输出范围单相220V±1%，断电延长时间4小时，主机、电池三年质保，免费安装调试。  </span></span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; font-family: 宋体, SimSun; font-size: 20px;\">报名截止时间：2019年12月25日</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; font-family: 宋体, SimSun; font-size: 20px;\">报名地址：盐城市第一人民医院北区设备处（毓龙西路166号）</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; font-family: 宋体, SimSun; font-size: 20px;\">联系人： 李老师&nbsp;&nbsp;&nbsp;&nbsp; 联系电话：0515-88508946&nbsp; 83578956</span></p><p style=\"margin-bottom: 0px; text-indent: 37px; line-height: 3em;\"><span style=\"line-height: 150%; color: black; background: white; font-family: 宋体