INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6159','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院盐城市儿童医院4台射线装置评价项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院盐城市儿童医院4台射线装置评价项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'预防保健处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">一、根据《中华人民共和国政府采购法》、《中华人民共和国招标投标法》等有关法律规定，决定对盐城市第一人民医院盐城市儿童医院</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">4</span><span style=\"font-size:20px;font-family:宋体\">台射线装置评价项目进行公开招标，诚邀合格的供应商前来报名参加本项目的投标。</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\"> </span></p><p style=\"text-indent: 40px; line-height: 3em;\"><strong><span style=\"font-size: 20px;font-family:宋体\">二、项目概况</span></strong></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family: 宋体\">）项目名称：盐城市第一人民医院盐城市儿童医院</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">4</span><span style=\"font-size:20px;font-family:宋体\">台射线装置评价项目（</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">2</span><span style=\"font-size:20px;font-family:宋体\">台</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">DR</span><span style=\"font-size:20px;font-family:宋体\">、</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family:宋体\">台数字胃肠机、</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family:宋体\">台</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">CT</span><span style=\"font-size:20px;font-family:宋体\">）</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">2</span><span style=\"font-size:20px;font-family: 宋体\">）项目地点：盐城市毓龙西路</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">34</span><span style=\"font-size:20px;font-family:宋体\">号</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">3</span><span style=\"font-size:20px;font-family: 宋体\">）招标方式：公开招标</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">4</span><span style=\"font-size:20px;font-family: 宋体\">）采购内容：本项目为盐城市第一人民医院盐城市儿童医院</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">2</span><span style=\"font-size:20px;font-family:宋体\">台</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">DR</span><span style=\"font-size:20px;font-family:宋体\">、</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family:宋体\">台数字胃肠机、</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family:宋体\">台</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">CT</span><span style=\"font-size:20px;font-family:宋体\">评价项目</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">(</span><span style=\"font-size:20px;font-family:宋体\">含环评备案、防护检测、性能检测、预评价、控制效果评价及办理辐射安全许可证和放射诊疗许可证</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">)</span><span style=\"font-size:20px;font-family:宋体\">。</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">5</span><span style=\"font-size:20px;font-family: 宋体\">）资金来源：自筹</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><strong><span style=\"font-size: 20px;font-family:宋体\">三、申请人应当具备的主要资格条件及要求</span></strong></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">1</span><span style=\"font-size:20px;font-family: 宋体\">）投标人是在江苏省登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">2</span><span style=\"font-size:20px;font-family: 宋体\">）具备职业卫生（放射卫生）技术服务机构资质；</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman\\\',\\\'serif\\\'\">3</span><span style=\"font-size:20px;font-family: 宋体\">）具备与投标检测设备相符合的</span><span style=\"font-size:20px;font-family:\\\'Times New Roman\\\',\\\'serif\\\'\">CMA</span><span style=\"font-size:20px;font-family:宋体\">计量认证内容并提供证书及附件；</span></p><p style=\"text-indent: 40px; line-height: 3em;\"><span style=\"font-size:20px;font-family:宋体\">（</span><span style=\"font-size:20px;font-family: \\\'Times New Roman