INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6169','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院南、北区零星维修项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院南、北区零星维修项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">1</span><span style=\"font-size:19px;font-family:宋体\">、盐城市第一人民医院南、北区零星维修项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2</span><span style=\"font-size:19px;font-family:宋体\">、项目概况与招标内容</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.1</span><span style=\"font-size:19px;font-family:宋体\">项目名称：盐城市第一人民医院南、北区零星维修项目。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.2</span><span style=\"font-size:19px;font-family:宋体\">项目地点：盐城市第一人民医院南区（人民南路66号）、北区（毓龙西路166号）、五官科医院（南城河路12号）。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.3</span><span style=\"font-size:19px;font-family:宋体\">招标内容：南区东行政区办公室新做安装纱帘、被服中心增加转运车；北区医技楼二楼CT读片室西窗新做遮阳蓬、6#病房楼西侧化粪池污水管改造等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"text-align: left; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.4</span><span style=\"font-size:19px;font-famil