INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6172','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院北区急诊配液室净化空调主机采购及安装','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院北区急诊配液室净化空调主机采购及安装\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">1</span><span style=\"font-size:19px;font-family:宋体\">、盐城市第一人民医院北区急诊配液室净化空调主机采购及安装项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2</span><span style=\"font-size:19px;font-family:宋体\">、项目概况与招标内容</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.1</span><span style=\"font-size:19px;font-family:宋体\">项目名称：盐城市第一人民医院北区急诊配液室净化空调主机采购及安装项目。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.2</span><span style=\"font-size:19px;font-family:宋体\">项目地点：盐城市第一人民医院北区（毓龙西路166号）。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.3</span><span style=\"font-size:19px;font-family:宋体\">招标内容：北区急诊配液室净化空调主机采购、安装以及调试等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">2.4</span><span style=\"font-size:19px;font-family:宋体\">发包方式：包工包料。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">3</span><span style=\"font-size:19px;font-family:宋体\">、投标人资格要求</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">投标人须具有有效的机电工程施工总承包三级及以上或建筑机电安装专业承包三级及以上资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">4</span><span style=\"font-size:19px;font-family:宋体\">、投标报名及招标文件的获取</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间<span style=\"text-decoration:underline;\">2019</span>年<span style=\"text-decoration:underline;\">11</span>月<span style=\"text-decoration:underline;\">7</span>日<span style=\"text-decoration:underline;\"> 8 </span>时至<span style=\"text-decoration:underline;\">2019</span>年<span style=\"text-decoration:underline;\">11</span>月<span style=\"text-decoration:underline;\">12</span>日<span style=\"text-decoration:underline;\">18 </span>时。联系人：胡荣艳13815581495，李纯东18796550337。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">5</span><span style=\"font-size:19px;font-family:宋体\">、本项目采用竞争性谈判方式。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">6</span><span style=\"font-size:19px;font-family:宋体\">、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">7</span><span style=\"font-size:19px;font-family:宋体\">、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">其余内容详见招标文件。</span></p><p style=\"text-align: right; text-indent: 37px; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align: right; text-indent: 0em; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">&nbsp; </span><span style=\"font-size:19px;font-family:宋体\">招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; line-height: 3em;\"><span style=\"font-size:19px;font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:19px;font-family:宋体\">时间：2019年11月6日&nbsp;</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-11-07\',\n  \'timestamp\' => \'2019-11-07\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'15e4ce18e1bf7c81\',\n  \'views\' => \'334\',\n  \'verifier\' => \'ycyyo\',\n)','1654844758');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6173','5','cms','item','add','0','859','','ycyyo','添加内容 | 部分新增检验项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'部分新增检验项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'设备处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">为了提高医院医疗诊断水平，检验科计划开展部分新的检验项目。根据《医疗机构医用耗材管理办法》等政策要求，以江苏省阳光采购平台目录为基础，按照“公平，公正，公开”的原则，对新增检验项目所用试剂及耗材进行招标采购，欢迎进入阳光采购平台的生产企业或取得生产企业授权的配送企业报名参加，现将有关事项公告如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">一．本次招标采购目录</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.异常凝血酶原</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2. ANCA（免疫荧光法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3. TPPA(梅毒诊断)梅毒螺旋体颗粒凝集实验</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4.甲戊肝（发光法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5. IgE检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">6. 内毒素检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">7. G实验&nbsp;&nbsp; 真菌/细菌动态监测仪</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; G实验&nbsp;&nbsp; 真菌（1-3）-B-D葡聚糖检测试剂盒</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">8. 血栓四项</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">9. 粪钙卫蛋白</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">10.肺支抗体</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">11.幽门螺旋杆菌抗体（可区分有毒株和无毒株）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">12.呼吸道抗原筛查（金标法）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">13.抗磷脂综合征化学发光法血清血检测</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">二．</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">供应商资格要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.具备独立法人的资格</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.具有良好的商业信誉</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.具有独立承担民事责任的能力</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">4.具有履行合同所必需的设备和专业技术能力</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">5.近三年内在经营活动中无严重不良记录</span><span style=\"font-family: 宋体, SimSun; font-size: 20px; text-indent: 2em;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三．报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">1.报名方式：现场报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">2.报名时间：2019年11月5日-11月9日 上午8：00 -11：30&nbsp;&nbsp;&nbsp; 下午14：00-17：30</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">3.报名地点：盐城一院北区设备处</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四．报名须知</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">凡具备资格要求的供应商报名时，须携带企业营业执照副本、资质证书、法定代表人身份证明、平台中标的相关资料、取得生产企业授权的委托书等复印件，上述文件必须加盖单位公章并装订成册。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">五．本次招标项目联系人：唐志明&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; &nbsp;联系电话：0515-88508946&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0515-83578946</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-11-05\',\n  \'timestamp\' => \'2019-11-05\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'5a4dedae9631670d\',\n  \'views\' => \'857\',\n  \'verifier\' => \'ycyyo\',\n)','1654844759');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6174','5','cms','item','add','0','859','','ycyyo','添加内容 | 一院10月26日中标公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'一院10月26日中标公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因医院实际工作需要，于2019年10月26日9:30时进行招标的项目，经评标小组评审，中标结果如下：<br /></span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、南、北区监控及门禁增补等项目</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：南京通广智能科技有限公司</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中 标 价：9.3万元&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"text-align: left; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">为体现公开、公平、公正的原则，特此公告三天。如有疑问请与盐城市第一人民医院监察室联系，电话0515-88508906。</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">　　&nbsp;</span></p><p style=\"text-align: right; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">盐城市第一人民医院</span></p><p style=\"text-align: right; text-indent: 2em; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;2019年10月26日</span></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-10-28\',\n  \'timestamp\' => \'2019-10-28\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'abd11a1210ad29ce\',\n  \'views\' => \'629\',\n  \'verifier\' => \'ycyyo\',\n)','1654844760');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6175','5','cms','item','add','0','859','','ycyyo','添加内容 | 1台口腔CT评价项目公开招标采购公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'1台口腔CT评价项目公开招标采购公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'预防保健处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、根据《中华人民共和国政府采购法》、《中华人民共和国招标投标法》等有关法律规定，决定对盐城市第一人民医院1台口腔CT评价项目进行公开招标，诚邀合格的供应商前来报名参加本项目的投标。 </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、项目概况</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）项目名称：盐城市第一人民医院1台口腔CT评价项目</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）项目地点：盐城市人民南路66号</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）招标方式：公开招标</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）采购内容：本项目为盐城市第一人民医院1台口腔CT评价项目(含环评备案、防护检测、性能检测、预评价、控制效果评价及办理辐射安全许可证和放射诊疗许可证)。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）资金来源：自筹</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（6）项目规模：详见清单</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、申请人应当具备的主要资格条件及要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（一）、投标人资质条件： </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）投标人是在江苏省登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）具备职业卫生（放射卫生）技术服务机构资质、江苏省生态环保厅颁发的社会辐射环境检测机构资质；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）具备与投标检测设备相符合的CMA计量认证内容并提供证书及附件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）符合《中华人民共和国政府采购法》第22条规定；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）本项目中标后，不得转包；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（6）本项目不接受联合体投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">四、报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">请投标申请人于2019年10月28日至2019年10月31日前每天上午8时30分至11时30，下午2时30分至4时到盐城市毓龙163号预防保健处报名,咨询电话：0515-66696828。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">报名经办人必须为投标单位正式职工，报名时须提供报名人员身份证、法定代表人授权委托书原件、营业执照正本复印件（加盖公章）、卫生和环保两方面颁发的资质证书复印件（加盖公章）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">五、本项目对投标申请人的资格审查采用资格后审方式，由评标委员会根据招标文件进行评定；报名时不进行报名资料的任何审查，由意向投标人自行判断是否符合投标资格</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">六、本招标项目采用的评标方法：经评审的最低价法</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">七、询价报价文件的组成及份数</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）投 标 函</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）法定代表人身份证明书</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）法定代表人授权委托书</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）开标一览表</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（5）营业执照正本复印件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（6）公司资质（卫生及环保）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（7）业绩；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（8）可提供的其他文件。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">八、投标时间、开标时间和地点</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）投标时间段：2019年10月28日8：30至16：00——2019年10月31日8：30至16：00</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（2）开标时间：2019年11月1日（具体时间待定）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（3）投标文件提交地点：盐城市第一人民医院预防保健科（地址：盐城市毓龙163号）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（4）成交结果公示后，成交供应商须在3个工作日内与采购单位签定合同。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">九、付款方式</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">提交现场检测结果的纸质检测报告后一周内支付全款。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">十、联系方式</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">（1）招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp; &nbsp;联系人:周公平&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话:0515-66696828</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;&nbsp;&nbsp; </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">&nbsp;</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-10-24\',\n  \'timestamp\' => \'2019-10-24\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'9c57ed8597ca5748\',\n  \'views\' => \'339\',\n  \'verifier\' => \'ycyyo\',\n)','1654844760');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6176','5','cms','item','add','0','859','','ycyyo','添加内容 | 放射设备年度环保、卫生检测项目公开招标采购公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'放射设备年度环保、卫生检测项目公开招标采购公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'预防保健处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">一、根据《中华人民共和国政府采购法》、《中华人民共和国招标投标法》等有关法律规定，决定对盐城市第一人民医院放射设备年度环保、卫生检测项目进行公开招标，诚邀合格的供应商前来报名参加本项目的投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、项目概况</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（1）项目名称：盐城市第一人民医院放射设备年度环保、卫生检测项目</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（2）项目地点：盐城市人民南路66号</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（3）招标方式：公开招标</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（4）采购内容：本项目为盐城市第一人民医院放射设备年度环保、卫生检测等技术服务项目。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（5）资金来源：自筹</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（6）项目规模：详见清单</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、申请人应当具备的主要资格条件及要求</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（一）、投标人资质条件： </span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（1）投标人是在江苏省登记注册的具有独立法人资格的单位，营业执照须具有相关经营范围，能独立承担民事责任；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（2）具备职业卫生（放射卫生）技术服务机构甲级资质、江苏省生态环保厅颁发的社会辐射环境检测机构甲级资质；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（3）具备与投标检测设备相符合的CMA计量认证内容并提供证书及附件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（4）符合《中华人民共和国政府采购法》第22条规定；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（5）本项目中标后，不得转包；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（6）本项目不接受联合体投标。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">四、报名及招标文件的获取</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">请投标申请人于2019年10月28日至2019年10月31日前每天上午8时30分至11时30，下午2时30分至4时到盐城市毓龙163号预防保健处报名,咨询电话：0515-66696828。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">报名经办人必须为投标单位正式职工，报名时须提供报名人员身份证、法定代表人授权委托书原件、营业执照正本复印件（加盖公章）、卫生和环保两方面颁发的资质证书复印件（加盖公章）。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">五、本项目对投标申请人的资格审查采用资格后审方式，由评标委员会根据招标文件进行评定；报名时不进行报名资料的任何审查，由意向投标人自行判断是否符合投标资格。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">六、本招标项目采用的评标方法：经评审的最低价法</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">七、询价报价文件的组成及份数</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（1）投 标 函</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（2）法定代表人身份证明书</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（3）法定代表人授权委托书</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（4）开标一览表</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（5）营业执照正本复印件；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（6）公司资质（卫生及环保）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（7）业绩；</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（8）可提供的其他文件。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">八、投标时间、开标时间和地点</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（1）投标时间段：2019年10月28日8：30至16：00——2019年10月31日8：30至16：00</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（2）开标时间：2019年 11月1日（具体时间待定）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（3）投标文件提交地点：盐城市第一人民医院预防保健处（地址：盐城市人民南路66号）</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（4）成交结果公示后，成交供应商须在3个工作日内与采购单位签定合同。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">九、付款方式</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">提交现场检测结果的纸质检测报告后一月内支付全款。</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">十、联系方式</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">（1）招标人：盐城市第一人民医院</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">&nbsp; &nbsp;联系人:周公平&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电话:0515-66696828</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><br /></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><br /></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><a style=\"color: rgb(0, 102, 204); text-decoration: underline; font-size: 20px; font-family: 宋体, SimSun;\" href=\"http://localhost/upload/files/2019/10/f1c7117c031f4abe.xlsx\" title=\"附件：检测项目清单（2019年度）.xlsx\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">附件：检测项目清单（2019年度）.xlsx</span></a></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-10-24\',\n  \'timestamp\' => \'2019-10-24\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'3dde8a1111656c68\',\n  \'views\' => \'879\',\n  \'verifier\' => \'ycyyo\',\n)','1654844761');
INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6177','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院医用耗材招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院医用耗材招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'招标采购中心\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">根据《医疗机构医用耗材管理办法》的政策要求，为更好地规范和动态管理医用耗材，经医院领导研究决定，将以江苏省医用耗材阳光采购平台目录为基础，按照“公开、公平、公正”的原则，采用分批次招标采购的方式，对医用耗材进行招标采购，欢迎进入江苏省阳光采购平台的生产企业和取得生产企业授权的配送企业参加，现将有关事项公告如下：</span></p><p style=\"text-indent: 37px; line-height: 3em;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">一、</span><span style=\"font-size: 19px; font-family: 宋体;\">本次招标采购项目</span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1、</span><span style=\"font-size: 19px; font-family: 宋体;\">麻醉科专用耗材</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2、</span><span style=\"font-size: 19px; font-family: 宋体;\">血液净化类耗材</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">二、供应商资格要求</span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1、</span><span style=\"font-size: 19px; font-family: 宋体;\">具备独立法人的资格。</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有良好的商业信誉。</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">3、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有独立承担民事责任的能力。</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">4、</span><span style=\"font-size: 19px; font-family: 宋体;\">具有履行合同所必需的设备和专业技术能力。</span></span></p><p style=\"margin-left: 0px; line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">5、</span><span style=\"font-size: 19px; font-family: 宋体;\">近三年内在经营活动中无严重不良记录。</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\">三、报名</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">1</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名方式：现场报名</span></span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-size: 20px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">2</span><span style=\"font-size: 19px; font-family: 宋体;\">、报名时间</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">:2019</span><span style=\"font-size: 19px; font-family: 宋体;\">年</span><span style=\"font-family: 宋体, SimSun; font-size: 19px;\">10</span><span style=\"fon