INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6178','5','cms','item','add','0','859','','ycyyo','添加内容 | 盐城市第一人民医院南、北区监控及门禁增补等项目招标公告','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'盐城市第一人民医院南、北区监控及门禁增补等项目招标公告\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'总务处\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"text-align: left; background: white; line-height: 3em;\"><span style=\"font-size: 20px; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-indent: 32px; font-family: 宋体, SimSun;\">&nbsp; 1、盐城市第一人民医院南、北区监控及门禁增补等项目，资金已落实，招标人盐城市第一人民医院现对该项目进行公开招标，诚邀潜在的投标人参加投标。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">2、项目概况与招标内容</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">2.1项目名称：盐城市第一人民医院南、北区监控及门禁增补项目。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">2.2项目地点：盐城市第一人民医院南区（人民南路66号）、北区（毓龙西路166号）。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">2.3招标内容：南区监控室增加时间同步系统、南区中心药房增加监控存储设备、南区感染科门诊增加门禁、南区输液室卫生间增加呼叫、南区核医学科放射源仓库增加门禁；北区重点科室增加监控、北区血液净化中心增加可视门铃、北区住院部地下室空调班和电工班增加监控等项目。具体内容详见招标人提供的工程量清单。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">2.4发包方式：包工包料。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">3、投标人资格要求</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"color: black; background: white; font-size: 20px; font-family: 宋体, SimSun;\">投标人须具有有效的电子与智能化工程二级及以上专业承包资质的独立法人，并具有有效的安全生产许可证。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">4、投标报名及招标文件的获取</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"color: black; background: white; font-size: 20px; font-family: 宋体, SimSun;\">请符合上述资质条件的投标人携带单位介绍信原件到江苏仁禾中衡工程咨询房地产估价有限公司造价二部（盐城市青年西路8号8楼810室）投标报名并领取招标文件，投标报名时间2019年10月<span style=\"font-family: 宋体; color: black; background: white; font-size: 20px; text-decoration: underline;\">21</span>日8时至2019年10月<span style=\"font-family: 宋体; color: black; background: white; font-size: 20px; text-decoration: underline;\"> 25 </span>日18时。联系人：胡荣艳，0515-68037830；手机：13815581495。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">5、本项目采用竞争性谈判方式。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">6、投标申请人有违反《盐城市市场廉政准则规定（试行）》中规定情形的，招标人将拒绝投标报名。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">7、招投标监督管理机构及投诉电话：盐城市第一人民医院纪检监察室，联系电话0515-66696810。</span></p><p style=\"margin-bottom: 0px; text-indent: 32px; background: white; line-height: 3em; text-align: left;\"><span style=\"color: black; background: white; font-size: 20px; font-family: 宋体, SimSun;\">其余内容详见招标文件。</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 招标人：盐城市第一人民医院</span></p><p style=\"text-align: right; background: white; line-height: 3em;\"><span style=\"font-size: 20px; color: black; background: white; font-family: 宋体, SimSun;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：2019年10月21日</span></p><p><br /></p>\',\n  ),\n  \'assist_category\' => \'\',\n  \'url\' => \'\',\n  \'author\' => \'\',\n  \'list_order\' => \'2019-10-21\',\n  \'timestamp\' => \'2019-10-21\',\n  \'verify\' => 1,\n  \'credit\' => \'\',\n  \'html_view_url_rule\' => \'\',\n  \'seo_keywords\' => \'\',\n  \'seo_description\' => \'\',\n  \'template\' => \'\',\n  \'model\' => \'article\',\n  \'verified\' => \'\',\n  \'id\' => \'\',\n  \'iid\' => \'\',\n  \'page\' => \'\',\n  \'_referer\' => \'http://127.0.0.1/admin.php/cms/item-add\',\n  \'action\' => \'add\',\n  \'attachment_hash\' => \'1ab0e3cbf2cd2f58\',\n  \'views\' => \'574\',\n  \'verifier\' => \'ycyyo\',\n)','1654844762');
