INSERT INTO `p8_admin_log` (`id`,`uid`,`system`,`module`,`action`,`iid`,`cid`,`site`,`username`,`title`,`url`,`ip`,`data`,`timestamp`) VALUES ('6208','5','cms','item','add','0','859','','ycyyo','添加内容 | 中标结果公示','/admin.php/cms/item-add','172.16.1.2','array (\n  \'__referer\' => \'http://192.168.162.72/admin.php/cms/item-add?model=article\',\n  \'cid\' => \'859\',\n  \'title\' => \'中标结果公示\',\n  \'title_color\' => \'\',\n  \'sub_title\' => \'\',\n  \'source\' => \'盐城市第一人民医院\',\n  \'keywords\' => \'\',\n  \'frame\' => \'\',\n  \'summary\' => \'\',\n  \'addon_title\' => \'\',\n  \'#field_content_posted\' => \'\',\n  \'field#\' => \n  array (\n    \'content\' => \'<p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">因医院实际工作需要，于2019年7月11日15时进行招标的项目，经评标小组评审，中标结果如下：</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">一、南、北区零星维修项目</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城明飞装饰工程有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：12.9万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">二、南、北区化粪池清理项目</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：盐城市恒旺通清洁服务有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标价：7.87万元</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><strong><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">三、北区手术室等净化区域空气检测项目</span></strong></p><p style=\"line-height: 3em; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun; font-size: 20px;\">中标单位：安徽省德通检测技术有限公司</span></p><p style=\"line-height: 3em; text-indent: 2em;\"><span s